              `‚Ä¢ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ: ${paymentStatus}\n` +
              `‚Ä¢ ÿßŸÑŸÖÿ®ŸÑÿ∫: ${formatCurrency(totalAmount)}`);
        
        setShowRentalModal(false);
        setSelectedGames([]);
        
        setTimeout(() => {
          refreshAllData();
        }, 1000);
      } else {
        alert(`‚ùå ${response.message || 'ŸÅÿ¥ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±'}`);
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      alert('‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ' + (error.message || 'ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿÆÿßÿØŸÖ'));
    }
  };

  // ‚úÖ 19. ÿ≠ÿ≥ÿßÿ® ÿ≥ÿπÿ± ÿßŸÑÿπŸÜÿµÿ± - ŸÖÿ≠ÿ≥ŸÜ
  const calculateItemPrice = useCallback((item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return (item.pricePer15min * units * item.quantity) || 0;
  }, []);

  // ‚úÖ 20. ÿ•ŸÜŸáÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± - ŸÖÿ≠ÿ≥ŸÜ
  const handleEndRental = async (rental) => {
