  if (activeRentals && activeRentals.length > 0) {
    const confirmed = window.confirm(
      `‚ö†Ô∏è ŸäŸàÿ¨ÿØ ${activeRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÜÿ¥ÿ∑!\n` +
      `ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÖÿπ Ÿàÿ¨ŸàÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©ÿü`
