}, [currentShift, activeRentals, loadGames, user]);
  // ‚úÖ 28. ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ ÿßŸÑÿ£ŸÑÿπÿßÿ® - ŸÖÿ≠ÿ≥ŸÜ
  const handleCreateMultiGameRental = useCallback(async (rentalData) => {
    try {
      if (!rentalData.rentalItems || rentalData.rentalItems.length === 0) {
        alert('‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ÿ£Ÿä ÿ£ŸÑÿπÿßÿ®');
        return;
      }
      
      const shiftValid = await validateShiftBeforeRental();
      if (!shiftValid) {
        return;
      }
      
      const rentalsToCreate = rentalData.rentalItems.map(item => {
        const game = games.find(g => g.id === item.gameId);
        
        if (!game) {
          throw new Error(`ÿßŸÑŸÑÿπÿ®ÿ© ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©: ${item.gameId}`);
        }
        
        return {
          game_id: item.gameId,
          game_name: game.name,
          customer_name: rentalData.customerInfo.name.trim(),
          customer_phone: rentalData.customerInfo.phone || '00000000000',
          rental_type: item.rentalType || 'fixed',
          duration_minutes: item.duration || 15,
          price_per_15min: game.price_per_15min || 100,
          total_amount: item.rentalType === 'fixed' ? calculateItemPrice(item) : 0,
          paid_amount: item.rentalType === 'fixed' ? calculateItemPrice(item) : 0,
          payment_method: rentalData.paymentMethod || 'ŸÉÿßÿ¥',
          payment_status: item.rentalType === 'fixed' ? 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' : 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°',
          quantity: item.quantity || 1,
          notes: item.notes || ''
        };
      });
      
      const results = [];
      let totalRevenue = 0;
      
      for (const rentalItem of rentalsToCreate) {
        try {
          const response = await api.createSmartRental({
            game_id: rentalItem.game_id,
            customer_name: rentalItem.customer_name,
            customer_phone: rentalItem.customer_phone,
            rental_type: rentalItem.rental_type
          });
          
          if (response.success) {
            results.push({
              success: true,
              game: rentalItem.game_name,
              rental_number: response.rental_number,
              type: rentalItem.rental_type === 'fixed' ? 'ÿ´ÿßÿ®ÿ™' : 'ŸÖŸÅÿ™Ÿàÿ≠',
              amount: rentalItem.total_amount
            });
            
            if (rentalItem.rental_type === 'fixed') {
              totalRevenue += rentalItem.total_amount;
            }
          } else {
            results.push({
              success: false,
              game: rentalItem.game_name,
              error: response.message
            });
          }
        } catch (error) {
          results.push({
            success: false,
            game: rentalItem.game_name,
            error: error.message
          });
        }
        
        await new Promise(resolve => setTimeout(resolve, 200));
      }
      
      const successful = results.filter(r => r.success);
      const failed = results.filter(r => !r.success);
      
      if (totalRevenue > 0) {
        setStats(prev => ({
          ...prev,
          todayRevenue: (prev.todayRevenue || 0) + totalRevenue
        }));
      }
      
      let message = '';
      
      if (successful.length > 0) {
        message += `‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ${successful.length} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠:\n`;
        successful.forEach((item, index) => {
          message += `${index + 1}. ${item.game} (${item.type}) - ${item.rental_number}\n`;
        });
      }
      
      if (failed.length > 0) {
        message += `\n‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ${failed.length} ÿ™ÿ£ÿ¨Ÿäÿ±:\n`;
        failed.forEach((item, index) => {
          message += `${index + 1}. ${item.game}: ${item.error}\n`;
        });
      }
      
      alert(message);
      
      if (successful.length > 0) {
        setShowRentalModal(false);
        setSelectedGames([]);
        
        setTimeout(() => {
          refreshAllData();
        }, 1000);
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ™ÿπÿØÿØ:', error);
      alert('‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ™ÿπÿØÿØ: ' + (error.message || 'ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿÆÿßÿØŸÖ'));
    }
  }, [games, calculateItemPrice, validateShiftBeforeRental, refreshAllData]);

  // ‚úÖ 29. ŸÖŸÉŸàŸÜ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ
const DashboardStats = React.memo(function DashboardStats({
  stats,
