                  </button>
                ) : (
                  <button
                    onClick={onEndShift}
                    className="rentals-btn rentals-btn-warning"
                    disabled={loading.processing}
                  >
                    {loading.processing ? (
                      <Loader2 className="rentals-spinner" size={14} />
