  // ‚úÖ 30. ŸÖŸÉŸàŸÜ ÿ¥ÿ®ŸÉÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®
const GamesGrid = React.memo(function GamesGrid({ 
  games, 
  branchId, 
  onSelectGame, 
  loading,
  searchTerm,
  onSearchChange,
  filterCategory,
  onFilterChange
}) {
    const [localGames, setLocalGames] = useState([]);
    const [localSearch, setLocalSearch] = useState('');
    const [localCategory, setLocalCategory] = useState('all');

    useEffect(() => {
      if (!games?.length || !branchId) {
        setLocalGames([]);
        return;
      }

      const currentBranchId = Number(branchId);
      const branchGames = games.filter(
        game => game && Number(game.branch_id) === currentBranchId
      );
      setLocalGames(branchGames);
    }, [games, branchId]);

    const filteredGames = useMemo(() => {
      if (!localGames.length) return [];

      const search = (searchTerm || localSearch).toLowerCase();
      const category = filterCategory || localCategory;

      return localGames.filter(game => {
        const matchesSearch =
          game.name?.toLowerCase().includes(search) ||
          game.category?.toLowerCase().includes(search);

        const matchesCategory =
          category === 'all' ||
          game.category === category;

        return matchesSearch && matchesCategory;
      });
    }, [localGames, searchTerm, localSearch, filterCategory, localCategory]);

    const categories = useMemo(() => {
      if (!localGames.length) return ['all'];
      const cats = new Set(['all']);
      localGames.forEach(g => g.category && cats.add(g.category));
      return Array.from(cats);
    }, [localGames]);

    const getGameImage = useCallback((game) => {
      if (!game) return '/images/default-game.jpg';
      
      const imageMap = {
        'Driftcar': '/images/Driftcar.jpg',
        'Havoerboard': '/images/Hoverboard.jpg',
        'Sctooter': '/images/Scooter.jpg',
        'Car': '/images/Car.jpg',
        'MOtor': '/images/Motor.jpg',
        'Harlly': '/images/harley.jpg',
        'Segway': '/images/Segway.jpg',
        'Crazycar': '/images/Ninebot.jpg',
        'skeet': '/images/Skate.jpg',
        'Trampoline': '/images/Trampoline.jpg',
        'Vedio game': '/images/Simulator.jpg'
      };
      
      if (game.image_url && game.image_url !== 'default-game.jpg') {
        if (game.image_url.startsWith('http')) {
          return game.image_url;
        }
        return `/images/${game.image_url}`;
      }
      
      return imageMap[game.name] || '/images/default-game.jpg';
    }, []);

    const handleSearchChange = (e) => {
      setLocalSearch(e.target.value);
      if (onSearchChange) onSearchChange(e.target.value);
    };

    const handleCategoryChange = (e) => {
      setLocalCategory(e.target.value);
      if (onFilterChange) onFilterChange(e.target.value);
    };

    if (!branchId) {
      return (
        <div className="rentals-games-grid-section">
          <div className="rentals-empty-state">
            <Building size={48} />
            <h3>ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ</h3>
            <p>Ÿäÿ¨ÿ® ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</p>
          </div>
        </div>
      );
    }

    if (loading.games) {
      return (
        <div className="rentals-games-grid-section">
          <div className="rentals-loading-state">
            <Loader2 className="rentals-spinner" size={32} />
            <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®...</p>
          </div>
        </div>
      );
    }

    return (
      <div className="rentals-games-grid-section">
        <div className="rentals-section-header">
          <h3 className="rentals-section-title">
            <Gamepad2 size={24} />
            ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
            <span className="rentals-section-count">
              ({localGames.length})
            </span>
          </h3>
          
          <div className="rentals-search-filter">
            <div className="rentals-search-box">
              <Search size={18} />
              <input
                type="text"
                placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÑÿπÿ®ÿ©..."
                value={localSearch}
                onChange={handleSearchChange}
              />
            </div>
            
            <select 
              value={localCategory}
              onChange={handleCategoryChange}
              className="rentals-category-filter"
            >
              <option value="all">ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ¶ÿßÿ™</option>
              {categories.filter(cat => cat !== 'all').map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
        </div>

        {localGames.length === 0 ? (
          <div className="rentals-empty-state">
            <Gamepad2 size={48} />
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©</h3>
            <p>ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖÿ§ÿ¨ÿ±ÿ© ÿ£Ÿà ŸÇŸäÿØ ÿßŸÑÿµŸäÿßŸÜÿ©</p>
          </div>
        ) : filteredGames.length === 0 ? (
          <div className="rentals-empty-state">
            <Gamepad2 size={48} />
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ™ÿ∑ÿßÿ®ŸÇ &quot;{localSearch}&quot;</p>
          </div>
        ) : (
          <div className="rentals-games-grid">
            {filteredGames.map(game => (
              <div
                key={game.id}
                className="rentals-game-card"
                onClick={() => onSelectGame(game)}
              >
                <div className="rentals-game-image-container">
                  <img
                    src={getGameImage(game)}
                    alt={game.name}
                    className="rentals-game-image"
                    loading="lazy"
                    onError={(e) => {
                      e.target.src = '/images/default-game.jpg';
                    }}
                  />

                  <div className="rentals-game-status rentals-available">
                    <CheckCircle size={14} />
                    <span>ŸÖÿ™ÿßÿ≠ ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±</span>
                  </div>
                </div>

                <div className="rentals-game-info">
                  <h4>{game.name}</h4>
                  {game.category && (
                    <div className="rentals-game-category">
                      {game.category}
                    </div>
                  )}
                  <div className="rentals-game-price">
                    {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                  </div>

                  <div className="rentals-game-actions">
                    <button className="rentals-btn-select-game">
                      <Plus size={16} />
                      ÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  });

  // ‚úÖ 31. ŸÖŸÉŸàŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
const ActiveRentalsList = React.memo(function ActiveRentalsList({ 
  rentals, 
  loading, 
  onAction,
  currentShift 
}) {
    const [timeNow, setTimeNow] = useState(new Date());

    useEffect(() => {
      const interval = setInterval(() => {
        setTimeNow(new Date());
      }, 1000); // ÿ™ÿ≠ÿØŸäÿ´ ŸÉŸÑ ÿ´ÿßŸÜŸäÿ© ÿ®ÿØŸÑÿßŸã ŸÖŸÜ ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
      
      return () => clearInterval(interval);
    }, []);

    // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä (ŸÑŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠)
    const calculateElapsedTime = (startTime) => {
      if (!startTime) return '--:--';
      const start = new Date(startTime);
      const diffMs = timeNow - start;
      const diffMinutes = Math.floor(diffMs / 60000);
      const diffSeconds = Math.floor((diffMs % 60000) / 1000);
      
      if (diffMinutes < 60) {
        return `${diffMinutes.toString().padStart(2, '0')}:${diffSeconds.toString().padStart(2, '0')}`;
      } else {
        const hours = Math.floor(diffMinutes / 60);
        const minutes = diffMinutes % 60;
        return `${hours}:${minutes.toString().padStart(2, '0')}:${diffSeconds.toString().padStart(2, '0')}`;
      }
    };

    // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä (ŸÑŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™)
    const calculateRemainingTime = (rental) => {
      if (!rental.start_time) return '--:--';
      
      const rentalType = getRentalType(rental);
      const startTime = new Date(rental.start_time);
      
      if (rentalType === 'fixed') {
        const duration = rental.duration_minutes || 15;
        const endTime = new Date(startTime.getTime() + duration * 60000);
        const remainingMs = endTime - timeNow;
        
        if (remainingMs <= 0) return '00:00';
        
        const remainingMinutes = Math.floor(remainingMs / 60000);
        const remainingSeconds = Math.floor((remainingMs % 60000) / 1000);
        return `${remainingMinutes.toString().padStart(2, '0')}:${remainingSeconds.toString().padStart(2, '0')}`;
      }
      
      return 'ŸÖŸÅÿ™Ÿàÿ≠';
    };

    // ÿ≠ÿ≥ÿßÿ® ŸàŸÇÿ™ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑŸÖÿ™ŸàŸÇÿπ (ŸÑŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™)
    const calculateEndTime = (rental) => {
      if (!rental.start_time || getRentalType(rental) !== 'fixed') return '--:--';
      
      const duration = rental.duration_minutes || 15;
      const startTime = new Date(rental.start_time);
      const endTime = new Date(startTime.getTime() + duration * 60000);
      
      return formatTime(endTime);
    };

    // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÜÿ≥ÿ®ÿ© ÿßŸÑŸÖÿ¶ŸàŸäÿ© ÿßŸÑŸÖÿ™ÿ®ŸÇŸäÿ© (ŸÑŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™)
    const calculateProgress = (rental) => {
      if (!rental.start_time || getRentalType(rental) !== 'fixed') return 100;
      
      const duration = rental.duration_minutes || 15;
      const startTime = new Date(rental.start_time);
      const endTime = new Date(startTime.getTime() + duration * 60000);
      const totalMs = duration * 60000;
      const remainingMs = endTime - timeNow;
      
      if (remainingMs <= 0) return 0;
      
      const percentage = (remainingMs / totalMs) * 100;
      return Math.max(0, Math.min(100, percentage));
    };

    if (loading.rentals) {
      return (
        <div className="rentals-active-rentals-section">
          <div className="rentals-loading-state">
            <Loader2 className="rentals-spinner" size={32} />
            <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©...</p>
          </div>
        </div>
      );
    }

    if (!rentals || rentals.length === 0) {
      return (
        <div className="rentals-active-rentals-section">
          <div className="rentals-empty-state">
            <Clock size={48} />
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</h3>
            <p>ŸÑŸÖ Ÿäÿ™ŸÖ ÿ®ÿØÿ° ÿ£Ÿä ÿ™ÿ£ÿ¨Ÿäÿ± ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑÿ¥ŸäŸÅÿ™</p>
          </div>
        </div>
      );
    }

    return (
      <div className="rentals-active-rentals-section">
        <div className="rentals-section-header">
          <h3 className="rentals-section-title">
            <Clock size={24} />
            ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            <span className="rentals-section-count">
              ({rentals.length})
            </span>
          </h3>
        </div>

        <div className="rentals-rentals-table">
          <div className="rentals-table-header">
            <div className="rentals-table-cell">ÿßŸÑÿπŸÖŸäŸÑ</div>
            <div className="rentals-table-cell">ÿßŸÑŸÑÿπÿ®ÿ©</div>
            <div className="rentals-table-cell">ÿßŸÑŸÜŸàÿπ</div>
            <div className="rentals-table-cell">ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°</div>
            <div className="rentals-table-cell">ŸàŸÇÿ™ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°</div>
            <div className="rentals-table-cell">ÿßŸÑŸàŸÇÿ™</div>
            <div className="rentals-table-cell">ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</div>
          </div>

          {rentals.map(rental => {
            const rentalType = getRentalType(rental);
            const isFixed = rentalType === 'fixed';
            
            return (
              <div key={rental.id} className="rentals-table-row">
                <div className="rentals-table-cell">
                  <div className="rentals-customer-info">
                    <User size={16} />
                    <span>{rental.customer_name}</span>
                  </div>
                  {rental.customer_phone && rental.customer_phone !== '00000000000' && (
                    <div className="rentals-customer-phone">
                      <Phone size={14} />
                      <span>{rental.customer_phone}</span>
                    </div>
                  )}
                </div>
                
                <div className="rentals-table-cell">
                  <div className="rentals-game-info-small">
                    <Gamepad2 size={16} />
                    <span>{rental.game_name}</span>
                  </div>
                  <div className="rentals-game-price-small">
                    {formatCurrency(rental.price_per_15min || 100)} / 15 ÿØŸÇŸäŸÇÿ©
                  </div>
                </div>
                
                <div className="rentals-table-cell">
                  <span className={`rentals-type-badge rentals-type-${rentalType}`}>
                    {isFixed ? '‚è∞ ÿ´ÿßÿ®ÿ™' : 'üîì ŸÖŸÅÿ™Ÿàÿ≠'}
                  </span>
                </div>
                
                <div className="rentals-table-cell">
                  <div className="rentals-time-info">
                    <Clock size={14} />
                    <span>{formatTime(rental.start_time)}</span>
                  </div>
                </div>
                
                <div className="rentals-table-cell">
                  <div className="rentals-time-info">
                    <Clock size={14} />
                    <span>{isFixed ? calculateEndTime(rental) : '--:--'}</span>
                  </div>
                </div>
                
                <div className="rentals-table-cell">
                  <div className="rentals-timer-container">
                    {isFixed ? (
                      <>
                        <div className="rentals-timer-countdown">
                          <div className="rentals-timer-icon">
                            <Clock size={14} />
                          </div>
                          <span className="rentals-timer-text">
                            {calculateRemainingTime(rental)}
                          </span>
                        </div>
                        <div className="rentals-progress-bar">
                          <div 
                            className="rentals-progress-fill"
                            style={{ width: `${calculateProgress(rental)}%` }}
                          ></div>
                        </div>
                      </>
                    ) : (
                      <div className="rentals-timer-countup">
                        <div className="rentals-timer-icon">
                          <Clock size={14} />
                        </div>
                        <span className="rentals-timer-text">
                          {calculateElapsedTime(rental.start_time)}
                        </span>
                      </div>
                    )}
                  </div>
                </div>
                
                <div className="rentals-table-cell">
                  <div className="rentals-actions">
                    <button 
                      onClick={() => onAction('details', rental)}
                      className="rentals-btn-action rentals-btn-info"
                      title="ÿ™ŸÅÿßÿµŸäŸÑ"
                    >
                      <Eye size={16} />
                    </button>
                    
                    {!isFixed && (
                      <button 
                        onClick={() => onAction('end', rental)}
                        className="rentals-btn-action rentals-btn-success"
                        title="ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                      >
                        <Check size={16} />
                      </button>
                    )}
                    
                    {isFixed && (
                      <button 
                        onClick={() => onAction('early_end', rental)}
                        className="rentals-btn-action rentals-btn-warning"
                        title="ÿ•ŸÑÿ∫ÿßÿ° ŸÖÿ®ŸÉÿ±"
                      >
                        <Minus size={16} />
                      </button>
                    )}
                    
                    <button 
                      onClick={() => onAction('change_game', rental)}
                      className="rentals-btn-action rentals-btn-secondary"
                      title="ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©"
                    >
                      <RefreshCw size={16} />
                    </button>
                    
                    <button 
                      onClick={() => onAction('cancel', rental)}
                      className="rentals-btn-action rentals-btn-danger"
                      title="ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                    >
                      <X size={16} />
                    </button>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    );
  });

  // ‚úÖ 32. ŸÖŸÉŸàŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
const CompletedRentalsList = React.memo(function CompletedRentalsList({ 
  rentals, 
  loading, 
  onRerental,
  isManager 
}) {
    if (!isManager) return null;

    if (loading.stats) {
      return (
        <div className="rentals-completed-rentals-section">
          <div className="rentals-loading-state">
            <Loader2 className="rentals-spinner" size={32} />
            <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©...</p>
          </div>
        </div>
      );
    }

    if (!rentals || rentals.length === 0) {
      return (
        <div className="rentals-completed-rentals-section">
          <div className="rentals-empty-state">
            <CheckCircle size={48} />
            <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÖŸÉÿ™ŸÖŸÑÿ©</h3>
            <p>ŸÑŸÖ Ÿäÿ™ŸÖ ÿ•ŸÉŸÖÿßŸÑ ÿ£Ÿä ÿ™ÿ£ÿ¨Ÿäÿ± ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑÿ¥ŸäŸÅÿ™</p>
