        game.status === 'ŸÖÿ™ÿßÿ≠' &&
        (game.id !== rental?.game_id) &&
        (!searchTerm || 
         game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
         game.category?.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }, [games, rental, searchTerm]);

    const handleConfirm = async () => {
      if (!selectedGame) {
        alert('Ÿäÿ±ÿ¨Ÿâ ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ©');
        return;
      }

      setIsSubmitting(true);
      try {
        await onConfirm(selectedGame);
      } catch (error) {
        console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©:', error);
      } finally {
        setIsSubmitting(false);
      }
    };

    if (!show || !rental) return null;

    return (
      <div className="rentals-modal-overlay">
        <div className="rentals-modal rentals-change-game-modal">
          <div className="rentals-modal-header">
            <h2>
              <RefreshCw size={24} />
              ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©
            </h2>
            <button onClick={onClose} className="rentals-modal-close" disabled={isSubmitting}>
              <X size={24} />
            </button>
          </div>
          
          <div className="rentals-modal-body">
            <div className="rentals-current-game-info">
              <h4>ÿßŸÑŸÑÿπÿ®ÿ© ÿßŸÑÿ≠ÿßŸÑŸäÿ©:</h4>
              <div className="rentals-game-card-small">
                <Gamepad2 size={20} />
                <span>{rental.game_name}</span>
              </div>
            </div>
            
            <div className="rentals-search-box">
              <Search size={18} />
              <input
                type="text"
                placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÑÿπÿ®ÿ©..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                disabled={isSubmitting}
              />
            </div>
            
            <div className="rentals-games-list">
              {filteredGames.length === 0 ? (
                <div className="rentals-empty-state">
                  <Package size={48} />
                  <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ©</p>
                </div>
              ) : (
                filteredGames.map(game => (
                  <div 
                    key={game.id}
                    className={`rentals-game-select-item ${selectedGame?.id === game.id ? 'rentals-selected' : ''}`}
                    onClick={() => setSelectedGame(game)}
                  >
                    <div className="rentals-game-select-image">
                      <img 
                        src={`/images/${game.image_url || 'default-game.jpg'}`} 
                        alt={game.name}
                        onError={(e) => e.target.src = '/images/default-game.jpg'}
                      />
                    </div>
                    <div className="rentals-game-select-info">
                      <h4>{game.name}</h4>
                      <div className="rentals-game-price">
                        {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                      </div>
                    </div>
                    {selectedGame?.id === game.id && (
                      <div className="rentals-game-select-check">
                        <CheckCircle size={20} />
                      </div>
                    )}
                  </div>
                ))
              )}
            </div>
          </div>
          
          <div className="rentals-modal-footer">
            <button 
              onClick={onClose}
              className="rentals-btn rentals-btn-secondary"
              disabled={isSubmitting}
            >
              ÿ•ŸÑÿ∫ÿßÿ°
            </button>
            <button 
              onClick={handleConfirm}
              className="rentals-btn rentals-btn-primary"
              disabled={isSubmitting || !selectedGame}
            >
              {isSubmitting ? (
                <>
                  <Loader2 className="rentals-spinner" size={16} />
                  ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ∫ŸäŸäÿ±...
                </>
              ) : (
                <>
                  <RefreshCw size={16} />
                  ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    );
  });

  // ‚úÖ 35. ŸÖŸÉŸàŸÜ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
const RerentalModal = React.memo(function RerentalModal({ 
  show, 
  onClose, 
  rental, 
  games, 
  onConfirm 
}) {
    const [selectedGame, setSelectedGame] = useState(null);
    const [rentalType, setRentalType] = useState('fixed');
    const [duration, setDuration] = useState(15);
    const [isSubmitting, setIsSubmitting] = useState(false);

    useEffect(() => {
      if (show && games.length > 0) {
        const previousGame = games.find(g => g.name === rental?.game_name);
        setSelectedGame(previousGame || games[0]);
      }
    }, [show, rental, games]);

    const handleConfirm = async () => {
      if (!selectedGame || !rental) return;

      setIsSubmitting(true);
      try {
        await onConfirm(selectedGame, rentalType, duration);
      } catch (error) {
        console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
      } finally {
        setIsSubmitting(false);
      }
    };

    if (!show || !rental) return null;

    return (
      <div className="rentals-modal-overlay">
        <div className="rentals-modal rentals-rerental-modal">
          <div className="rentals-modal-header">
            <h2>
              <RotateCcw size={24} />
              ÿ•ÿπÿßÿØÿ© ÿ™ÿ£ÿ¨Ÿäÿ±
            </h2>
            <button onClick={onClose} className="rentals-modal-close" disabled={isSubmitting}>
              <X size={24} />
            </button>
          </div>
          
          <div className="rentals-modal-body">
            <div className="rentals-customer-info-summary">
              <h4>ÿßŸÑÿπŸÖŸäŸÑ ÿßŸÑÿ≥ÿßÿ®ŸÇ:</h4>
              <div className="rentals-customer-details">
                <div className="rentals-customer-name">
                  <User size={16} />
                  <span>{rental.customer_name}</span>
                </div>
                {rental.customer_phone && (
                  <div className="rentals-customer-phone">
                    <Phone size={16} />
                    <span>{rental.customer_phone}</span>
                  </div>
                )}
              </div>
            </div>
            
            <div className="rentals-form-section">
              <div className="rentals-form-group">
                <label>ÿßÿÆÿ™ÿ± ŸÑÿπÿ®ÿ©:</label>
                <select 
                  value={selectedGame?.id || ''}
                  onChange={(e) => {
                    const game = games.find(g => g.id === parseInt(e.target.value));
                    setSelectedGame(game);
                  }}
                  disabled={isSubmitting}
                >
                  {games.filter(g => g.status === 'ŸÖÿ™ÿßÿ≠').map(game => (
                    <option key={game.id} value={game.id}>
                      {game.name} - {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                    </option>
                  ))}
                </select>
              </div>
              
              <div className="rentals-form-group">
                <label>ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™:</label>
                <select 
                  value={rentalType}
                  onChange={(e) => setRentalType(e.target.value)}
                  disabled={isSubmitting}
                >
                  <option value="fixed">‚è∞ ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™</option>
                  <option value="open">üîì ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠</option>
                </select>
              </div>
              
              {rentalType === 'fixed' && (
                <div className="rentals-form-group">
                  <label>ÿßŸÑŸÖÿØÿ© (ÿØŸÇÿßÿ¶ŸÇ):</label>
                  <select 
                    value={duration}
                    onChange={(e) => setDuration(parseInt(e.target.value))}
                    disabled={isSubmitting}
                  >
                    <option value="15">15 ÿØŸÇŸäŸÇÿ©</option>
                    <option value="30">30 ÿØŸÇŸäŸÇÿ©</option>
                    <option value="45">45 ÿØŸÇŸäŸÇÿ©</option>
                    <option value="60">60 ÿØŸÇŸäŸÇÿ©</option>
                    <option value="90">90 ÿØŸÇŸäŸÇÿ©</option>
                    <option value="120">120 ÿØŸÇŸäŸÇÿ©</option>
                  </select>
                </div>
              )}
            </div>
          </div>
          
          <div className="rentals-modal-footer">
            <button 
              onClick={onClose}
              className="rentals-btn rentals-btn-secondary"
              disabled={isSubmitting}
            >
              ÿ•ŸÑÿ∫ÿßÿ°
            </button>
            <button 
              onClick={handleConfirm}
              className="rentals-btn rentals-btn-primary"
              disabled={isSubmitting || !selectedGame}
            >
              {isSubmitting ? (
                <>
                  <Loader2 className="rentals-spinner" size={16} />
                  ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ•ŸÜÿ¥ÿßÿ°...
                </>
              ) : (
                <>
                  <RotateCcw size={16} />
                  ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    );
  });

  // ‚úÖ 36. ÿπÿ±ÿ∂ ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
  return (
    <div className="rentals-container">
      <div className="rentals-header">
        <h1>
          <Gamepad2 size={32} />
          ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
        </h1>
        <div className="rentals-header-actions">
          <button 
            onClick={refreshAllData}
            className="rentals-btn rentals-btn-outline"
            disabled={loading.processing}
          >
            {loading.processing ? (
              <Loader2 className="rentals-spinner" size={16} />
            ) : (
              <RefreshCw size={16} />
            )}
            ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </button>
          
          {currentShift && (
            <div className="rentals-shift-info">
              <Clock size={16} />
              <span>ÿßŸÑÿ¥ŸäŸÅÿ™ #{currentShift.id} - {currentShift.employee_name}</span>
            </div>
          )}
        </div>
      </div>

      <DashboardStats
        stats={stats}
        currentShift={currentShift}
        onStartShift={handleStartShift}
        onEndShift={handleEndShift}
        loading={loading}
      />

      <GamesGrid
        games={games}
        branchId={user?.branch_id}
        onSelectGame={handleSelectGame}
        loading={loading}
        searchTerm={searchTerm}
        onSearchChange={setSearchTerm}
        filterCategory={filterCategory}
        onFilterChange={setFilterCategory}
      />

      <ActiveRentalsList
        rentals={activeRentals}
        loading={loading}
        onAction={handleRentalAction}
        currentShift={currentShift}
      />

      {isManager && (
        <CompletedRentalsList
          rentals={completedRentals}
          loading={loading}
          onRerental={handleRerental}
          isManager={isManager}
        />
      )}

      <RentalModal
        show={showRentalModal}
        onClose={() => setShowRentalModal(false)}
        selectedGames={selectedGames}
        onConfirmRental={handleCreateRental}
        currentShift={currentShift}
        allGames={allGames}
      />

      <ChangeGameModal
        show={showChangeGameModal}
        onClose={() => setShowChangeGameModal(false)}
        rental={selectedRentalForChange}
        games={games}
        onConfirm={confirmChangeGame}
      />

      <RerentalModal
        show={showRerentalModal}
        onClose={() => setShowRerentalModal(false)}
        rental={selectedCompletedRental}
        games={games}
        onConfirm={confirmRerental}
      />
    </div>
  );
};

export default Rentals;
