import axios from 'axios';

// ==================== ÿ•ÿπÿØÿßÿØÿßÿ™ ÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================
// baseURL Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÜÿ™ŸáŸä ÿ®ŸÄ /api
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL, // http://localhost:5000/api
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
});

// ==================== Interceptors ====================
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // DEBUG: ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
    console.log('üåê API Request:', {
      method: config.method?.toUpperCase(),
      url: config.url,
      baseURL: config.baseURL,
      fullUrl: `${config.baseURL}${config.url}`
    });
    
    return config;
  },
  (error) => {
    console.error('‚ùå Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    // DEBUG: ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿßÿ™ ÿßŸÑŸÜÿßÿ¨ÿ≠ÿ©
    console.log('‚úÖ API Response:', {
      url: response.config.url,
      status: response.status,
      success: response.data?.success
    });
    
    const responseData = response.data;
    
    if (responseData && typeof responseData === 'object' && 'success' in responseData) {
      return responseData;
    }
    
    return {
      success: true,
      status: response.status,
      data: responseData,
      message: 'ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿπŸÖŸÑŸäÿ© ÿ®ŸÜÿ¨ÿßÿ≠'
    };
  },
  (error) => {
    // DEBUG: ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ£ÿÆÿ∑ÿßÿ°
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä API:', {
      url: error.config?.url,
      fullUrl: `${error.config?.baseURL}${error.config?.url}`,
      method: error.config?.method?.toUpperCase(),
      status: error.response?.status,
      statusText: error.response?.statusText,
      message: error.message,
      data: error.response?.data
    });
    
    let apiError = {
      success: false,
      status: error.response?.status || 0,
      message: '',
      data: error.response?.data,
      error: error
    };
    
    if (error.code === 'ERR_NETWORK') {
      apiError.message = 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ http://localhost:5000';
    } else if (error.response?.status === 401) {
      apiError.message = 'ÿßŸÜÿ™Ÿáÿ™ ÿ¨ŸÑÿ≥ÿ© ÿßŸÑÿπŸÖŸÑ. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ';
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      localStorage.removeItem('lastLogin');
      if (window.location.pathname !== '/login') {
        window.location.href = '/login';
      }
    } else if (error.response?.status === 404) {
      const fullUrl = `${error.config?.baseURL}${error.config?.url}`;
      apiError.message = `ÿßŸÑŸÖÿ≥ÿßÿ± ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ: ${fullUrl}`;
    } else if (error.response?.status === 500) {
      apiError.message = 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ';
    } else {
      apiError.message = error.response?.data?.message || error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
    
    return Promise.reject(apiError);
  }
);

// ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ====================
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    localStorage.setItem('token', token);
  } else {
    delete api.defaults.headers.common['Authorization'];
    localStorage.removeItem('token');
  }
};

api.checkAuthStatus = () => {
  const token = localStorage.getItem('token');
  const user = localStorage.getItem('user');
  
  return {
    isAuthenticated: !!token,
    token: token,
    user: user ? JSON.parse(user) : null
  };
};

// ==================== ÿØŸàÿßŸÑ API ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================

// 1. ÿßŸÑŸÖÿµÿßÿØŸÇÿ©
api.login = async (email, password) => {
  try {
    // ÿßÿ≥ÿ™ÿÆÿØŸÖ axios ŸÖÿ®ÿßÿ¥ÿ±ÿ©Ÿã
    const response = await axios.post(`${api.defaults.baseURL}/auth/login`, {
      email, 
      password
    }, {
      headers: {
        'Content-Type': 'application/json'
      },
      timeout: 15000
    });
    
    const data = response.data;
    
    if (data.success && data.token) {
      api.setToken(data.token);
      
      if (data.user) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('lastLogin', new Date().toISOString());
      }
      
      return {
        success: true,
        data: data
      };
    } else {
      return {
        success: false,
        message: data.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
      };
    }
  } catch (error) {
    if (error.code === 'ECONNABORTED') {
      return {
        success: false,
        message: 'ÿßŸÜÿ™Ÿáÿ™ ÿßŸÑŸÖŸáŸÑÿ©. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ'
      };
    }
    
    if (error.response?.status === 401) {
      return {
        success: false,
        message: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©'
      };
    }
    
    return {
      success: false,
      message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ'
    };
  }
};

api.logout = () => {
  api.setToken(null);
  localStorage.removeItem('user');
  localStorage.removeItem('lastLogin');
  window.location.href = '/login';
};

// 2. ÿßŸÑŸÖŸÑŸÅ ÿßŸÑÿ¥ÿÆÿµŸä
api.getProfile = async () => {
  try {
    const response = await api.get('/auth/profile');
    return response;
  } catch (error) {
    return {
      success: false,
