            <p>ŸäŸÖŸÉŸÜŸÉ ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ ŸÖŸÜ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</p>
          </div>
        ) : (
          <div className="rentals-grid">
            {activeRentals.slice(0, 6).map((rental, index) => {
              const isOpenTime = rental.rental_type === 'open' || rental.is_open_time === 1;
              const timeLeft = calculateTimeLeft(rental);
              
              return (
                <div key={rental.id || index} className="rental-card">
                  <div className="rental-header">
                    <div className="rental-type-badge">
                      {isOpenTime ? (
                        <span className="open-time">‚è≥ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠</span>
                      ) : (
                        <span className="fixed-time">‚è±Ô∏è ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™</span>
                      )}
                    </div>
                    <div className="rental-timer">
                      <Clock size={14} />
                      <span>{timeLeft}</span>
                    </div>
                  </div>
                  
                  <div className="rental-body">
                    <h4 className="customer-name">
                      <User size={16} />
                      {rental.customer_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}
                    </h4>
                    
                    <div className="rental-details">
                      <div className="detail">
                        <span className="label">ÿßŸÑŸÑÿπÿ®ÿ©:</span>
                        <span className="value">{rental.game_name || 'ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ'}</span>
                      </div>
                      
                      <div className="detail">
                        <span className="label">ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°:</span>
                        <span className="value">{formatTime(rental.start_time)}</span>
                      </div>
                      
                      {!isOpenTime && rental.duration_minutes && (
                        <div className="detail">
                          <span className="label">ÿßŸÑŸÖÿØÿ©:</span>
                          <span className="value">{rental.duration_minutes} ÿØŸÇŸäŸÇÿ©</span>
                        </div>
                      )}
                      
                      <div className="detail">
                        <span className="label">ÿßŸÑŸÖÿ®ŸÑÿ∫:</span>
                        <span className="value amount">
                          {isOpenTime ? 'Ÿäÿ≠ÿØÿØ ÿπŸÜÿØ ÿßŸÑÿ•ŸÜÿ™Ÿáÿßÿ°' : formatCurrency(rental.paid_amount || rental.total_amount || 0)}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
        
        {activeRentals.length > 6 && (
          <div className="view-all-container">
            <Link to="/rentals" className="view-all-link">
              ÿπÿ±ÿ∂ ÿ¨ŸÖŸäÿπ {activeRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÜÿ¥ÿ∑ ‚Üí
            </Link>
          </div>
        )}
      </div>
      
      {/* ŸÇÿ≥ŸÖ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ™ŸÅÿµŸäŸÑŸäÿ© */}
      <div className="detailed-stats-section">
        <div className="section-header">
          <h2>
            <TrendingUp size={24} />
            ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ™ŸÅÿµŸäŸÑŸäÿ©
          </h2>
          <span className="update-time">
            ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´: {new Date().toLocaleTimeString('ar-EG')}
          </span>
        </div>
        
        <div className="detailed-stats-grid">
          <div className="detailed-stat-card">
            <h3>ÿ™Ÿàÿ≤Ÿäÿπ ÿ£ŸÜŸàÿßÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</h3>
            <div className="distribution-chart">
              <div className="distribution-item">
                <div className="distribution-label">
                  <span className="dot fixed-dot"></span>
                  <span>ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™</span>
                </div>
                <div className="distribution-value">
                  {dashboardData.rentalStats.fixedTime}
                </div>
              </div>
              <div className="distribution-item">
                <div className="distribution-label">
                  <span className="dot open-dot"></span>
                  <span>ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠</span>
                </div>
                <div className="distribution-value">
                  {dashboardData.rentalStats.openTime}
                </div>
              </div>
            </div>
          </div>
          
          <div className="detailed-stat-card">
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿ≥ÿ® ÿßŸÑŸÜŸàÿπ</h3>
            <div className="games-by-category">
              {(() => {
                const categories = {};
                availableGames.forEach(game => {
                  const category = game.category || 'ÿ∫Ÿäÿ± ŸÖÿµŸÜŸÅ';
                  categories[category] = (categories[category] || 0) + 1;
                });
                
                return Object.entries(categories).map(([category, count]) => (
                  <div key={category} className="category-item">
                    <span className="category-name">{category}</span>
                    <span className="category-count">{count}</span>
                  </div>
                ));
              })()}
            </div>
          </div>
          
          <div className="detailed-stat-card">
            <h3>ŸÖŸÑÿÆÿµ ÿßŸÑÿ£ÿØÿßÿ° ÿßŸÑŸäŸàŸÖŸä</h3>
            <div className="performance-summary">
              <div className="performance-item">
                <span className="performance-label">ŸÖÿ™Ÿàÿ≥ÿ∑ ŸÇŸäŸÖÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:</span>
                <span className="performance-value">
                  {dashboardData.todayRentals > 0 
                    ? formatCurrency(dashboardData.todayRevenue / dashboardData.todayRentals)
                    : formatCurrency(0)}
                </span>
              </div>
              <div className="performance-item">
                <span className="performance-label">ŸÖÿπÿØŸÑ ÿßŸÑÿ•ÿ¥ÿ∫ÿßŸÑ:</span>
                <span className="performance-value">
                  {dashboardData.availableGames + dashboardData.activeRentals > 0
                    ? `${Math.round((dashboardData.activeRentals / (dashboardData.availableGames + dashboardData.activeRentals)) * 100)}%`
                    : '0%'}
                </span>
              </div>
              <div className="performance-item">
                <span className="performance-label">ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑŸÖÿ™ŸàŸÇÿπ:</span>
                <span className="performance-value">
                  {formatCurrency(dashboardData.todayRevenue + dashboardData.rentalStats.totalAmount)}
                </span>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {/* ÿ™ÿπŸÑŸäŸÖÿßÿ™ Ÿàÿ£ÿ≤ÿ±ÿßÿ± ÿ≥ÿ±Ÿäÿπÿ© */}
      <div className="quick-actions-section">
        <h2>‚ö° ÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ©</h2>
        <div className="quick-actions-grid">
          <Link to="/rentals" className="quick-action-card">
            <div className="action-icon">
              <Zap size={24} />
            </div>
            <div className="action-content">
              <h3>ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ</h3>
              <p>ÿßŸÜŸÇÿ± ŸÑŸÑÿßŸÜÿ™ŸÇÿßŸÑ ÿ•ŸÑŸâ ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</p>
            </div>
          </Link>
          
          <Link to="/rentals?tab=active" className="quick-action-card">
            <div className="action-icon">
              <Clock size={24} />
            </div>
            <div className="action-content">
              <h3>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
              <p>ÿπÿ±ÿ∂ ŸàÿßŸÑÿ™ÿ≠ŸÉŸÖ ÿ®ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑÿ¨ÿßÿ±Ÿäÿ©</p>
            </div>
          </Link>
          
          <Link to="/rentals?tab=completed" className="quick-action-card">
            <div className="action-icon">
              <CheckCircle size={24} />
            </div>
            <div className="action-content">
              <h3>ÿπÿ±ÿ∂ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©</h3>
              <p>ŸÖÿ±ÿßÿ¨ÿπÿ© ÿßŸÑŸÅŸàÿßÿ™Ÿäÿ± ŸàÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±</p>
            </div>
          </Link>
          
          <div className="quick-action-card" onClick={handleManualRefresh}>
            <div className="action-icon">
              <RefreshCw size={24} />
            </div>
            <div className="action-content">
              <h3>ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¢ŸÜ</h3>
              <p>ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ£ÿ≠ÿØÿ´ ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
