import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';

const GamesManagement = () => {
  const { user } = useAuth();
  const [allGames, setAllGames] = useState([]); // Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù…Ù† Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ±ÙˆØ¹
  const [currentGames, setCurrentGames] = useState([]); // Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…Ø¹Ø±ÙˆØ¶Ø© Ø­Ø§Ù„ÙŠØ§Ù‹
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingBranch, setLoadingBranch] = useState(false);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});

useEffect(() => {
  loadGamesData(); 
}, []);

// ÙÙŠ loadGamesData ÙÙŠ GamesManagement.jsx
const loadGamesData = async () => {
  try {
    setLoading(true);
    setError('');

    // âœ… **Ø§Ù„Ø­Ù„: Ø§Ø³ØªØ®Ø¯Ø§Ù… getGames Ø§Ù„Ø¹Ø§Ø¯ÙŠ Ø¨Ø¯ÙˆÙ† all_branches**
    const gamesResponse = await authService.getGames();
    
    console.log('ðŸŽ® Ø§Ø³ØªØ¬Ø§Ø¨Ø© getGames:', {
      success: gamesResponse.success,
      dataLength: gamesResponse.data?.length,
      message: gamesResponse.message
    });
    
    if (gamesResponse.success) {
      const gamesData = gamesResponse.data || [];
      console.log('âœ… Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…Ø­Ù…Ù„Ø©:', gamesData.length);
      
      // Ø¹Ø±Ø¶ Ø¨Ø¹Ø¶ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ØªØ´Ø®ÙŠØµ
      if (gamesData.length > 0) {
        console.log('ðŸ“‹ Ø¹ÙŠÙ†Ø© Ù…Ù† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:');
        gamesData.slice(0, 3).forEach((game, index) => {
          console.log(`  ${index + 1}. ${game.name} - ÙØ±Ø¹: ${game.branch_id} - Ø­Ø§Ù„Ø©: ${game.status}`);
        });
      }
      
      setAllGames(gamesData);
      setCurrentGames(gamesData);
      
      // âœ… **Ø§Ù„Ø­Ù„: ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹ Ø¨Ø´ÙƒÙ„ Ù…Ù†ÙØµÙ„**
      const branchesResponse = await authService.getBranches();
      if (branchesResponse.success) {
        const branchesData = branchesResponse.data || [];
        console.log('ðŸ¬ Ø¹Ø¯Ø¯ Ø§Ù„ÙØ±ÙˆØ¹ Ø§Ù„Ù…Ø­Ù…Ù„Ø©:', branchesData.length);
        setBranches(branchesData);
        
        // âœ… **Ø§Ù„Ø­Ù„: Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ù…Ø­Ù„ÙŠØ§Ù‹**
        const stats = {};
        
        // ØªÙ‡ÙŠØ¦Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ù„ÙƒÙ„ ÙØ±Ø¹
        branchesData.forEach(branch => {
          stats[branch.id] = {
            branch_id: branch.id,
            branch_name: branch.name,
            total_games: 0,
            available_games: 0,
            rented_games: 0,
            maintenance_games: 0,
            gameCounts: {}
          };
        });
        
        // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„ÙƒÙ„ ÙØ±Ø¹
        gamesData.forEach(game => {
          const branchId = game.branch_id;
          if (stats[branchId]) {
            stats[branchId].total_games++;
            
            // Ø²ÙŠØ§Ø¯Ø© Ø¹Ø¯Ø¯ ÙƒÙ„ Ù†ÙˆØ¹ Ù…Ù† Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
            const gameName = game.name;
            stats[branchId].gameCounts[gameName] = (stats[branchId].gameCounts[gameName] || 0) + 1;
            
            // Ø­Ø³Ø¨ Ø§Ù„Ø­Ø§Ù„Ø©
            switch (game.status) {
              case 'Ù…ØªØ§Ø­':
                stats[branchId].available_games++;
                break;
              case 'Ù…Ø¤Ø¬Ø±Ø©':
                stats[branchId].rented_games++;
                break;
              case 'ØµÙŠØ§Ù†Ø©':
                stats[branchId].maintenance_games++;
                break;
            }
          } else {
            // Ø¥Ø°Ø§ ÙƒØ§Ù† Ø§Ù„ÙØ±Ø¹ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯ ÙÙŠ Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„ÙØ±ÙˆØ¹
            console.warn(`âš ï¸  Ù„Ø¹Ø¨Ø© Ù…Ø±ØªØ¨Ø·Ø© Ø¨ÙØ±Ø¹ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯: ${game.name} - ÙØ±Ø¹ ID: ${branchId}`);
          }
        });
        
        console.log('ðŸ“Š Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ù…Ø­Ø³ÙˆØ¨Ø©:', stats);
        setBranchStats(stats);
      } else {
        console.error('âŒ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹:', branchesResponse.message);
        setError('ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹: ' + (branchesResponse.message || ''));
