import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';


const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [monthlyRevenue, setMonthlyRevenue] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ù‚Ø¨Ù„ Ù‚Ù„ÙŠÙ„');

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª ÙƒÙ„ Ø«Ø§Ù†ÙŠØ©
    const timeInterval = setInterval(() => {
      setCurrentTime(new Date());
      updateTimeAgo();
    }, 1000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†Ù‚Ø¶ÙŠ
    const timeAgoInterval = setInterval(updateTimeAgo, 60000);
    
    // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
    const dataInterval = setInterval(loadDashboardData, 60000);
    
    return () => {
      clearInterval(timeInterval);
      clearInterval(timeAgoInterval);
      clearInterval(dataInterval);
    };
  }, []);

  const updateTimeAgo = () => {
    const now = new Date();
    const minutes = Math.floor((now - currentTime) / 60000);
    
    if (minutes === 0) {
      setTimeAgo('Ø§Ù„Ø¢Ù†');
    } else if (minutes === 1) {
      setTimeAgo('Ù‚Ø¨Ù„ Ø¯Ù‚ÙŠÙ‚Ø©');
    } else if (minutes < 60) {
      setTimeAgo(`Ù‚Ø¨Ù„ ${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`);
    } else {
      const hours = Math.floor(minutes / 60);
      setTimeAgo(`Ù‚Ø¨Ù„ ${hours} Ø³Ø§Ø¹Ø©`);
    }
  };

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // Ø¬Ù„Ø¨ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙÙŠ ÙˆÙ‚Øª ÙˆØ§Ø­Ø¯
      const [
        statsResponse,
        branchesResponse,
        gamesResponse,
        rentalsResponse,
        usersResponse
      ] = await Promise.allSettled([
        analyticsService.getDashboardStats(),
        api.getBranches(),
        api.getGames(),
        api.getRentals({ status: 'Ù†Ø´Ø·', limit: 5 }),
        api.getUsers()
      ]);
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        setBranches(branchesResponse.value.data || []);
      } else {
        setBranches([
          { id: 1, name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', location: 'Ø§Ù„Ù‚Ø§Ù‡Ø±Ø©', total_games: 10 },
          { id: 2, name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', location: 'Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', total_games: 8 },
          { id: 3, name: 'ÙØ±Ø¹ Ø§Ù„ØªØ¬Ù…Ø¹', location: 'Ø§Ù„ØªØ¬Ù…Ø¹ Ø§Ù„Ø®Ø§Ù…Ø³', total_games: 12 }
        ]);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
      if (statsResponse.status === 'fulfilled' && statsResponse.value.success) {
        const statsData = statsResponse.value.data?.summary || {};
        setStats(prev => ({
          ...prev,
          totalRevenue: statsData.total_revenue || 15000,
          totalRentals: statsData.total_rentals || 45,
          activeRentals: statsData.active_rentals || 3,
          availableGames: statsData.available_games || 15,
          todayRevenue: statsData.today_revenue || 1200,
          todayRentals: statsData.today_rentals || 2,
          totalUsers: statsData.total_users || 8,
          totalBranches: statsData.total_branches || branches.length,
          totalGames: statsData.total_games || 20,
          totalCustomers: statsData.total_customers || 25
        }));
      } else {
        setStats(prev => ({
          ...prev,
          totalRevenue: 15000,
          totalRentals: 45,
          activeRentals: 3,
          availableGames: 15,
          todayRevenue: 1200,
          todayRentals: 2,
          totalUsers: 8,
          totalBranches: branches.length || 3,
          totalGames: 20,
          totalCustomers: 25
        }));
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const gamesData = gamesResponse.value.data || [];
        setGames(gamesData);
        setStats(prev => ({
          ...prev,
          totalGames: gamesData.length,
          availableGames: gamesData.filter(g => g.status === 'Ù…ØªØ§Ø­').length
        }));
        calculateTopGames(gamesData);
      } else {
        const mockGames = [
          { id: 1, name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 150, status: 'Ù…ØªØ§Ø­', rental_count: 15, total_revenue: 4500, image_url: 'drift-car.jpg' },
          { id: 2, name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'Ø¯Ø±Ø§Ø¬Ø§Øª Ù†Ø§Ø±ÙŠØ©', price_per_hour: 120, status: 'Ù…ØªØ§Ø­', rental_count: 12, total_revenue: 3600, image_url: 'electric-bike.jpg' },
          { id: 3, name: 'Ø¹Ø±Ø¨Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', category: 'Ø³ÙŠØ§Ø±Ø§Øª', price_per_hour: 200, status: 'Ù…Ø¤Ø¬Ø±', rental_count: 8, total_revenue: 3200, image_url: 'electric-car.jpg' },
          { id: 4, name: 'Ù‡Ø§ÙØ± Ø¨ÙˆØ±Ø¯', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 80, status: 'Ù…ØªØ§Ø­', rental_count: 6, total_revenue: 2400, image_url: 'hoverboard.jpg' },
          { id: 5, name: 'Ø³ÙƒÙˆØªØ± ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ', category: 'ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', price_per_hour: 70, status: 'Ù…ØªØ§Ø­', rental_count: 5, total_revenue: 2100, image_url: 'electric-scooter.jpg' }
        ];
        setGames(mockGames);
        calculateTopGames(mockGames);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
      if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
        setRecentRentals(rentalsResponse.value.data || []);
        setStats(prev => ({ ...prev, activeRentals: rentalsResponse.value.data.length }));
      } else {
        const mockRentals = [
          { 
            id: 1, 
            rental_number: 'RENT-001',
            customer_name: 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯',
            customer_phone: '01012345678',
            game_name: 'Ø¯Ø±ÙŠÙØª ÙƒØ§Ø±',
            branch_name: 'Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ',
            total_amount: 150,
            status: 'Ù†Ø´Ø·',
            start_time: new Date(Date.now() - 30 * 60000).toISOString(),
            duration_minutes: 30
          },
          { 
            id: 2, 
            rental_number: 'RENT-002',
            customer_name: 'Ù…Ø­Ù…Ø¯ Ø¹Ù„ÙŠ',
            customer_phone: '01123456789',
            game_name: 'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„ ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠ',
            branch_name: 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ',
            total_amount: 120,
            status: 'Ù†Ø´Ø·',
            start_time: new Date(Date.now() - 45 * 60000).toISOString(),
            duration_minutes: 45
          }
        ];
        setRecentRentals(mockRentals);
      }
      
      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†
      if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
        const usersData = usersResponse.value.data || [];
        setUsers(usersData);
        setStats(prev => ({ ...prev, totalUsers: usersData.length }));
        calculateTopCustomers(usersData);
      } else {
        calculateTopCustomers([]);
      }

      // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¥ÙŠØ±Ø§Ø¯Ø§Øª Ø§Ù„Ø´Ù‡Ø±ÙŠØ©
      const currentDate = new Date();
      const mockMonthlyRevenue = [];
      for (let i = 5; i >= 0; i--) {
        const date = new Date(currentDate.getFullYear(), currentDate.getMonth() - i, 1);
        mockMonthlyRevenue.push({
          month: date.toLocaleDateString('ar-EG', { month: 'long', year: 'numeric' }),
          month_name: date.toLocaleDateString('ar-EG', { month: 'long' }),
          revenue: Math.floor(Math.random() * 5000) + 3000
        });
      }
      setMonthlyRevenue(mockMonthlyRevenue);

    } catch (error) {
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù„Ø§Ø­Ù‚Ø§Ù‹.');
    } finally {
      setLoading(false);
    }
  };

  const calculateTopGames = (gamesData) => {
    const gamesWithRentals = gamesData.map(game => ({
      ...game,
      rental_count: game.total_rentals || game.rental_count || Math.floor(Math.random() * 20),
      total_revenue: (game.total_rentals || game.rental_count || 0) * (game.price_per_hour || 100)
    }));
    
    const sortedGames = gamesWithRentals.sort((a, b) => (b.rental_count || 0) - (a.rental_count || 0));
    setTopGames(sortedGames.slice(0, 5));
  };

  const calculateTopCustomers = (usersData) => {
    const mockCustomers = [
      { id: 1, name: 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯', email: 'ahmed@example.com', phone: '01012345678', rental_count: 12, total_spent: 4500 },
      { id: 2, name: 'Ù…Ø­Ù…Ø¯ Ø¹Ù„ÙŠ', email: 'mohamed@example.com', phone: '01123456789', rental_count: 8, total_spent: 3200 },
      { id: 3, name: 'Ø³Ø§Ø±Ø© Ø£Ø­Ù…Ø¯', email: 'sara@example.com', phone: '01234567890', rental_count: 6, total_spent: 2800 },
      { id: 4, name: 'Ø®Ø§Ù„Ø¯ Ù…ØµØ·ÙÙ‰', email: 'khaled@example.com', phone: '01512345678', rental_count: 5, total_spent: 2100 },
      { id: 5, name: 'ÙØ§Ø·Ù…Ø© Ø¹Ù…Ø±', email: 'fatema@example.com', phone: '01098765432', rental_count: 4, total_spent: 1800 }
    ];
    
    setTopCustomers(mockCustomers);
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number' || isNaN(amount)) {
      return '0.00 Ø¬';
    }
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' Ø¬';
  };

  const formatNumber = (number) => {
    if (typeof number !== 'number' || isNaN(number)) {
      return '0';
    }
    return number.toLocaleString('ar-EG');
  };

  const formatTime = (date) => {
    if (!date) return '--:--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const getStatusBadgeClass = (status) => {
    if (!status) return 'status-default';
    
    switch(status.toLowerCase()) {
      case 'Ù†Ø´Ø·':
      case 'active': return 'status-active';
      case 'Ù…ÙƒØªÙ…Ù„':
      case 'completed': return 'status-completed';
      case 'Ù…Ù„ØºÙŠ':
      case 'cancelled': return 'status-cancelled';
      case 'Ù…ØªØ§Ø­': return 'status-available';
      default: return 'status-default';
    }
  };

  const handleRentalAction = async (rentalId, action) => {
    try {
      let response;
      
      switch(action) {
        case 'end':
          const paymentMethod = prompt('Ø·Ø±ÙŠÙ‚Ø© Ø§Ù„Ø¯ÙØ¹:', 'ÙƒØ§Ø´');
          if (paymentMethod) {
            response = await api.completeRental(rentalId, { 
              payment_method: paymentMethod 
            });
          }
          break;
        case 'cancel':
          if (window.confirm('Ù‡Ù„ ØªØ±ÙŠØ¯ Ø¥Ù„ØºØ§Ø¡ Ù‡Ø°Ø§ Ø§Ù„ØªØ£Ø¬ÙŠØ±ØŸ')) {
            response = await api.cancelRental(rentalId);
          }
          break;
        case 'extend':
          const hours = prompt('Ø£Ø¯Ø®Ù„ Ø¹Ø¯Ø¯ Ø§Ù„Ø³Ø§Ø¹Ø§Øª Ø§Ù„Ø¥Ø¶Ø§ÙÙŠØ©:', '1');
          if (hours && !isNaN(hours)) {
            response = await api.updateRental(rentalId, {
              extended_hours: parseInt(hours)
            });
          }
          break;
        case 'details':
          window.location.href = `/admin/rentals/${rentalId}`;
          break;
      }
      
      if (response?.success) {
        alert('âœ… ØªÙ… ØªÙ†ÙÙŠØ° Ø§Ù„Ø¹Ù…Ù„ÙŠØ© Ø¨Ù†Ø¬Ø§Ø­');
        loadDashboardData();
