import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const { login, isAuthenticated, loading: authLoading, error: authError } = useAuth();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    console.log('üìä ÿ≠ÿßŸÑÿ© ÿßŸÑŸÖÿµÿßÿØŸÇÿ©:', isAuthenticated);
    
    if (isAuthenticated) {
      console.log('‚úÖ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ ÿØÿÆŸàŸÑÿå ÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ ÿßŸÑÿµŸÅÿ≠ÿ© ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ©...');
      
      // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿØŸàÿ± ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      const user = JSON.parse(localStorage.getItem('user'));
      const role = user?.role;
      
      console.log('üé≠ ÿØŸàÿ± ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', role);
      
      // ÿ™Ÿàÿ¨ŸäŸá ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿØŸàÿ±
      if (role === 'admin') {
        navigate('/admin/dashboard');
      } else if (role === 'branch_manager' || role === 'employee') {
        navigate('/employee/dashboard');
      } else {
        navigate('/dashboard');
      }
    }
  }, [isAuthenticated, navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();
    
    if (!email || !password) {
      setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸàŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ');
      return;
    }

    setLoading(true);
    setError('');

    try {
      console.log(`üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ: ${email}`);
      
      const result = await login(email, password);
      
      if (result.success) {
        console.log('‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠ ŸÖŸÜ ÿµŸÅÿ≠ÿ© Login');
        
        // ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ≥Ÿäÿ™ŸÖ ŸÅŸä useEffect ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ isAuthenticated
      } else {
        setError(result.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ');
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', error);
      setError('ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÑŸâ ÿßŸÑŸÖŸÜŸÅÿ∞ 5000');
    } finally {
      setLoading(false);
    }
  };

  const handleTestLogin = (type = 'admin') => {
    const credentials = {
      admin: { email: 'admin@l3bty.com', password: '123456' },
      manager: { email: 'manager@l3bty.com', password: '123456' },
      employee: { email: 'employee@l3bty.com', password: '123456' }
    };
    
    const cred = credentials[type];
    setEmail(cred.email);
    setPassword(cred.password);
  };

  if (authLoading) {
    return (
      <div className="loading-screen">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ...</p>
      </div>
    );
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <div className="login-header">
          <h1>
             <img
            src="/images/l3bty.png"
            alt="L3BTY Store Logo"
            className="logo-icon"
          />
            L3BTY Store
          </h1>
          <p className="login-subtitle">ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
        </div>

        <form onSubmit={handleLogin} className="login-form">
          {(error || authError) && (
            <div className="alert alert-error">
              <span className="alert-icon">‚ö†Ô∏è</span>
              {error || authError}
            </div>
          )}

          <div className="form-group">
            <label htmlFor="email">ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä</label>
            <input
              id="email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="ÿ£ÿØÿÆŸÑ ÿ®ÿ±ŸäÿØŸÉ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä"
              className="form-input"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±</label>
            <div className="password-input">
              <input
                id="password"
                type={showPassword ? "text" : "password"}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="ÿ£ÿØÿÆŸÑ ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ±"
                className="form-input"
                required
              />
              <button
                type="button"
                className="password-toggle"
                onClick={() => setShowPassword(!showPassword)}
              >
                {showPassword ? "üôà" : "üëÅÔ∏è"}
              </button>
            </div>
          </div>

          <div className="form-actions">
            <button
              type="submit"
              className="btn-login"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span className="spinner-small"></span>
                  ÿ¨ÿßÿ±Ÿä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ...
                </>
              ) : (
                'ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
              )}
            </button>
          </div>
        </form>

      </div>
    </div>
  );
};

export default Login;
