import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import activityService from '../../services/activityService';
import './AdminPages.css';

const GamesManagement = () => {
  const [games, setGames] = useState([]);
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingGame, setEditingGame] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterBranch, setFilterBranch] = useState('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);
  const [activeTab, setActiveTab] = useState('games'); // 'games', 'branches', 'reports'
  const [stats, setStats] = useState({
    totalGames: 0,
    availableGames: 0,
    rentedGames: 0,
    maintenanceGames: 0
  });
  
  const itemsPerPage = 10;

  const [newGame, setNewGame] = useState({
    name: '',
    category: 'سيارات',
    price_per_hour: '',
    deposit_amount: 500,
    branch_id: '',
    description: '',
    image_url: '',
    status: 'متاح',
    minimum_age: 16,
    max_speed: '',
    battery_life: '',
    weight_limit: ''
  });

  const categories = [
    'سيارات', 'دراجات نارية', 'كهربائية', 'أخرى'
  ];

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // جلب الفروع
      const branchesResponse = await authService.getBranches();
      if (branchesResponse.success) {
        setBranches(branchesResponse.data);
        if (branchesResponse.data.length > 0) {
          setNewGame(prev => ({
            ...prev,
            branch_id: branchesResponse.data[0].id
          }));
        }
      }

      // جلب الألعاب
      await loadGames();
      
    } catch (error) {
      setError('تعذر تحميل البيانات');
      console.error('Error loading data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadGames = async () => {
    try {
      const response = await authService.getGames();
      if (response.success) {
        setGames(response.data);
        calculateStats(response.data);
      } else {
        setError(response.message);
      }
    } catch (error) {
      console.error('Error loading games:', error);
      setError('تعذر تحميل الألعاب');
    }
  };

  const calculateStats = (gamesList) => {
    const totalGames = gamesList.length;
    const availableGames = gamesList.filter(g => g.status === 'متاح').length;
    const rentedGames = gamesList.filter(g => g.status === 'مؤجرة').length;
    const maintenanceGames = gamesList.filter(g => g.status === 'صيانة').length;
    
    setStats({
      totalGames,
      availableGames,
      rentedGames,
      maintenanceGames
    });
  };

  const handleAddGame = async () => {
    try {
      if (!newGame.name || !newGame.price_per_hour || !newGame.branch_id) {
        alert('يرجى ملء جميع الحقول المطلوبة');
        return;
      }

      const response = await AdminService.createGame(newGame);
      
      if (response.success) {
        alert('✅ تم إضافة اللعبة بنجاح');
        setShowAddModal(false);
        resetNewGame();
        loadGames();
        // تسجيل النشاط
        await activityService.log('create', 'game', response.data.id, newGame.name, 
          `تم إضافة لعبة جديدة: ${newGame.name}`);
      } else {
        alert(response.message || 'فشل في إضافة اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleUpdateGame = async () => {
    if (!editingGame) return;

    try {
      const response = await AdminService.updateGame(editingGame.id, editingGame);
      
      if (response.success) {
        alert('✅ تم تحديث اللعبة بنجاح');
        setShowEditModal(false);
        setEditingGame(null);
        loadGames();
        // تسجيل النشاط
        await activityService.log('update', 'game', editingGame.id, editingGame.name, 
          `تم تحديث معلومات اللعبة: ${editingGame.name}`);
      } else {
        alert(response.message || 'فشل في تحديث اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleStatus = async (game, newStatus) => {
    try {
      const response = await authService.updateGame(game.id, {
        status: newStatus
      });
      
      if (response.success) {
        // تسجيل النشاط
        await activityService.log('update', 'game', game.id, game.name, 
          `تم تغيير حالة اللعبة ${game.name} إلى ${newStatus}`);
        
        alert(`✅ تم تغيير حالة اللعبة إلى ${newStatus}`);
        loadGames();
      } else {
        alert(response.message || 'فشل في تغيير الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleDeleteGame = async (id, name) => {
    if (!window.confirm(`هل تريد حذف اللعبة "${name}"؟\nملاحظة: لا يمكن حذف اللعبة إذا كانت مرتبطة بتأجيرات سابقة.`)) return;

    try {
      const response = await AdminService.deleteGame(id);
      
      if (response.success) {
        alert('✅ تم حذف اللعبة بنجاح');
        loadGames();
        // تسجيل النشاط
        await activityService.log('delete', 'game', id, name, 
          `تم حذف اللعبة: ${name}`);
      } else {
        alert(response.message || 'فشل في حذف اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const resetNewGame = () => {
    setNewGame({
      name: '',
      category: 'سيارات',
      price_per_hour: '',
      deposit_amount: 500,
      branch_id: branches.length > 0 ? branches[0].id : '',
      description: '',
      image_url: '',
      status: 'متاح',
      minimum_age: 16,
      max_speed: '',
      battery_life: '',
      weight_limit: ''
    });
  };

  const getBranchName = (branchId) => {
    const branch = branches.find(b => b.id === branchId);
    return branch ? branch.name : 'غير محدد';
  };

  const getStatusBadgeClass = (status) => {
    switch(status) {
      case 'متاح': return 'available';
      case 'مؤجرة': return 'rented';
      case 'صيانة': return 'maintenance';
      default: return 'default';
    }
  };

  // فلترة وترتيب الألعاب
  const filteredGames = games.filter(game => {
    const matchesSearch = 
      (game.name && game.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (game.category && game.category.toLowerCase().includes(searchTerm.toLowerCase()));
    
    const matchesBranch = filterBranch === 'all' || game.branch_id == filterBranch;
    const matchesStatus = filterStatus === 'all' || game.status === filterStatus;
    
    return matchesSearch && matchesBranch && matchesStatus;
  });

  const totalPages = Math.ceil(filteredGames.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedGames = filteredGames.slice(startIndex, startIndex + itemsPerPage);

  // إحصائيات حسب الفروع
  const branchStats = {};
  games.forEach(game => {
    const branchName = getBranchName(game.branch_id);
    if (!branchStats[branchName]) {
      branchStats[branchName] = { total: 0, available: 0, rented: 0 };
    }
    branchStats[branchName].total++;
    if (game.status === 'متاح') branchStats[branchName].available++;
    if (game.status === 'مؤجرة') branchStats[branchName].rented++;
  });

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل الألعاب...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      {/* رأس الصفحة */}
      <div className="page-header">
        <div className="header-content">
          <h1>
            <span className="header-icon">🎮</span>
            إدارة الألعاب
            <span className="games-count">{games.length} لعبة</span>
          </h1>
          <p className="page-subtitle">إدارة جميع ألعاب النظام مرتبطة بالفروع</p>
        </div>
        <button 
          className="btn-primary"
          onClick={() => setShowAddModal(true)}
        >
          <span className="btn-icon">+</span>
          إضافة لعبة جديدة
        </button>
      </div>

      {/* علامات التبويب */}
      <div className="tabs-container">
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'games' ? 'active' : ''}`}
            onClick={() => setActiveTab('games')}
          >
            <span className="tab-icon">🎮</span>
            جميع الألعاب
            <span className="tab-badge">{games.length}</span>
          </button>
          <button 
            className={`tab ${activeTab === 'branches' ? 'active' : ''}`}
            onClick={() => setActiveTab('branches')}
          >
            <span className="tab-icon">🏬</span>
            الألعاب حسب الفرع
          </button>
          <button 
            className={`tab ${activeTab === 'reports' ? 'active' : ''}`}
            onClick={() => setActiveTab('reports')}
          >
            <span className="tab-icon">📊</span>
            تقارير الألعاب
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          <div className="alert-content">
            <span className="alert-icon">⚠️</span>
            <span>{error}</span>
          </div>
          <button 
            className="btn-retry"
            onClick={loadData}
          >
            <span className="retry-icon">🔄</span>
            إعادة المحاولة
          </button>
        </div>
      )}

      {/* إحصائيات سريعة */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">🎮</div>
          <div className="stat-content">
            <h3>إجمالي الألعاب</h3>
            <h2>{stats.totalGames}</h2>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">✅</div>
          <div className="stat-content">
            <h3>المتاحة</h3>
            <h2>{stats.availableGames}</h2>
            <div className="stat-subtext">{Math.round((stats.availableGames / stats.totalGames) * 100) || 0}%</div>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">⏱️</div>
          <div className="stat-content">
            <h3>المؤجرة</h3>
            <h2>{stats.rentedGames}</h2>
            <div className="stat-subtext">{Math.round((stats.rentedGames / stats.totalGames) * 100) || 0}%</div>
          </div>
        </div>
        <div className="stat-card">
          <div className="stat-icon">🔧</div>
          <div className="stat-content">
            <h3>تحت الصيانة</h3>
            <h2>{stats.maintenanceGames}</h2>
            <div className="stat-subtext">{Math.round((stats.maintenanceGames / stats.totalGames) * 100) || 0}%</div>
          </div>
        </div>
      </div>

      {/* شريط البحث والفلترة */}
      <div className="control-panel">
        <div className="search-box">
          <span className="search-icon">🔍</span>
          <input
            type="text"
            placeholder="بحث عن لعبة بالاسم أو التصنيف..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
          {searchTerm && (
            <button 
              className="clear-search"
              onClick={() => setSearchTerm('')}
            >
              ✕
            </button>
          )}
        </div>
        
        <div className="filters-row">
          <div className="filter-group">
            <span className="filter-icon">🏬</span>
            <select
              value={filterBranch}
              onChange={(e) => setFilterBranch(e.target.value)}
              className="filter-select"
            >
              <option value="all">جميع الفروع</option>
              {branches.map(branch => (
                <option key={branch.id} value={branch.id}>
                  {branch.name}
                </option>
              ))}
            </select>
          </div>
          
          <div className="filter-group">
            <span className="filter-icon">📊</span>
            <select
              value={filterStatus}
              onChange={(e) => setFilterStatus(e.target.value)}
              className="filter-select"
            >
              <option value="all">جميع الحالات</option>
              <option value="متاح">متاح</option>
              <option value="مؤجرة">مؤجرة</option>
              <option value="صيانة">صيانة</option>
            </select>
          </div>
          
          <div className="view-actions">
            <button 
              className="btn-action btn-export"
              title="تصدير البيانات"
            >
              📥 تصدير
            </button>
            <Link to="/admin/branches" className="btn-secondary">
              <span className="btn-icon">🏬</span>
              إدارة الفروع
            </Link>
          </div>
        </div>
      </div>

      {/* محتوى علامات التبويب */}
      {activeTab === 'games' && (
        <>
          {/* جدول الألعاب */}
          <div className="table-container">
            <table className="data-table">
              <thead>
                <tr>
                  <th>#</th>
                  <th>اللعبة</th>
                  <th>التصنيف</th>
                  <th>الفرع</th>
                  <th>السعر/ساعة</th>
                  <th>الحالة</th>
                  <th>عدد التأجيرات</th>
                  <th>الإجراءات</th>
                </tr>
              </thead>
              <tbody>
                {paginatedGames.length === 0 ? (
                  <tr>
                    <td colSpan="8" className="empty-state-cell">
                      <div className="empty-state">
                        <span className="empty-icon">🎮</span>
                        <p>لا توجد ألعاب مطابقة للبحث</p>
                        <button 
                          className="btn-primary"
                          onClick={() => setShowAddModal(true)}
                        >
                          + إضافة لعبة جديدة
                        </button>
                      </div>
                    </td>
                  </tr>
                ) : (
                  paginatedGames.map((game, index) => (
                    <tr key={game.id} className={index % 2 === 0 ? 'even' : 'odd'}>
                      <td className="text-center">{startIndex + index + 1}</td>
                      <td>
                        <div className="game-cell">
                          <div className="game-image-small">
                            <img 
                              src={`/images/${game.image_url || 'default-game.jpg'}`}
                              alt={game.name}
                              onError={(e) => {
                                e.target.onerror = null;
                                e.target.src = "/images/default-game.jpg";
                              }}
                            />
                          </div>
                          <div className="game-info">
                            <div className="game-name">{game.name}</div>
                            <div className="game-description">{game.description}</div>
                          </div>
                        </div>
                      </td>
                      <td>
                        <span className={`category-badge category-${game.category}`}>
                          {game.category}
                        </span>
                      </td>
                      <td>
                        <div className="branch-cell">
                          <span className="branch-icon">🏬</span>
                          {getBranchName(game.branch_id)}
                        </div>
                      </td>
                      <td className="price-cell">
                        <div className="price-amount">{game.price_per_hour} ج</div>
                        {game.deposit_amount > 0 && (
                          <div className="deposit-amount">تأمين: {game.deposit_amount} ج</div>
                        )}
                      </td>
                      <td>
                        <span className={`status-badge ${getStatusBadgeClass(game.status)}`}>
                          {game.status}
                          {game.status === 'متاح' && <span className="status-dot"></span>}
                        </span>
                      </td>
                      <td>
                        <div className="rental-count">
                          <span className="count-icon">🔄</span>
                          {game.rental_count || 0}
                        </div>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button 
                            className="btn-action btn-edit"
                            onClick={() => {
                              setEditingGame(game);
                              setShowEditModal(true);
                            }}
                            title="تعديل اللعبة"
                          >
                            ✏️
                          </button>
                          
                          
                          <button 
                            className="btn-action btn-view"
                            title="عرض التفاصيل"
                          >
                            👁️
                          </button>
                          
                          <button 
                            className="btn-action btn-delete"
                            onClick={() => handleDeleteGame(game.id, game.name)}
                            title="حذف اللعبة"
                          >
                            🗑️
                          </button>
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>

          {/* الترقيم */}
          {totalPages > 1 && (
            <div className="pagination">
              <button 
                className="pagination-btn"
                onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                disabled={currentPage === 1}
              >
                السابق
              </button>
              <div className="pagination-info">
                صفحة {currentPage} من {totalPages}
              </div>
              <div className="pagination-numbers">
                {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                  let page;
                  if (totalPages <= 5) {
                    page = i + 1;
                  } else if (currentPage <= 3) {
                    page = i + 1;
                  } else if (currentPage >= totalPages - 2) {
                    page = totalPages - 4 + i;
                  } else {
                    page = currentPage - 2 + i;
                  }
                  return (
                    <button
                      key={page}
                      className={`pagination-number ${currentPage === page ? 'active' : ''}`}
                      onClick={() => setCurrentPage(page)}
                    >
                      {page}
                    </button>
                  );
                })}
              </div>
              <button 
                className="pagination-btn"
                onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                disabled={currentPage === totalPages}
              >
                التالي
              </button>
            </div>
          )}
        </>
      )}

      {activeTab === 'branches' && (
        <div className="branches-stats-section">
          <h3 className="section-title">توزيع الألعاب على الفروع</h3>
          <div className="branches-grid">
            {branches.map(branch => {
              const branchGames = games.filter(g => g.branch_id === branch.id);
              const available = branchGames.filter(g => g.status === 'متاح').length;
              const rented = branchGames.filter(g => g.status === 'مؤجرة').length;
              const maintenance = branchGames.filter(g => g.status === 'صيانة').length;
              
              return (
                <div key={branch.id} className="branch-stat-card">
                  <div className="branch-header">
                    <h4>{branch.name}</h4>
                    <span className="branch-games-count">{branchGames.length} لعبة</span>
                  </div>
                  <div className="branch-stats">
                    <div className="branch-stat">
                      <span className="stat-label">متاحة:</span>
                      <span className="stat-value">{available}</span>
                    </div>
                    <div className="branch-stat">
                      <span className="stat-label">مؤجرة:</span>
                      <span className="stat-value">{rented}</span>
                    </div>
                    <div className="branch-stat">
                      <span className="stat-label">صيانة:</span>
                      <span className="stat-value">{maintenance}</span>
                    </div>
                  </div>
                  <div className="branch-actions">
                    <Link 
                      to={`/admin/branches/${branch.id}`}
                      className="btn-action btn-small"
                    >
                      عرض التفاصيل
                    </Link>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      )}

      {activeTab === 'reports' && (
        <div className="reports-section">
          <div className="reports-grid">
            <div className="report-card">
              <div className="report-icon">📈</div>
              <h3>الألعاب الأكثر تأجيراً</h3>
              <div className="report-content">
                {games
                  .filter(g => g.rental_count > 0)
                  .sort((a, b) => (b.rental_count || 0) - (a.rental_count || 0))
                  .slice(0, 5)
                  .map((game, index) => (
                    <div key={game.id} className="top-game-item">
                      <span className="game-rank">#{index + 1}</span>
                      <span className="game-name">{game.name}</span>
                      <span className="game-count">{game.rental_count} تأجير</span>
                    </div>
                  ))
                }
              </div>
            </div>
            
            <div className="report-card">
              <div className="report-icon">💰</div>
              <h3>الأعلى إيرادات</h3>
              <div className="report-content">
                {games
                  .sort((a, b) => (b.total_revenue || 0) - (a.total_revenue || 0))
                  .slice(0, 5)
                  .map((game, index) => (
                    <div key={game.id} className="top-game-item">
                      <span className="game-rank">#{index + 1}</span>
                      <span className="game-name">{game.name}</span>
                      <span className="game-revenue">{game.total_revenue || 0} ج</span>
                    </div>
                  ))
                }
              </div>
            </div>
            
            <div className="report-card">
              <div className="report-icon">🔧</div>
              <h3>الألعاب تحت الصيانة</h3>
              <div className="report-content">
                {games
                  .filter(g => g.status === 'صيانة')
                  .slice(0, 5)
                  .map((game, index) => (
                    <div key={game.id} className="maintenance-item">
                      <span className="game-name">{game.name}</span>
                      <span className="game-branch">{getBranchName(game.branch_id)}</span>
                    </div>
                  ))
                }
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Modal إضافة لعبة */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal wide-modal">
            <div className="modal-header">
              <h2>🎮 إضافة لعبة جديدة</h2>
              <button 
                className="close-btn"
                onClick={() => {
                  setShowAddModal(false);
                  resetNewGame();
                }}
              >
                ✕
              </button>
            </div>
            <div className="modal-body">
              <div className="form-grid">
                <div className="form-group">
                  <label>اسم اللعبة *</label>
                  <input
                    type="text"
                    value={newGame.name}
                    onChange={(e) => setNewGame({...newGame, name: e.target.value})}
                    placeholder="أدخل اسم اللعبة"
                    className="form-input"
                  />
                </div>
                
                <div className="form-group">
                  <label>التصنيف *</label>
                  <select
                    value={newGame.category}
                    onChange={(e) => setNewGame({...newGame, category: e.target.value})}
                    className="form-select"
                  >
                    {categories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
                
                <div className="form-group">
                  <label>الفرع *</label>
                  <select
                    value={newGame.branch_id}
                    onChange={(e) => setNewGame({...newGame, branch_id: parseInt(e.target.value)})}
                    className="form-select"
                  >
                    <option value="">اختر الفرع</option>
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>
                        {branch.name}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="form-group">
                  <label>سعر الساعة (جنية) *</label>
                  <input
                    type="number"
                    value={newGame.price_per_hour}
                    onChange={(e) => setNewGame({...newGame, price_per_hour: e.target.value})}
                    placeholder="أدخل سعر الساعة"
                    className="form-input"
                    min="1"
                  />
                </div>
                
                <div className="form-group">
                  <label>مبلغ التأمين (جنية)</label>
                  <input
                    type="number"
                    value={newGame.deposit_amount}
                    onChange={(e) => setNewGame({...newGame, deposit_amount: e.target.value})}
                    placeholder="مبلغ التأمين"
                    className="form-input"
                    min="0"
                  />
                </div>
                
                <div className="form-group">
                  <label>الحد الأدنى للعمر</label>
                  <input
                    type="number"
                    value={newGame.minimum_age}
                    onChange={(e) => setNewGame({...newGame, minimum_age: e.target.value})}
                    placeholder="الحد الأدنى للعمر"
                    className="form-input"
                    min="1"
                  />
                </div>
                
                <div className="form-group">
                  <label>السرعة القصوى</label>
                  <input
                    type="text"
                    value={newGame.max_speed}
                    onChange={(e) => setNewGame({...newGame, max_speed: e.target.value})}
                    placeholder="السرعة القصوى (كم/ساعة)"
                    className="form-input"
                  />
                </div>
                
                <div className="form-group">
                  <label>مدة البطارية</label>
                  <input
                    type="text"
                    value={newGame.battery_life}
                    onChange={(e) => setNewGame({...newGame, battery_life: e.target.value})}
                    placeholder="مدة البطارية (ساعات)"
                    className="form-input"
                  />
                </div>
                
                <div className="form-group">
                  <label>الحد الأقصى للوزن</label>
                  <input
                    type="text"
                    value={newGame.weight_limit}
                    onChange={(e) => setNewGame({...newGame, weight_limit: e.target.value})}
                    placeholder="الحد الأقصى للوزن (كجم)"
                    className="form-input"
                  />
                </div>
                
                <div className="form-group full-width">
                  <label>صورة اللعبة (اسم الملف)</label>
                  <input
                    type="text"
                    value={newGame.image_url}
                    onChange={(e) => setNewGame({...newGame, image_url: e.target.value})}
                    placeholder="اسم ملف الصورة من مجلد images"
                    className="form-input"
                  />
                  <small className="form-hint">أمثلة: drift-car.jpg, electric-bike.jpg</small>
                </div>
                
                <div className="form-group full-width">
                  <label>الوصف</label>
                  <textarea
                    value={newGame.description}
                    onChange={(e) => setNewGame({...newGame, description: e.target.value})}
                    placeholder="وصف مختصر للعبة"
                    rows="3"
                    className="form-textarea"
                  />
                </div>
                
                <div className="form-group">
                  <label>الحالة</label>
                  <select
                    value={newGame.status}
                    onChange={(e) => setNewGame({...newGame, status: e.target.value})}
                    className="form-select"
                  >
                    <option value="متاح">متاح</option>
                    <option value="مؤجرة">مؤجرة</option>
                    <option value="صيانة">صيانة</option>
                  </select>
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-secondary"
                onClick={() => {
                  setShowAddModal(false);
                  resetNewGame();
                }}
              >
                إلغاء
              </button>
              <button 
                className="btn-primary"
                onClick={handleAddGame}
              >
                <span className="btn-icon">✓</span>
                حفظ اللعبة
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal تعديل لعبة */}
      {showEditModal && editingGame && (
        <div className="modal-overlay">
          <div className="modal wide-modal">
            <div className="modal-header">
              <h2>✏️ تعديل لعبة: {editingGame.name}</h2>
              <button 
                className="close-btn"
                onClick={() => {
                  setShowEditModal(false);
                  setEditingGame(null);
                }}
              >
                ✕
              </button>
            </div>
            <div className="modal-body">
              <div className="form-grid">
                <div className="form-group">
                  <label>اسم اللعبة *</label>
                  <input
                    type="text"
                    value={editingGame.name}
                    onChange={(e) => setEditingGame({...editingGame, name: e.target.value})}
                    className="form-input"
                  />
                </div>
                
                <div className="form-group">
                  <label>التصنيف *</label>
                  <select
                    value={editingGame.category}
                    onChange={(e) => setEditingGame({...editingGame, category: e.target.value})}
                    className="form-select"
                  >
                    {categories.map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>
                
                <div className="form-group">
                  <label>الفرع *</label>
                  <select
                    value={editingGame.branch_id}
                    onChange={(e) => setEditingGame({...editingGame, branch_id: parseInt(e.target.value)})}
                    className="form-select"
                  >
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>
                        {branch.name}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="form-group">
                  <label>سعر الساعة (جنية) *</label>
                  <input
                    type="number"
                    value={editingGame.price_per_hour}
                    onChange={(e) => setEditingGame({...editingGame, price_per_hour: e.target.value})}
                    className="form-input"
                    min="1"
                  />
                </div>
                
                <div className="form-group">
                  <label>الحالة</label>
                  <select
                    value={editingGame.status}
                    onChange={(e) => setEditingGame({...editingGame, status: e.target.value})}
                    className="form-select"
                  >
                    <option value="متاح">متاح</option>
                    <option value="مؤجرة">مؤجرة</option>
                    <option value="صيانة">صيانة</option>
                  </select>
                </div>
                
                <div className="form-group full-width">
                  <label>الوصف</label>
                  <textarea
                    value={editingGame.description || ''}
                    onChange={(e) => setEditingGame({...editingGame, description: e.target.value})}
                    rows="3"
                    className="form-textarea"
                  />
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-secondary"
                onClick={() => {
                  setShowEditModal(false);
                  setEditingGame(null);
                }}
              >
                إلغاء
              </button>
              <button 
                className="btn-primary"
                onClick={handleUpdateGame}
              >
                <span className="btn-icon">✓</span>
                حفظ التغييرات
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GamesManagement;
