  });

  // ==================== LOAD USER & SHIFT DATA ====================
  const loadUserAndShift = useCallback(async () => {
    try {
      console.log('üë§ ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ...');
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ localStorage
      const user = api.getCurrentUser();
      setCurrentUser(user);
      
      if (user?.branch_id) {
        // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
        const branchRes = await api.getBranch(user.branch_id);
        if (branchRes?.success) {
          setUserBranch(branchRes.data);
          console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ:', branchRes.data.name);
        }
      }
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
