      'ÿßŸÑŸáÿßÿ™ŸÅ': rental.customer_phone,
      'ÿßŸÑŸÑÿπÿ®ÿ©': rental.game_name,
      'ÿßŸÑŸÖÿ®ŸÑÿ∫': rental.total_amount,
      'ÿßŸÑŸÖÿØŸÅŸàÿπ': rental.paid_amount,
      'ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ': rental.payment_status,
      'ÿßŸÑÿ≠ÿßŸÑÿ©': rental.status,
      'ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™': rental.is_open_time ? 'ŸÖŸÅÿ™Ÿàÿ≠' : 'ÿ´ÿßÿ®ÿ™',
      'ÿßŸÑŸÖÿØÿ© (ÿØŸÇŸäŸÇÿ©)': rental.duration_minutes || 0,
