// frontend/src/pages/employee/Dashboard.jsx (ŸÖÿ≠ÿØÿ´)
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    loadBranchInfo();
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      const statsResponse = await api.get('/dashboard/stats');
      if (statsResponse.data.success) {
        setStats(statsResponse.data.data);
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©
      const gamesResponse = await api.get('/games');
      if (gamesResponse.data.success) {
        setAvailableGames(gamesResponse.data.data.filter(game => game.status === 'ŸÖÿ™ÿßÿ≠'));
      }

      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
      const rentalsResponse = await api.get('/rentals?status=ŸÜÿ¥ÿ∑');
      if (rentalsResponse.data.success) {
        setActiveRentals(rentalsResponse.data.data);
      }

    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadBranchInfo = async () => {
    try {
      const response = await api.get('/branches');
      if (response.data.success && user?.branch_id) {
        const userBranch = response.data.data.find(b => b.id === user.branch_id);
        setBranch(userBranch);
      }
    } catch (error) {
      console.error('Error loading branch info:', error);
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 2
    }).format(amount);
  };

  const formatTime = (dateString) => {
    return new Date(dateString).toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const calculateTimeLeft = (startTime, expectedDuration) => {
    const start = new Date(startTime);
    const expectedEnd = new Date(start.getTime() + expectedDuration * 60000);
    const now = new Date();
    const diff = expectedEnd - now;
    
    if (diff <= 0) return 'ŸÖŸÜÿ™ŸáŸä';
    
    const minutes = Math.floor(diff / 60000);
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    return `${Math.floor(minutes / 60)} ÿ≥ÿßÿπÿ© ${minutes % 60} ÿØŸÇŸäŸÇÿ©`;
  };

  const handleCompleteRental = async (rentalId) => {
    if (window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° Ÿáÿ∞ÿß ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿü')) {
      try {
        await api.put(`/rentals/${rentalId}/complete`);
        loadDashboardData();
      } catch (error) {
        alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    }
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="employee-dashboard">
      {/* ÿ±ÿ£ÿ≥ ÿßŸÑÿµŸÅÿ≠ÿ© */}
      <div className="dashboard-header">
        <div>
          <h1>ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖŸàÿ∏ŸÅ</h1>
          <p>
            {branch?.name ? `ŸÅÿ±ÿπ ${branch.name} - ${branch.location}` : 'ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®'}
          </p>
        </div>
        <div className="header-info">
          <span className="welcome">ŸÖÿ±ÿ≠ÿ®ÿßŸã {user?.name}</span>
          <span className="role">
            {user?.role === 'branch_manager' ? 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ' : 'ŸÖŸàÿ∏ŸÅ'}
          </span>
        </div>
      </div>

      {/* ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© */}
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">üéÆ</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</h3>
            <h2>{stats.availableGames}</h2>
            <p>ŸÑÿπÿ®ÿ© ÿ¨ÿßŸáÿ≤ÿ©</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚è±Ô∏è</div>
          <div className="stat-content">
            <h3>ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h3>
            <h2>{stats.activeRentals}</h2>
            <p>ÿ™ÿ£ÿ¨Ÿäÿ± ŸÇŸäÿØ ÿßŸÑÿ™ÿ¥ÿ∫ŸäŸÑ</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üí∞</div>
          <div className="stat-content">
            <h3>ÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{formatCurrency(stats.todayRevenue)}</h2>
            <p>ÿ¨ŸÜŸäŸá ŸÖÿµÿ±Ÿä</p>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìà</div>
          <div className="stat-content">
            <h3>ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸäŸàŸÖ</h3>
            <h2>{stats.todayRentals}</h2>
            <p>ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÉÿ™ŸÖŸÑ</p>
          </div>
        </div>
      </div>

    

      {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© */}
      <div className="active-rentals-section">
        <div className="section-header">
          <h2>‚è±Ô∏è ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©</h2>
          <button onClick={loadDashboardData} className="btn-refresh">
            üîÑ ÿ™ÿ≠ÿØŸäÿ´
          </button>
        </div>
        
        {activeRentals.length === 0 ? (
          <div className="empty-state">
            <p>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ© ÿ≠ÿßŸÑŸäÿßŸã</p>
          </div>
        ) : (
          <div className="rentals-grid">
            {activeRentals.map(rental => (
              <div key={rental.id} className="rental-card">
                <div className="rental-header">
                  <h3>#{rental.rental_number}</h3>
                  <span className="timer">
                    ‚è∞ {calculateTimeLeft(rental.start_time, rental.duration_minutes)}
                  </span>
                </div>
                
                <div className="rental-info">
                  <div className="info-row">
                    <span className="label">ÿßŸÑÿπŸÖŸäŸÑ:</span>
                    <span className="value">{rental.customer_name}</span>
                  </div>
                  <div className="info-row">
                    <span className="label">ÿßŸÑŸÑÿπÿ®ÿ©:</span>
                    <span className="value">{rental.game_name}</span>
                  </div>
                  <div className="info-row">
                    <span className="label">ÿ®ÿØÿ£ ŸÅŸä:</span>
                    <span className="value">{formatTime(rental.start_time)}</span>
                  </div>
                  <div className="info-row">
                    <span className="label">ÿßŸÑŸÖÿ®ŸÑÿ∫:</span>
                    <span className="value amount">{formatCurrency(rental.total_amount)}</span>
                  </div>
                </div>
                
                <div className="rental-actions">
                  <button className="btn-extend">ÿ™ŸÖÿØŸäÿØ</button>
                  <button 
                    className="btn-complete"
                    onClick={() => handleCompleteRental(rental.id)}
                  >
                    ÿ•ŸÜŸáÿßÿ°
                  </button>
                  <button className="btn-details">ÿ™ŸÅÿßÿµŸäŸÑ</button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>

      {/* ÿ™ÿπŸÑŸäŸÖÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ© */}
      <div className="quick-guide">
        <h2>üìù ÿ™ÿπŸÑŸäŸÖÿßÿ™ ÿ≥ÿ±Ÿäÿπÿ©</h2>
        <div className="guide-steps">
          <div className="step">
            <div className="step-number">1</div>
            <div className="step-content">
              <h4>ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿπŸÖŸäŸÑ</h4>
              <p>ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿπŸÖŸäŸÑ ÿ£Ÿà ÿ≥ÿ¨ŸÑ ÿπŸÖŸäŸÑ ÿ¨ÿØŸäÿØ</p>
            </div>
          </div>
          <div className="step">
            <div className="step-number">2</div>
            <div className="step-content">
              <h4>ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑŸÑÿπÿ®ÿ©</h4>
              <p>ÿßÿÆÿ™ÿ± ÿßŸÑŸÑÿπÿ®ÿ© ÿßŸÑŸÖŸÜÿßÿ≥ÿ®ÿ© Ÿàÿ≠ÿØÿØ ŸÖÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</p>
            </div>
          </div>
          <div className="step">
            <div className="step-number">3</div>
            <div className="step-content">
              <h4>ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</h4>
              <p>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ ÿ®ÿØÿ° Ÿàÿ≥Ÿäÿ®ÿØÿ£ ÿßŸÑÿπÿØ ÿßŸÑÿ™ŸÜÿßÿ≤ŸÑŸä ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã</p>
            </div>
          </div>
          <div className="step">
            <div className="step-number">4</div>
            <div className="step-content">
              <h4>ÿßŸÑŸÖÿ™ÿßÿ®ÿπÿ©</h4>
              <p>ÿ™ÿßÿ®ÿπ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä Ÿàÿ≠ÿ≥ÿ® ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑŸÖÿØÿ©</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EmployeeDashboard;
