import React, { useState, useEffect, useMemo, useCallback } from 'react';
import { 
  Search, Download, RefreshCw, Calendar, 
  DollarSign, TrendingUp, BarChart3, FileText,
  CheckCircle, XCircle, Eye, Printer,
  Clock, Users, ChevronLeft, ChevronRight, 
  AlertCircle, Check, X, Plus, Minus, Calculator,
  Lock, Unlock, Edit, Trash2, User, Gamepad2, CreditCard,
  Calendar as CalendarIcon, Clock as ClockIcon,
  Phone, Mail, MapPin, Package, Building,
  Zap
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== ÿØŸàÿßŸÑ ŸÖÿ≥ÿßÿπÿØÿ© ====================
const formatCurrency = (amount) => {
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => /^01[0-9]{9}$/.test(phone);

const generateRentalNumber = () => {
  const timestamp = Date.now().toString().slice(-8);
  const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
  return `RENT-${timestamp}-${random}`;
};

const GamesGrid = ({ games, branchId, onSelectGame }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [availableGames, setAvailableGames] = useState([]);

  useEffect(() => {
    if (!branchId) return;
    
    console.log(`üéÆ ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÑŸÅÿ±ÿπ ${branchId}`);
    
    // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸÇÿ∑
    const branchGames = games.filter(game => {
      const gameBranchId = parseInt(game.branch_id);
      const currentBranchId = parseInt(branchId);
      
      // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑŸÑÿπÿ®ÿ© ÿ™ÿßÿ®ÿπÿ© ŸÑŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä
      const isSameBranch = gameBranchId === currentBranchId;
      
      // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑŸÑÿπÿ®ÿ© ŸÖÿ™ÿßÿ≠ÿ© ŸàŸÜÿ¥ÿ∑ÿ©
      const isAvailable = game.status === 'ŸÖÿ™ÿßÿ≠' || game.status === 'available';
      const isActive = game.is_active === 1 || game.is_active === true;
      
      return isSameBranch && isAvailable && isActive;
    });
    
    console.log(`üìä ÿπÿØÿØ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ®ÿπÿØ ÿßŸÑÿ™ÿµŸÅŸäÿ©: ${branchGames.length}`);
    setAvailableGames(branchGames);
  }, [games, branchId]);

  const getGameImage = useCallback((game) => {
    const imageMap = {
      'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±': '/images/Driftcar.jpg',
      'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
      'ÿ≥ŸÉŸàÿ™ÿ±': '/images/Scooter.jpg',
      'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': '/images/Car.jpg',
      'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Motor.jpg',
      'Ÿáÿßÿ±ŸÑŸä': '/images/harley.jpg',
      'ÿ≥Ÿäÿ¨ŸàÿßŸä': '/images/Segway.jpg',
      'ŸÉÿ±ÿßÿ≤Ÿä ŸÉÿßÿ±': '/images/Ninebot.jpg',
      'ÿ≥ŸÉŸäÿ™ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Skate.jpg',
      'ÿ™ÿ±ÿßŸÖÿ®ŸàŸÑŸäŸÜ': '/images/Trampoline.jpg',
      'ŸÖÿ≠ÿßŸÉŸä': '/images/Simulator.jpg'
    };
    
    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿµŸàÿ±ÿ© ŸÖŸÜ ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ£ŸàŸÑÿßŸã
    if (game.image_url && game.image_url !== 'default-game.jpg') {
      return game.image_url;
    }
    
    // ÿ´ŸÖ ŸÖŸÜ external_image_url
    if (game.external_image_url) {
      return game.external_image_url;
    }
    
    // ÿ´ŸÖ ŸÖŸÜ ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ´ÿßÿ®ÿ™ÿ©
    return imageMap[game.name] || '/images/default-game.jpg';
  }, []);

  const filteredGames = useMemo(() => {
    if (!availableGames.length) return [];
    
    return availableGames.filter(game => {
      const matchesSearch = game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (game.category || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (game.description || '').toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || game.category === selectedCategory;
      
      return matchesSearch && matchesCategory;
    });
  }, [availableGames, searchTerm, selectedCategory]);

  const categories = useMemo(() => {
    if (!availableGames.length) return ['all'];
    
    const cats = new Set(['all']);
    availableGames.forEach(game => {
      if (game.category) {
        cats.add(game.category);
      }
    });
    return Array.from(cats);
  }, [availableGames]);

  // ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸáŸÜÿßŸÉ ŸÅÿ±ÿπ ŸÖÿ≠ÿØÿØ
  if (!branchId) {
    return (
      <div className="games-grid-section">
        <div className="empty-state">
          <div className="empty-icon">üè¨</div>
          <h3>ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ</h3>
          <p>Ÿäÿ¨ÿ® ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©</p>
        </div>
      </div>
    );
  }

  return (
    <div className="games-grid-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">üéÆ</span>
          ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä
          <span className="section-count">({availableGames.length})</span>
          <span className="branch-badge">ÿßŸÑŸÅÿ±ÿπ #{branchId}</span>
        </h3>
        
        <div className="section-controls">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="ÿßÿ®ÿ≠ÿ´ ÿπŸÜ ŸÑÿπÿ®ÿ©..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="clear-search">
                <X size={16} />
              </button>
            )}
          </div>
          
          {categories.length > 1 && (
            <div className="filter-controls">
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="filter-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat === 'all' ? 'ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ¶ÿßÿ™' : cat}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
      </div>
      
      {availableGames.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">üéÆ</div>
          <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÑŸáÿ∞ÿß ÿßŸÑŸÅÿ±ÿπ</h3>
          <p>ŸÇŸÖ ÿ®ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ÿ¨ÿØŸäÿØÿ© ŸÑŸÑŸÅÿ±ÿπ ŸÖŸÜ ÿµŸÅÿ≠ÿ© ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÅÿ±Ÿàÿπ</p>
          <div className="empty-actions">
            <button 
              className="btn btn-primary"
              onClick={() => window.location.href = '/branches'}
            >
              <Plus size={16} />
              ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ŸÑŸÑŸÅÿ±ÿπ
            </button>
          </div>
        </div>
      ) : filteredGames.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">üîç</div>
          <h3>ŸÑŸÖ Ÿäÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ£ŸÑÿπÿßÿ®</h3>
<p>
  ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ÿ™ÿ∑ÿßÿ®ŸÇ ÿ®ÿ≠ÿ´ŸÉ &quot;{searchTerm}&quot;
</p>

          <button 
            className="btn btn-secondary"
            onClick={() => {
              setSearchTerm('');
              setSelectedCategory('all');
            }}
          >
            ÿπÿ±ÿ∂ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ®
          </button>
        </div>
      ) : (
        <>
          <div className="games-stats">
            <div className="stat-item">
              <span className="stat-label">ÿ•ÿ¨ŸÖÿßŸÑŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®:</span>
              <span className="stat-value">{availableGames.length}</span>
            </div>
            <div className="stat-item">
              <span className="stat-label">ÿ™ŸÖÿ™ ÿ™ÿµŸÅŸäÿ™Ÿáÿß:</span>
              <span className="stat-value">{filteredGames.length}</span>
            </div>
            {selectedCategory !== 'all' && (
              <div className="stat-item">
                <span className="stat-label">ÿßŸÑŸÅÿ¶ÿ©:</span>
                <span className="stat-value">{selectedCategory}</span>
              </div>
            )}
          </div>
          
          <div className="games-grid">
            {filteredGames.map(game => (
              <div 
                key={game.id} 
                className="game-card"
                onClick={() => onSelectGame(game)}
                title={`ÿßŸÜŸÇÿ± ŸÑÿ•ÿ∂ÿßŸÅÿ© ${game.name} ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±`}
              >
                <div className="game-image-container">
                  <img 
                    src={getGameImage(game)} 
                    alt={game.name}
                    className="game-image"
                    onError={(e) => {
                      console.warn(`ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿµŸàÿ±ÿ© ${game.name}:`, e.target.src);
                      e.target.src = '/images/default-game.jpg';
                    }}
                  />
                  <div className="game-status-badge available">
                    <CheckCircle size={14} />
                    <span>ŸÖÿ™ÿßÿ≠</span>
                  </div>
                </div>
                
                <div className="game-info">
                  <h4 className="game-name">{game.name}</h4>
                  {game.description && (
                    <p className="game-description">{game.description}</p>
                  )}
                  
                  <div className="game-details">
                    <span className="game-category">{game.category || 'ÿπÿßŸÖ'}</span>
                    <span className="game-price">
                      {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                    </span>
                  </div>
                  
                  <div className="game-meta">
                    <span className="game-id">#{game.id}</span>
                    <span className="game-price-hour">
                      {formatCurrency(game.price_per_hour || (game.price_per_15min * 4))} / ÿ≥ÿßÿπÿ©
                    </span>
                  </div>
                  
                  <div className="game-actions">
                    <button className="select-game-btn">
                      <span className="btn-icon">‚ûï</span>
                      <span className="btn-text">ÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±</span>
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

// ==================== ŸÖŸÉŸàŸÜ ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ ====================
const MultiRentalModal = ({ show, onClose, selectedGames, onConfirmRental, currentShift }) => {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });
  
  const [rentalItems, setRentalItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('ŸÉÿßÿ¥');
  const [paymentStatus, setPaymentStatus] = useState('ŸÖÿØŸÅŸàÿπ');
  const [notes, setNotes] = useState('');
  
  useEffect(() => {
    if (show && selectedGames.length > 0) {
      const items = selectedGames.map(game => ({
        id: Date.now() + Math.random(),
        gameId: game.id,
        gameName: game.name,
        gameImage: '/images/default-game.jpg',
        rentalType: 'fixed',
        duration: 15,
        pricePer15min: game.price_per_15min || 100,
        quantity: 1,
        notes: ''
      }));
      setRentalItems(items);
    }
  }, [show, selectedGames]);

  const updateRentalItem = (id, updates) => {
    setRentalItems(items => 
      items.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  };

  const removeRentalItem = (id) => {
    setRentalItems(items => items.filter(item => item.id !== id));
  };

  const calculateItemPrice = (item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return item.pricePer15min * units * item.quantity;
  };

  const calculateTotal = () => {
    return rentalItems.reduce((total, item) => {
      return total + calculateItemPrice(item);
    }, 0);
  };

  const handleSubmit = () => {
    if (!customerInfo.name || !customerInfo.phone) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ Ÿàÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
      return;
    }
    
    if (!validatePhone(customerInfo.phone)) {
      alert('ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠. Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ 11 ÿ±ŸÇŸÖÿßŸã ŸàŸäÿ®ÿØÿ£ ÿ®ŸÄ 01');
      return;
    }
    
    if (rentalItems.length === 0) {
      alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      return;
    }

    const rentalData = {
      customerInfo,
      rentalItems,
      paymentMethod,
      paymentStatus,
      notes,
      totalAmount: calculateTotal(),
      shiftId: currentShift?.id,
      timestamp: new Date().toISOString()
    };

    onConfirmRental(rentalData);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal multi-rental-modal">
        <div className="modal-header">
          <div className="modal-title">
            <h2>
              <span className="modal-icon">üéØ</span>
              ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ
              {currentShift && (
                <span className="shift-badge">ÿßŸÑÿ¥ŸäŸÅÿ™ #{currentShift.id}</span>
              )}
            </h2>
          </div>
          <button onClick={onClose} className="modal-close">‚úï</button>
        </div>
        
        <div className="modal-body">
          {/* ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿπŸÖŸäŸÑ */}
          <div className="customer-section">
            <h3 className="section-title">
              <User size={20} />
              ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿπŸÖŸäŸÑ
            </h3>
            <div className="form-grid">
              <div className="form-group">
                <label>ÿßŸÑÿßÿ≥ŸÖ ÿßŸÑŸÉÿßŸÖŸÑ *</label>
                <div className="input-with-icon">
                  <User size={18} />
                  <input
                    type="text"
                    value={customerInfo.name}
                    onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                    placeholder="ÿ£ÿØÿÆŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ"
                    className="form-input"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ *</label>
                <div className="input-with-icon">
                  <Phone size={18} />
                  <input
                    type="tel"
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                    placeholder="01xxxxxxxxx"
                    maxLength={11}
                    className="form-input"
                  />
                </div>
                {customerInfo.phone && !validatePhone(customerInfo.phone) && (
