// ==================== ŸÖŸÉŸàŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ====================
const ActiveRentalsTable = ({ rentals, onAction, onRefresh }) => {
  const [selectedRentals, setSelectedRentals] = useState([]);
  const [countdowns, setCountdowns] = useState({});

  useEffect(() => {
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿπÿØÿßÿØÿßÿ™ ÿßŸÑÿ™ŸÜÿßÿ≤ŸÑŸäÿ© ŸÉŸÑ ÿ´ÿßŸÜŸäÿ©
    const interval = setInterval(() => {
      const newCountdowns = {};
      
      rentals.forEach(rental => {
        if (!rental.is_open_time && rental.rental_type !== 'open') {
          const endTime = new Date(rental.start_time);
          endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
          const now = new Date();
          
          if (now > endTime) {
            newCountdowns[rental.id] = '‚è∞ ÿßŸÜÿ™ŸáŸâ';
          } else {
            const diffMs = endTime - now;
            const minutes = Math.floor(diffMs / 60000);
            const seconds = Math.floor((diffMs % 60000) / 1000);
            newCountdowns[rental.id] = `${minutes}:${seconds.toString().padStart(2, '0')}`;
          }
        } else {
          // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä ŸÑŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠
          const startTime = new Date(rental.start_time);
          const now = new Date();
          const diffMs = now - startTime;
          const minutes = Math.floor(diffMs / 60000);
          const seconds = Math.floor((diffMs % 60000) / 1000);
          newCountdowns[rental.id] = `‚è≥ ${minutes}:${seconds.toString().padStart(2, '0')}`;
        }
      });
      
      setCountdowns(newCountdowns);
    }, 1000);
    
    return () => clearInterval(interval);
  }, [rentals]);

  const handleSelectAll = () => {
    if (selectedRentals.length === rentals.length) {
      setSelectedRentals([]);
    } else {
      setSelectedRentals(rentals.map(r => r.id));
    }
  };

  const handleSelectRental = (rentalId) => {
    setSelectedRentals(prev =>
      prev.includes(rentalId)
        ? prev.filter(id => id !== rentalId)
        : [...prev, rentalId]
    );
  };

  const getRentalTimeInfo = (rental) => {
    if (rental.is_open_time || rental.rental_type === 'open') {
      return {
        type: '‚è≥ ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠',
        remaining: countdowns[rental.id] || '‚è≥ 0:00'
      };
    }
    
    const endTime = new Date(rental.start_time);
    endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
    const now = new Date();
    
    if (now > endTime) {
      return {
        type: '‚è∞ ÿßŸÜÿ™ŸáŸâ',
        remaining: '‚è∞ 0:00'
      };
    }
    
    return {
      type: `${rental.duration_minutes} ÿØŸÇŸäŸÇÿ©`,
      remaining: countdowns[rental.id] || `${rental.duration_minutes}:00`
    };
  };

  const calculateRemainingMinutes = (rental) => {
    if (rental.is_open_time || rental.rental_type === 'open') return null;
    
    const endTime = new Date(rental.start_time);
    endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
    const now = new Date();
    
    return Math.max(0, Math.floor((endTime - now) / 60000));
  };

  const calculateRentalAmount = (rental) => {
    if (rental.total_amount > 0) {
      return formatCurrency(rental.total_amount);
    }
    
    if (rental.is_open_time || rental.rental_type === 'open') {
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÖÿ®ŸÑÿ∫ ŸÑŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const minutes = Math.floor((now - startTime) / 60000);
      const units = Math.ceil(minutes / 15);
      const pricePer15Min = rental.price_per_15min || 100;
      
      return formatCurrency(units * pricePer15Min);
    }
    
    return formatCurrency(rental.total_amount || 0);
  };

  return (
    <div className="active-rentals-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">üî•</span>
          ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
          <span className="section-count">({rentals.length})</span>
        </h3>
        
        <div className="section-controls">
          <button
            onClick={onRefresh}
            className="btn btn-sm btn-secondary"
          >
            <RefreshCw size={16} />
            ÿ™ÿ≠ÿØŸäÿ´
          </button>
        </div>
      </div>
      
      {rentals.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">‚è±Ô∏è</div>
          <h3>ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ŸÜÿ¥ÿ∑ÿ©</h3>
          <p>ÿßÿ®ÿØÿ£ ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ ŸÖŸÜ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="rentals-table">
            <thead>
              <tr>
                <th>ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</th>
                <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                <th>ÿßŸÑŸÖÿØÿ©</th>
                <th>ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä</th>
                <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
                <th>ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ</th>
                <th>ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™</th>
              </tr>
            </thead>
            <tbody>
              {rentals.map(rental => {
                const timeInfo = getRentalTimeInfo(rental);
                const remainingMinutes = calculateRemainingMinutes(rental);
                
                return (
                  <tr key={rental.id} className={
                    timeInfo.type === '‚è∞ ÿßŸÜÿ™ŸáŸâ' ? 'expired-rental' : ''
                  }>
                    <td className="rental-number">
                      <strong>#{rental.rental_number || rental.id}</strong>
                      {(rental.is_open_time || rental.rental_type === 'open') && (
                        <span className="badge badge-warning">ŸÖŸÅÿ™Ÿàÿ≠</span>
                      )}
                    </td>
                    <td>
                      <div className="customer-cell">
                        <div className="customer-name">
                          <User size={14} />
                          {rental.customer_name}
                        </div>
                        <div className="customer-phone">
                          <Phone size={12} />
                          {rental.customer_phone}
                        </div>
                      </div>
                    </td>
                    <td className="game-cell">
                      <Gamepad2 size={14} />
                      {rental.game_name}
                    </td>
                    <td>
                      {timeInfo.type}
                    </td>
                    <td>
                      <div className="countdown-cell">
                        <Clock size={14} />
                        <span className={timeInfo.remaining.startsWith('‚è∞') ? 'expired' : ''}>
                          {timeInfo.remaining}
                        </span>
                      </div>
                    </td>
                    <td className="amount-cell">
                      {calculateRentalAmount(rental)}
                    </td>
                    <td>
                      <span className={`payment-status ${rental.payment_status === 'ŸÖÿØŸÅŸàÿπ' ? 'paid' : 'pending'}`}>
                        {rental.payment_status === 'ŸÖÿØŸÅŸàÿπ' 
                          ? <><CheckCircle size={14} /> ŸÖÿØŸÅŸàÿπ</>
                          : <><Clock size={14} /> {rental.payment_status || 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°'}</>
                        }
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          onClick={() => onAction('details', rental)}
                          className="action-btn view-btn"
                          title="ÿπÿ±ÿ∂ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ"
                        >
                          <Eye size={16} />
                        </button>
                        
                        {(rental.is_open_time || rental.rental_type === 'open') && (
                          <button
                            onClick={() => onAction('end', rental)}
                            className="action-btn end-btn"
                            title="ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™"
                          >
                            <Clock size={16} />
                          </button>
                        )}
                        
                        {!(rental.is_open_time || rental.rental_type === 'open') && 
                         remainingMinutes !== null && remainingMinutes < 30 && (
                          <button
                            onClick={() => onAction('extend', rental)}
                            className="action-btn extend-btn"
                            title="ÿ™ŸÖÿØŸäÿØ ÿßŸÑŸàŸÇÿ™"
                          >
                            <Plus size={16} />
                          </button>
                        )}
                        
                        <button
                          onClick={() => onAction('cancel', rental)}
                          className="action-btn cancel-btn"
                          title="ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±"
                        >
                          <XCircle size={16} />
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

// ==================== ŸÖŸÉŸàŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ====================
const CompletedRentalsTable = ({ rentals, onExport, onPrint }) => {
  const [selectedPeriod, setSelectedPeriod] = useState('today');
  const [filteredRentals, setFilteredRentals] = useState([]);

  useEffect(() => {
    const now = new Date();
    let filtered = [...rentals];

    switch (selectedPeriod) {
      case 'today': {
        const today = now.toISOString().split('T')[0];
        filtered = rentals.filter(r => r.end_time && r.end_time.split('T')[0] === today);
        break;
      }
      case 'week': {
        const weekAgo = new Date(now.setDate(now.getDate() - 7));
        filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= weekAgo);
        break;
      }
      case 'month': {
        const monthAgo = new Date(now.setMonth(now.getMonth() - 1));
        filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= monthAgo);
        break;
      }
      default:
        break;
    }

    setFilteredRentals(filtered);
  }, [rentals, selectedPeriod]);

