const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  // ÿßŸÑÿ≠ÿßŸÑÿßÿ™ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©
  const [loading, setLoading] = useState(true);
   const [error, setError] = useState(null); 
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  
  // ÿßŸÑÿ≠ÿßŸÑÿßÿ™ ŸÑŸÑŸàÿßÿ¨Ÿáÿ©
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  
  // ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ==================== ÿØŸàÿßŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ====================
  const loadGames = useCallback(async () => {
    try {
      const response = await api.get('/games', {
        params: { 
          branch_id: user.branch_id,
          is_active: 1
        }
      });
      
      if (response.success && response.data) {
        setGames(response.data);
        
        const availableGames = response.data.filter(g => 
          g.status === 'ŸÖÿ™ÿßÿ≠' || g.status === 'available'
        ).length;
        
        setStats(prev => ({
          ...prev,
          availableGames: availableGames,
          totalGames: response.data.length
        }));
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
    }
  }, [user.branch_id]);

  const loadActiveRentals = useCallback(async () => {
    try {
      const response = await api.getActiveRentals(user.branch_id);
      
      if (response.success && response.data) {
        setActiveRentals(response.data);
        setStats(prev => ({
          ...prev,
          activeRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error);
    }
  }, [user.branch_id]);

  const loadCompletedRentals = useCallback(async () => {
    if (!isManager) return;
    
    try {
      const today = new Date().toISOString().split('T')[0];
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ',
          start_date: today,
          limit: 50
        }
      });
      
      if (response.success && response.data) {
        setCompletedRentals(response.data);
        
        const todayRentals = response.data.filter(r => 
          r.end_time && r.end_time.split('T')[0] === today
        );
        
        const todayRevenue = todayRentals.reduce((sum, rental) => 
          sum + (parseFloat(rental.total_amount) || 0), 0
        );
        
        setStats(prev => ({
          ...prev,
          todayRevenue: todayRevenue,
          todayRentals: todayRentals.length
        }));
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
    }
  }, [user.branch_id, isManager]);

  const loadCurrentShift = useCallback(async () => {
    try {
      const response = await api.getActiveShiftSafe();
      
      if (response.success && response.data) {
        setCurrentShift(response.data);
        localStorage.setItem('current_shift', JSON.stringify(response.data));
      } else {
        setCurrentShift(null);
      }
    } catch (error) {
      console.error('ŸÅÿ¥ŸÑ ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      setCurrentShift(null);
    }
  }, []);

  const startShift = async () => {
    try {
      const response = await api.startShiftSafe();
      
      if (response.success) {
        setCurrentShift(response.data);
        localStorage.setItem('current_shift', JSON.stringify(response.data));
        
        setTimeout(() => {
          loadAllData();
        }, 1000);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    }
  };

  const endShift = async () => {
    if (!currentShift) return;
    
    if (!window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿü')) return;
    
    try {
      if (currentShift.id) {
        await api.endShift(currentShift.id, {
          end_time: new Date().toISOString(),
          status: 'ŸÖŸÜÿ™ŸáŸä'
        });
      }
      
      setCurrentShift(null);
      setActiveRentals([]);
      localStorage.removeItem('current_shift');
      
      await loadAllData();
      
    } catch (error) {
      console.error('Error ending shift:', error);
      setCurrentShift(null);
      setActiveRentals([]);
      localStorage.removeItem('current_shift');
    }
  };

const loadAllData = useCallback(async (forceRefresh = false) => {
  // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÇŸäÿØ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑÿå ŸÑÿß ÿ™ÿ≠ŸÖŸÑŸáÿß ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ
  if (loading && !forceRefresh) return;
  
  setLoading(true);
  setError(null);
  
  try {
    console.log('üîÑ ÿ®ÿØÿ° ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
    
    // ŸÅÿ≠ÿµ ŸÖÿ™ÿ∫Ÿäÿ±ÿßÿ™ ÿßŸÑÿ™ÿ≠ŸÉŸÖ
    const currentTimestamp = Date.now();
    const lastLoadTime = localStorage.getItem('last_load_time') || 0;
    const timeDiff = currentTimestamp - lastLoadTime;
    
    // ÿ•ÿ∞ÿß ŸÖÿ± ÿ£ŸÇŸÑ ŸÖŸÜ 5 ÿ´ŸàÿßŸÜŸä ŸÖŸÜÿ∞ ÿ¢ÿÆÿ± ÿ™ÿ≠ŸÖŸäŸÑÿå ÿ™ÿÆÿ∑Ÿä
    if (timeDiff < 5000 && !forceRefresh) {
      console.log('‚è≠Ô∏è ÿ™ÿÆÿ∑Ÿä ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ - ŸÖÿ± ŸàŸÇÿ™ ŸÇÿµŸäÿ± ÿ¨ÿØÿßŸã');
      setLoading(false);
      return;
    }
    
    // ÿ™ÿ≠ÿØŸäÿ´ ŸàŸÇÿ™ ÿ¢ÿÆÿ± ÿ™ÿ≠ŸÖŸäŸÑ
    localStorage.setItem('last_load_time', currentTimestamp);
    
    if (!user?.branch_id) {
      console.error('‚ùå ŸÑÿß ŸäŸàÿ¨ÿØ ŸÅÿ±ÿπ ŸÖÿ≠ÿØÿØ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ');
      setError('Ÿäÿ¨ÿ® ÿ™ÿπŸäŸäŸÜ ŸÅÿ±ÿπ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ£ŸàŸÑÿßŸã');
      setLoading(false);
      return;
    }

    // ÿ¨ŸÑÿ® ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿ¥ŸÉŸÑ ŸÖÿ™Ÿàÿßÿ≤Ÿä ŸÑŸÉŸÜ ÿ®ÿ™ÿ≠ŸÉŸÖ
    const promises = [
      api.getGames({ 
        branch_id: user.branch_id, 
        is_active: 1,
        status: 'ŸÖÿ™ÿßÿ≠',
        cache: true // ÿ•ÿ∂ÿßŸÅÿ© ŸÉÿßÿ¥ ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™
      }).catch(e => {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ£ŸÑÿπÿßÿ®:', e);
        return { success: false, data: [] };
      }),
      
      api.getActiveRentals(user.branch_id).catch(e => {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', e);
        return { success: false, data: [] };
      }),
      
      api.getActiveShiftSafe().catch(e => {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™:', e);
        return { success: false, data: null };
      }),
      
      api.getSimpleStats().catch(e => {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™:', e);
        return { success: false, data: {} };
      })
    ];
    
    const [
      gamesResponse,
      activeResponse,
      shiftResponse,
      statsResponse
    ] = await Promise.all(promises);
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ≠ÿßŸÑÿßÿ™
    if (gamesResponse.success) {
      setGames(gamesResponse.data || []);
    }
    
    if (activeResponse.success) {
      const activeRentalsData = activeResponse.data || [];
      setActiveRentals(activeRentalsData);
      setStats(prev => ({ ...prev, activeRentals: activeRentalsData.length }));
    }
    
    if (shiftResponse.success && shiftResponse.data) {
      const shiftData = shiftResponse.data;
      setCurrentShift(shiftData);
      localStorage.setItem('current_shift', JSON.stringify(shiftData));
    } else if (shiftResponse.data === null) {
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
    }
    
    if (statsResponse.success) {
      setStats(prev => ({ ...prev, ...statsResponse.data }));
    }
    
    // ÿ™ÿ≠ÿØŸäÿ´ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®
    setStats(prev => ({
      ...prev,
      availableGames: games.filter(g => 
        g.status === 'ŸÖÿ™ÿßÿ≠' || g.status === 'available'
      ).length,
      totalGames: games.length
    }));
    
    console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
    
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ÿπÿßŸÖ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
    setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
  } finally {
    setLoading(false);
  }
}, [user?.branch_id, isManager]);


  // ==================== ÿØŸàÿßŸÑ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ====================
  const handleSelectGame = (game) => {
    if (!currentShift) {
      alert('Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
      return;
    }
    
    const isAlreadySelected = selectedGames.some(g => g.id === game.id);
    
    if (!isAlreadySelected) {
      setSelectedGames([...selectedGames, game]);
