          shift_id: currentShift.id,
          employee_id: user.id,
          employee_name: user.name,
          rental_type: item.rentalType,
          duration_minutes: item.rentalType === 'fixed' ? item.duration : 0,
          total_amount: item.rentalType === 'fixed' ? 
            (item.pricePer15min * Math.ceil(item.duration / 15) * item.quantity) : 0,
          payment_method: paymentMethod,
          payment_status: paymentStatus,
          notes: `${notes || ''} ${item.notes || ''}`.trim(),
          quantity: item.quantity
        };
        
        const response = await api.createRental(rentalPayload);
        
        if (response.success) {
          createdRentals.push(response.data);
          successCount++;
          console.log(`‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${index + 1} ÿ®ŸÜÿ¨ÿßÿ≠`);
        } else {
          console.error(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${index + 1}:`, response.message);
        }
        
      } catch (itemError) {
        console.error(`üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${index + 1}:`, itemError);
      }
      
      // ÿ™ÿ£ÿÆŸäÿ± ÿ®ÿ≥Ÿäÿ∑ ÿ®ŸäŸÜ ŸÉŸÑ ÿ∑ŸÑÿ® ŸÑÿ™ÿ¨ŸÜÿ® ÿ∂ÿ∫ÿ∑ ÿßŸÑÿÆÿßÿØŸÖ
      if (index < rentalItems.length - 1) {
        await new Promise(resolve => setTimeout(resolve, 100));
      }
    }
    
    if (successCount > 0) {
      alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÜÿ¥ÿßÿ° ${successCount} ŸÖŸÜ ${rentalItems.length} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠`);
      setShowRentalModal(false);
      setSelectedGames([]);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ®ÿπÿØ ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
      await loadAllData();
    } else {
      alert('‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜÿ¥ÿßÿ° ÿ£Ÿä ÿ™ÿ£ÿ¨Ÿäÿ±. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.');
    }
    
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ÿπÿßŸÖ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error);
    alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.');
  }
};

  const handleRentalAction = async (action, rental) => {
    switch(action) {
      case 'details':
        alert(`ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}\nÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}\nÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}`);
        break;
        
      case 'end':
        try {
          const response = await api.completeRentalSimple(rental.id, {
            payment_method: rental.payment_method || 'ŸÉÿßÿ¥'
          });
          
          if (response.success) {
            alert('ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
            await loadAllData();
          }
        } catch (error) {
          console.error('Error ending rental:', error);
          alert('ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
        }
        break;
          
      case 'cancel':
        if (window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ${rental.rental_number}ÿü`)) {
          try {
            const response = await api.cancelRental(rental.id, rental.total_amount || 0, 'ÿ•ŸÑÿ∫ÿßÿ° ŸÖŸÜ ŸÇÿ®ŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅ');
            
            if (response.success) {
              alert('ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
              await loadAllData();
            }
          } catch (error) {
            console.error('Error cancelling rental:', error);
            alert('ŸÅÿ¥ŸÑ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
          }
        }
        break;
    }
  };

  const handleExportCompleted = (rentals, period) => {
    const dataStr = JSON.stringify(rentals, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    
    const exportFileDefaultName = `ÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™_${period}_${new Date().toISOString().split('T')[0]}.json`;
    
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
    
    alert(`ÿ™ŸÖ ÿ™ÿµÿØŸäÿ± ${rentals.length} ÿ™ÿ£ÿ¨Ÿäÿ±`);
  };

  const handlePrintCompleted = (rentals, period) => {
    const printWindow = window.open('', '_blank');
    const content = `
      <html>
        <head>
          <title>ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ - ${period}</title>
          <style>
            body { font-family: 'Arial', sans-serif; direction: rtl; padding: 20px; }
            h1 { text-align: center; color: #333; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: center; }
            th { background-color: #f2f2f2; }
            .total { font-weight: bold; color: #2196F3; }
            @media print {
              body { padding: 0; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          <h1>ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©</h1>
          <p><strong>ÿßŸÑŸÅÿ™ÿ±ÿ©:</strong> ${period}</p>
          <p><strong>ÿπÿØÿØ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:</strong> ${rentals.length}</p>
          <p><strong>ÿßŸÑÿ™ÿßÿ±ŸäÿÆ:</strong> ${new Date().toLocaleDateString('ar-EG')}</p>
          
          <table>
            <thead>
              <tr>
                <th>ÿ±ŸÇŸÖ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±</th>
                <th>ÿßŸÑÿπŸÖŸäŸÑ</th>
                <th>ÿßŸÑŸÑÿπÿ®ÿ©</th>
                <th>ŸàŸÇÿ™ ÿßŸÑÿ®ÿØÿ°</th>
                <th>ŸàŸÇÿ™ ÿßŸÑÿßŸÜÿ™Ÿáÿßÿ°</th>
                <th>ÿßŸÑŸÖÿ®ŸÑÿ∫</th>
              </tr>
            </thead>
            <tbody>
              ${rentals.map(rental => `
                <tr>
                  <td>${rental.rental_number || rental.id}</td>
                  <td>${rental.customer_name}</td>
                  <td>${rental.game_name}</td>
                  <td>${formatTime(rental.start_time)}</td>
                  <td>${formatTime(rental.end_time)}</td>
                  <td>${formatCurrency(rental.total_amount)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          
          <div style="margin-top: 20px; padding: 10px; background: #f9f9f9;">
            <p class="total">ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä: ${formatCurrency(rentals.reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0))}</p>
          </div>
          
          <button class="no-print" onclick="window.print()" style="margin-top: 20px; padding: 10px 20px; background: #2196F3; color: white; border: none; cursor: pointer;">
            ÿ∑ÿ®ÿßÿπÿ© ÿßŸÑÿ™ŸÇÿ±Ÿäÿ±
          </button>
        </body>
      </html>
    `;
    
    printWindow.document.write(content);
    printWindow.document.close();
  };

  // ==================== useEffect ====================
// ==================== useEffect ====================
useEffect(() => {
  let isMounted = true;
  let intervalId = null;

  const loadData = async () => {
    if (isMounted) {
      await loadAllData();
    }
  };

  // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸàŸÑŸäÿ©
  loadData();

  // ÿ•ÿπÿØÿßÿØ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ŸÑŸÇÿßÿ¶Ÿä ŸÅŸÇÿ∑ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑
  if (currentShift && isMounted) {
    intervalId = setInterval(async () => {
      try {
        // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ŸÅŸÇÿ∑ (ÿ®ÿØŸàŸÜ ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ŸÉŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™)
        const response = await api.getActiveRentals(user.branch_id);
        if (response.success && isMounted) {
          setActiveRentals(response.data || []);
        }
      } catch (error) {
        console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error);
      }
    }, 30000); // ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
  }

  return () => {
    isMounted = false;
    if (intervalId) {
      clearInterval(intervalId);
    }
  };
}, [currentShift]); 

  // ==================== ÿßŸÑÿ™ÿµŸäŸäÿ± ====================
  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÜÿ∏ÿßŸÖ...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* ÿßŸÑÿ±ÿ£ÿ≥ */}
      <header className="page-header">
        <div className="header-content">
          <div className="header-title">
            <h1 className="page-title">
              <span className="title-icon">üéÆ</span>
              ŸÜÿ∏ÿßŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
            </h1>
            <div className="page-subtitle">
              {user.branch_name}
            </div>
          </div>
          <div className="user-info">
            <div className="user-badge">
              <User size={18} />
              <div className="user-details">
                <span className="user-name">{user?.name}</span>
                <span className="user-role">
                  {isManager ? 'üëë ŸÖÿØŸäÿ±' : 'üë∑ ŸÖŸàÿ∏ŸÅ'}
                </span>
              </div>
            </div>
            <div className="branch-info">
              <Building size={16} />
              <span className="branch-name">{user?.branch_name}</span>
            </div>
          </div>
        </div>
        
        <div className="header-actions">
          {selectedGames.length > 0 && (
            <button 
              onClick={() => setShowRentalModal(true)}
              className="btn btn-primary"
            >
              <Zap size={18} />
              ÿ®ÿØÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ({selectedGames.length})
            </button>
          )}
          
          <button 
            onClick={loadAllData}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          </button>
          
          {currentShift && (
            <div className="shift-indicator">
              <Clock size={16} />
              <span>ÿ¥ŸäŸÅÿ™ #{currentShift.id}</span>
              <span className="shift-time">
                {formatTime(currentShift.start_time)}
              </span>
            </div>
          )}
        </div>
      </header>
      
      {/* ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ */}
      <DashboardStats 
        stats={stats}
        currentShift={currentShift}
        onStartShift={startShift}
        onEndShift={endShift}
      />
      
      {/* ÿßŸÑÿ±ÿ≥ÿßŸÑÿ© ÿπŸÜÿØ ÿπÿØŸÖ Ÿàÿ¨ŸàÿØ ÿ¥ŸäŸÅÿ™ */}
      {!currentShift && (
        <div className="no-shift-alert">
          <div className="alert-content">
            <div className="alert-icon">
              <AlertCircle size={24} />
            </div>
            <div className="alert-text">
              <h3>ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</h3>
              <p>Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ ŸÑŸÑÿ™ŸÖŸÉŸÜ ŸÖŸÜ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™</p>
            </div>
            <button onClick={startShift} className="btn btn-success">
              <Zap size={18} />
              ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
            </button>
          </div>
        </div>
      )}
      
      {/* ÿßŸÑŸÖÿ≠ÿ™ŸàŸâ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä */}
      {currentShift && (
        <div className="main-content">
          {/* ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
          <GamesGrid 
            games={games}
            branchId={user.branch_id}
            onSelectGame={handleSelectGame}
          />
          
          {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© */}
          <ActiveRentalsTable 
            rentals={activeRentals}
            onAction={handleRentalAction}
            onRefresh={loadActiveRentals}
          />
          
          {/* ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© (ŸÑŸÑŸÖÿØŸäÿ± ŸÅŸÇÿ∑) */}
          {isManager && (
            <CompletedRentalsTable 
              rentals={completedRentals}
              onExport={handleExportCompleted}
              onPrint={handlePrintCompleted}
            />
          )}
        </div>
      )}
      
      {/* ŸÜÿßŸÅÿ∞ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ™ÿπÿØÿØ */}
      <MultiRentalModal 
        show={showRentalModal}
        onClose={() => {
          setShowRentalModal(false);
          if (selectedGames.length === 0) {
            setSelectedGames([]);
          }
        }}
        selectedGames={selectedGames}
        currentShift={currentShift}
        onConfirmRental={handleCreateRental}
      />
    </div>
  );
};

export default Rentals;
