// pages/admin/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import authService from '../../services/authService';
import analyticsService from '../../services/analyticsService';
import api from '../../services/api';
import './AdminPages.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    totalGames: 0,
    totalCustomers: 0,
    branchRevenues: [],
    completedRentalsByBranch: []
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [topCustomers, setTopCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentTime, setCurrentTime] = useState(new Date());
  const [error, setError] = useState('');
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [users, setUsers] = useState([]);
  const [timeAgo, setTimeAgo] = useState('Ø§Ù„Ø¢Ù†');
  const [retryCount, setRetryCount] = useState(0);

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const loadDashboardData = useCallback(async () => {
    try {
      setLoading(true);
      setError('');
      
      const currentUser = authService.getCurrentUser();
      setUser(currentUser);
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø´ÙƒÙ„ Ù…ØªÙˆØ§Ø²ÙŠ
      const [
        branchesData,
        statistics,
        topCustomersData,
        recentRentalsData
      ] = await Promise.allSettled([
        loadRealBranchesData(),
        loadRealStatistics(),
        loadRealTopCustomers(),
        loadRecentRentals()
      ]);

      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
      const updatedStats = {
        totalRevenue: 0,
        totalRentals: 0,
        todayRevenue: 0,
        todayRentals: 0,
        totalUsers: 0,
        totalBranches: 0,
        totalGames: 0,
        totalCustomers: 0,
        branchRevenues: [],
        completedRentalsByBranch: []
      };

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±ÙˆØ¹
      if (branchesData.status === 'fulfilled') {
        const { branchRevenues, completedRentalsByBranch } = branchesData.value;
        updatedStats.branchRevenues = branchRevenues;
        updatedStats.completedRentalsByBranch = completedRentalsByBranch;
        updatedStats.totalBranches = branchRevenues.length;
      }

      // Ù…Ø¹Ø§Ù„Ø¬Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
      if (statistics.status === 'fulfilled') {
        Object.assign(updatedStats, statistics.value);
      }

      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø­Ø§Ù„Ø©
      setStats(updatedStats);

      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¹Ù…Ù„Ø§Ø¡
      if (topCustomersData.status === 'fulfilled') {
        setTopCustomers(topCustomersData.value.slice(0, 5)); // Ø¹Ø±Ø¶ Ø£ÙØ¶Ù„ 5 Ø¹Ù…Ù„Ø§Ø¡ ÙÙ‚Ø·
      }

      // ØªØ­Ø¯ÙŠØ« Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ø£Ø®ÙŠØ±Ø©
      if (recentRentalsData.status === 'fulfilled') {
        setRecentRentals(recentRentalsData.value.slice(0, 10));
      }

      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„Ù…Ø³ØªØ®Ø¯Ù…ÙŠÙ†
      await Promise.allSettled([
        loadRealGames(),
        loadRealUsers()
      ]);

      setRetryCount(0); // Ø¥Ø¹Ø§Ø¯Ø© ØªØ¹ÙŠÙŠÙ† Ø¹Ø¯Ø§Ø¯ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø§Øª

    } catch (error) {
      console.error('Error loading dashboard data:', error);
      
      if (retryCount < 3) {
        setRetryCount(prev => prev + 1);
        setTimeout(() => loadDashboardData(), 3000); // Ø¥Ø¹Ø§Ø¯Ø© Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ø¨Ø¹Ø¯ 3 Ø«ÙˆØ§Ù†ÙŠ
      } else {
        setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª. ÙŠØ±Ø¬Ù‰ Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§ØªØµØ§Ù„ Ø§Ù„Ø´Ø¨ÙƒØ© ÙˆØ¥Ø¹Ø§Ø¯Ø© Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø©.');
      }
    } finally {
      setLoading(false);
    }
  }, [retryCount]);

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹
  const loadRealBranchesData = async () => {
    try {
      const today = new Date().toISOString().split('T')[0];
      const branchRevenues = [];
      const completedRentalsByBranch = [];
      
      const branchesResponse = await api.getBranches();
      if (branchesResponse.success) {
        const branchesData = branchesResponse.data || [];
        setBranches(branchesData);
        
        for (const branch of branchesData) {
          try {
            // Ø§Ø³ØªØ®Ø¯Ø§Ù… Ù…Ø­Ø§ÙˆÙ„Ø§Øª Ù…ØªØ¹Ø¯Ø¯Ø© Ù„ÙƒÙ„ ÙØ±Ø¹
