          payment_status: paymentStatus, // ‚úÖ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ©
          total_amount: totalAmount,
          is_open_time: item.rentalType === 'open' ? 1 : 0,
          paid_amount: paidAmount, // ‚úÖ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ
          final_amount: totalAmount,
          // ‚úÖ ÿßŸÑÿ™ÿ£ŸÉÿØ ŸÖŸÜ Ÿàÿ∂ÿπ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ÿßŸÑŸÜŸáÿßÿ¶Ÿä ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ´ÿßÿ®ÿ™
          expected_end_time: item.rentalType === 'fixed' ? 
            new Date(Date.now() + (item.duration * 60 * 1000)).toISOString() : 
            null
        };
        
        console.log(`üì§ ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑŸÄ ${item.gameName}:`, {
          ...rentalRequest,
          payment_status: paymentStatus,
          paid_amount: paidAmount
        });
        
        const response = await api.createRental(rentalRequest);
        
        if (response && response.success) {
          results.push({
            game: item.gameName,
            success: true,
            rental_number: response.data?.rental_number || rentalNumber,
            payment_status: paymentStatus
          });
          
          console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ${item.gameName} - ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ: ${paymentStatus}`);
