};


// ÿØÿßŸÑÿ© ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
const checkAndCompleteRentals = useCallback(async () => {
  if (!currentShift || !user?.branch_id) return;
  
  try {
    console.log('üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã...');
    
    const response = await api.post('/api/rentals/process-completed');
    
    if (response.success && response.data.processed_count > 0) {
      console.log(`‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ${response.data.processed_count} ÿ™ÿ£ÿ¨Ÿäÿ± ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã`);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÇŸàÿßÿ¶ŸÖ
      loadActiveRentals();
      loadCompletedRentals();
      
      // ÿ•ÿ¥ÿπÿßÿ± ÿ®ÿµÿ±Ÿä (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)
