}, [currentShift, user?.branch_id]);

// ÿßÿ≥ÿ™ÿØÿπÿßÿ° ÿßŸÑÿØÿßŸÑÿ© ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÉŸàŸÜ Ÿàÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
useEffect(() => {
  if (currentShift) {
    checkAndCompleteRentals();
    
    // ÿ∂ÿ®ÿ∑ ŸÖÿ§ŸÇÿ™ ŸÑŸÑÿ™ÿ≠ŸÇŸÇ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ©
    const interval = setInterval(checkAndCompleteRentals, 30000);
    return () => clearInterval(interval);
  }
}, [currentShift, checkAndCompleteRentals]);

  const calculateItemPrice = useCallback((item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return (item.pricePer15min * units * item.quantity) || 0;
  }, []);

const handleEndRental = async (rental) => {
  try {
    if (!rental || !rental.id) {
      alert('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©');
      return;
    }
    
    const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
    
    if (isOpenTime) {
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const minutes = Math.floor((now - startTime) / 60000);
      const units = Math.ceil(minutes / 15);
      const pricePer15Min = rental.price_per_15min || 100;
      const estimatedAmount = units * pricePer15Min;
      
      if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠ÿü\n\nÿßŸÑŸÖÿØÿ©: ${minutes} ÿØŸÇŸäŸÇÿ©\nÿßŸÑÿ≥ÿπÿ± ÿßŸÑÿ™ŸÇÿØŸäÿ±Ÿä: ${formatCurrency(estimatedAmount)}`)) {
        return;
      }
      
      const response = await api.completeRental(rental.id, {
        payment_method: 'ŸÉÿßÿ¥'
      });
      
      if (response.success) {
        alert('‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
        loadActiveRentals();
        // ‚ùå ÿ•ÿ≤ÿßŸÑÿ©: ŸÑÿß ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        // loadGames();
        loadCompletedRentals();
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
      }
    } else {
      alert('ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ ŸäÿØŸàŸäÿßŸã');
    }
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
    alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
  }
};

  // ==================== ÿØŸàÿßŸÑ ÿ¨ÿØŸäÿØÿ© ====================

  // 1. ÿØÿßŸÑÿ© ÿ•ŸÜŸáÿßÿ° ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ®ŸÉÿ± (ŸÅŸä ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ ŸÅŸÇÿ∑)
  const handleEarlyEndRental = async (rental) => {
    try {
      if (!rental || !rental.id) {
        alert('ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©');
        return;
      }
      
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ£ŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™
      const isFixedTime = !rental.is_open_time && rental.rental_type === 'fixed';
      if (!isFixedTime) {
        alert('Ÿáÿ∞ÿß ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ° ÿÆÿßÿµ ÿ®ÿßŸÑÿ£ŸàŸÇÿßÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™ÿ© ŸÅŸÇÿ∑');
        return;
      }
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedMinutes = Math.floor((now - startTime) / 60000);
      
      // ÿßŸÑÿ≥ŸÖÿßÿ≠ ŸÅŸÇÿ∑ ŸÅŸä ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ
      if (elapsedMinutes > 3) {
        alert('‚ùå ŸÑÿß ŸäŸÖŸÉŸÜ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ÿπÿØ ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ\nÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä: ' + elapsedMinutes + ' ÿØŸÇŸäŸÇÿ©');
        return;
      }
      
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ±ÿØ (ŸÜÿ≥ÿ®ÿ© ŸÖŸÜ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÉŸÑŸä)
      const totalMinutes = rental.duration_minutes || 15;
      const pricePer15Min = rental.price_per_15min || 100;
      const totalUnits = Math.ceil(totalMinutes / 15);
      const totalAmount = totalUnits * pricePer15Min;
      
      // ÿ≠ÿ≥ÿßÿ® ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ±ÿØ (ŸÜÿ≥ÿ®ÿ© ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä)
      const remainingMinutes = totalMinutes - elapsedMinutes;
      const remainingUnits = Math.ceil(remainingMinutes / 15);
      const refundAmount = remainingUnits * pricePer15Min;
      
      // ÿπÿ±ÿ∂ ÿ™ÿ£ŸÉŸäÿØ ŸÖÿπ ÿßŸÑÿ™ŸÅÿßÿµŸäŸÑ
      const confirmMessage = `
      ÿ™ÿ£ŸÉŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ®ŸÉÿ±:
      
      ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:
      - ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}
      - ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}
      - ÿßŸÑŸÖÿØÿ© ÿßŸÑÿ£ÿµŸÑŸäÿ©: ${totalMinutes} ÿØŸÇŸäŸÇÿ©
      - ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜŸÇÿ∂Ÿä: ${elapsedMinutes} ÿØŸÇŸäŸÇÿ©
      - ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä: ${remainingMinutes} ÿØŸÇŸäŸÇÿ©
      
      ÿßŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÖÿßŸÑŸä:
      - ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ: ${formatCurrency(totalAmount)}
      - ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ±ÿØ: ${formatCurrency(refundAmount)}
      
      ŸÖŸÑÿßÿ≠ÿ∏ÿ©: ŸäŸÖŸÉŸÜ ÿßŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ± ŸÅŸÇÿ∑ ŸÅŸä ÿ£ŸàŸÑ 3 ÿØŸÇÿßÿ¶ŸÇ
      `;
      
      if (!window.confirm(confirmMessage)) {
        return;
      }
      
      // ÿ•ÿ±ÿ≥ÿßŸÑ ÿ∑ŸÑÿ® ÿßŸÑÿ•ŸÜŸáÿßÿ°
      const response = await api.completeRental(rental.id, {
        payment_method: rental.payment_method || 'ŸÉÿßÿ¥',
        early_end: true,
        refund_amount: refundAmount,
        notes: `ÿ™ŸÖ ÿßŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑŸÖÿ®ŸÉÿ± ÿ®ÿπÿØ ${elapsedMinutes} ÿØŸÇŸäŸÇÿ© - ÿ•ÿ±ÿ¨ÿßÿπ ${refundAmount} ÿ¨.ŸÖ`
      });
      
      if (response.success) {
        alert(`‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ®ŸÉÿ±ÿßŸã ÿ®ŸÜÿ¨ÿßÿ≠\nÿ™ŸÖ ÿ•ÿ±ÿ¨ÿßÿπ ${formatCurrency(refundAmount)} ŸÑŸÑÿπŸÖŸäŸÑ`);
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±: ${response.message}`);
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ®ŸÉÿ±:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑŸÖÿ®ŸÉÿ±');
    }
  };

  // 2. ÿØÿßŸÑÿ© ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ŸÖÿπ ÿ≠ÿ≥ÿßÿ® ŸÅÿ±ŸÇ ÿßŸÑÿ≥ÿπÿ±
  const handleChangeGame = (rental) => {
    setSelectedRentalForChange(rental);
    setShowChangeGameModal(true);
  };

  const confirmChangeGame = async (newGame) => {
    try {
      const rental = selectedRentalForChange;
      if (!rental || !newGame) return;

      const oldPrice = rental.price_per_15min || 100;
      const newPrice = newGame.price_per_15min || 100;
      const priceDifference = newPrice - oldPrice;

      let priceMessage = '';
      if (priceDifference > 0) {
        priceMessage = `ÿ≤ŸäÿßÿØÿ© ŸÅŸä ÿßŸÑÿ≥ÿπÿ±: +${formatCurrency(priceDifference)} ŸÑŸÉŸÑ 15 ÿØŸÇŸäŸÇÿ©`;
      } else if (priceDifference < 0) {
        priceMessage = `ÿÆÿµŸÖ ŸÅŸä ÿßŸÑÿ≥ÿπÿ±: ${formatCurrency(priceDifference)} ŸÑŸÉŸÑ 15 ÿØŸÇŸäŸÇÿ©`;
      } else {
        priceMessage = 'ŸÑÿß ŸäŸàÿ¨ÿØ ŸÅÿ±ŸÇ ŸÅŸä ÿßŸÑÿ≥ÿπÿ±';
      }

      const response = await api.updateRental(rental.id, {
        game_id: newGame.id,
        game_name: newGame.name,
        price_per_15min: newPrice,
        notes: `${rental.notes || ''}\nÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ŸÖŸÜ ${rental.game_name} ÿ•ŸÑŸâ ${newGame.name} - ${priceMessage}`
      });

      if (response.success) {
        alert('‚úÖ ÿ™ŸÖ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ© ÿ®ŸÜÿ¨ÿßÿ≠');
        setShowChangeGameModal(false);
        setSelectedRentalForChange(null);
        loadAllData();
      } else {
        alert(`‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©: ${response.message}`);
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©:', error);
      alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÑÿπÿ®ÿ©');
    }
  };

  // 3. ÿØÿßŸÑÿ© ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜ ÿßŸÑÿ¨ÿØŸàŸÑ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑ
  const handleRerental = (completedRental) => {
    setSelectedCompletedRental(completedRental);
    setShowRerentalModal(true);
  };

  const confirmRerental = async (newGame, rentalType, duration) => {
    try {
      const rental = selectedCompletedRental;
      if (!rental) return;

      // ÿ™ÿ≠ÿØŸäÿØ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™
      const paymentStatus = rentalType === 'fixed' ? 'ŸÖÿØŸÅŸàÿπ' : 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°';
      const pricePer15Min = newGame.price_per_15min || 100;
      const totalAmount = rentalType === 'fixed' ? (Math.ceil(duration / 15) * pricePer15Min) : 0;
      const paidAmount = rentalType === 'fixed' ? totalAmount : 0;

