  const RentalModal = ({ 
    show, 
    onClose, 
    selectedGames: initialSelectedGames, 
    onConfirmRental, 
    currentShift,
    allGames 
  }) => {
    const [customerInfo, setCustomerInfo] = useState({
      name: '',
      phone: '',
      email: '',
      address: ''
    });
    
    const [rentalItems, setRentalItems] = useState([]);
    const [paymentMethod, setPaymentMethod] = useState('ŸÉÿßÿ¥');
    const [notes, setNotes] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [showAddGameModal, setShowAddGameModal] = useState(false);

    useEffect(() => {
      if (show && initialSelectedGames.length > 0 && rentalItems.length === 0) {
        const items = initialSelectedGames.map(game => ({
          id: Date.now() + Math.random(),
          gameId: game.id,
          gameName: game.name,
          gameImage: '/images/default-game.jpg',
          rentalType: 'fixed',
          duration: 15,
          pricePer15min: game.price_per_15min || 100,
          quantity: 1,
          childName: '',
          notes: ''
        }));
        setRentalItems(items);
      }
    }, [show, initialSelectedGames]);

    const updateRentalItem = (id, updates) => {
      setRentalItems(items => 
        items.map(item => 
          item.id === id ? { ...item, ...updates } : item
        )
      );
    };

    const removeRentalItem = (id) => {
      setRentalItems(items => items.filter(item => item.id !== id));
    };

    const handleAddAnotherGame = () => {
      setShowAddGameModal(true);
    };

    const handleSelectNewGame = (game) => {
      const newItem = {
        id: Date.now() + Math.random(),
        gameId: game.id,
        gameName: game.name,
        gameImage: '/images/default-game.jpg',
        rentalType: 'fixed',
        duration: 15,
        pricePer15min: game.price_per_15min || 100,
        quantity: 1,
        childName: '',
        notes: ''
      };
      
      setRentalItems([...rentalItems, newItem]);
      setShowAddGameModal(false);
    };

    const calculateTotal = useCallback(() => {
      return rentalItems.reduce((total, item) => {
        if (item.rentalType === 'open') return total;
        const units = Math.ceil(item.duration / 15);
        return total + (item.pricePer15min * units * item.quantity) || 0;
      }, 0);
    }, [rentalItems]);

    const handleSubmit = async () => {
      if (!customerInfo.name || !customerInfo.phone) {
        alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ Ÿàÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
        return;
      }
      
      if (!validatePhone(customerInfo.phone)) {
        alert('ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠. Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ 11 ÿ±ŸÇŸÖÿßŸã ŸàŸäÿ®ÿØÿ£ ÿ®ŸÄ 01');
        return;
      }
      
      if (rentalItems.length === 0) {
        alert('Ÿäÿ±ÿ¨Ÿâ ÿ•ÿ∂ÿßŸÅÿ© ÿ£ŸÑÿπÿßÿ® ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
        return;
      }

      setIsSubmitting(true);

      try {
        const rentalData = {
          customerInfo,
          rentalItems,
          paymentMethod,
          notes,
          totalAmount: calculateTotal(),
          shiftId: currentShift?.id
        };

        await onConfirmRental(rentalData);
        
        setCustomerInfo({ name: '', phone: '', email: '', address: '' });
        setRentalItems([]);
        setPaymentMethod('ŸÉÿßÿ¥');
        setNotes('');
        onClose();
        
      } catch (error) {
        console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', error);
        alert('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜÿ¥ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      } finally {
        setIsSubmitting(false);
      }
    };

    if (!show) return null;

    const AddGameModal = () => {
      const [searchTerm, setSearchTerm] = useState('');
      const [filteredGames, setFilteredGames] = useState([]);

      useEffect(() => {
        const availableGames = allGames.filter(game => 
          game.status === 'ŸÖÿ™ÿßÿ≠' && 
          game.branch_id === user?.branch_id
        );
        
        if (searchTerm) {
          const filtered = availableGames.filter(game =>
