  };

  // 5. ŸÜÿßŸÅÿ∞ÿ© ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
  const RerentalModal = ({ show, onClose, rental, games, onConfirm }) => {
    const [selectedGame, setSelectedGame] = useState(null);
    const [rentalType, setRentalType] = useState('fixed');
    const [duration, setDuration] = useState(15);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
      if (rental && games.length > 0) {
        const originalGame = games.find(g => g.id === rental.game_id);
        if (originalGame) {
          setSelectedGame(originalGame);
        }
      }
    }, [rental, games]);

    if (!show || !rental) return null;

    const availableGames = games.filter(game => 
      game.status === 'ŸÖÿ™ÿßÿ≠' && 
      game.branch_id === user?.branch_id
    );

    const filteredGames = searchTerm 
      ? availableGames.filter(game =>
          game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
          game.category?.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : availableGames;

    const getGameImage = (game) => {
      const imageMap = {
        'ÿØÿ±ŸäŸÅÿ™ ŸÉÿßÿ±': '/images/Driftcar.jpg',
        'ŸáÿßŸÅÿ± ÿ®Ÿàÿ±ÿØ': '/images/Hoverboard.jpg',
        'ÿ≥ŸÉŸàÿ™ÿ±': '/images/Scooter.jpg',
        'ÿπÿ±ÿ®ÿ© ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©': '/images/Car.jpg',
        'ŸÖŸàÿ™Ÿàÿ≥ŸäŸÉŸÑ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Motor.jpg',
        'Ÿáÿßÿ±ŸÑŸä': '/images/harley.jpg',
        'ÿ≥Ÿäÿ¨ŸàÿßŸä': '/images/Segway.jpg',
        'ŸÉÿ±ÿßÿ≤Ÿä ŸÉÿßÿ±': '/images/Ninebot.jpg',
        'ÿ≥ŸÉŸäÿ™ ŸÉŸáÿ±ÿ®ÿßÿ¶Ÿä': '/images/Skate.jpg',
        'ÿ™ÿ±ÿßŸÖÿ®ŸàŸÑŸäŸÜ': '/images/Trampoline.jpg',
        'ŸÖÿ≠ÿßŸÉŸä': '/images/Simulator.jpg'
      };
      
      if (game.image_url && game.image_url !== 'default-game.jpg') {
        return game.image_url.startsWith('http') ? game.image_url : `/images/${game.image_url}`;
      }
      
      return imageMap[game.name] || '/images/default-game.jpg';
    };

    const calculateTotal = () => {
      if (!selectedGame || rentalType === 'open') return 0;
      const units = Math.ceil(duration / 15);
      return units * (selectedGame.price_per_15min || 100);
    };

    return (
      <div className="modal-overlay">
