                      <div className="rerental-game-info">
                        <h4>{game.name}</h4>
                        <div className="rerental-game-price">
                          {formatCurrency(game.price_per_15min)} / 15 ÿØŸÇŸäŸÇÿ©
                        </div>
                        {selectedGame?.id === game.id && (
                          <div className="selected-badge">
                            <Check size={16} />
                            ŸÖÿÆÿ™ÿßÿ±ÿ©
                          </div>
