api.testConnection = async () => {
  try {
    const statusResponse = await fetch(`${API_BASE_URL}/status`);
    const statusData = await statusResponse.json();
    
    const healthResponse = await fetch(`${API_BASE_URL}/health-check`);
    const healthData = await healthResponse.json();
    
    return {
      success: true,
      message: '✅ اتصال API يعمل بشكل صحيح',
      status: statusData,
      health: healthData
    };
  } catch (error) {
    return {
      success: false,
      message: '❌ فشل اختبار الاتصال: ' + error.message
    };
  }
};

api.testToken = async () => {
  try {
    const status = api.checkAuthStatus();
    
    if (!status.isAuthenticated) {
      return {
        success: false,
        message: 'لا يوجد توكن مسجل',
        status: status
      };
    }
    
    const response = await api.get('/auth/profile');
    
    return {
      success: true,
      message: '✅ التوكن صالح ويعمل',
      profile: response,
      status: status
    };
  } catch (error) {
    return {
      success: false,
      message: '❌ التوكن غير صالح: ' + error.message,
      status: api.checkAuthStatus()
    };
  }
};

