      notes: 'إنهاء الشيفت وحفظ البيانات'
    });
    
    if (response.success) {
      showNotification('success', `تم إنهاء الشيفت بنجاح. الإيرادات: ${formatCurrency(response.data.stats.total_revenue)}`);
      setCurrentShift(null);
