  // ==================== USE MEMO FOR FILTERED GAMES ====================
  const filteredGames = useMemo(() => {
    console.log('🔄 حساب الألعاب المصفاة...');
    
    if (!games || games.length === 0) {
      console.log('📭 لا توجد ألعاب لتصفيتها');
      return [];
    }
    
    let filtered = games.filter(game => {
      const isAvailable = game.status === 'متاح' && 
        (game.is_active === 1 || game.is_active === true);
      
      if (!isAvailable) {
        return false;
      }
      
      // البحث حسب الاسم
      if (searchTerm && searchTerm.trim() !== '') {
        const gameName = game.name?.toLowerCase() || '';
        const searchTermLower = searchTerm.toLowerCase();
        if (!gameName.includes(searchTermLower)) {
          return false;
        }
      }
      
      // البحث حسب الفئة
      if (selectedCategory && selectedCategory.trim() !== '') {
        const gameCategory = game.category || '';
        if (gameCategory !== selectedCategory) {
          return false;
        }
      }
      
      return true;
    });
    
    console.log(`✅ الألعاب المصفاة: ${filtered.length} من ${games.length}`);
    return filtered;
  }, [games, searchTerm, selectedCategory]);

  const getEgyptTimeNow = () => {
    const now = new Date();
    const egyptTime = new Date(now.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
    
    const hours = egyptTime.getHours();
    const minutes = egyptTime.getMinutes();
    const seconds = egyptTime.getSeconds();
    
    return {
      hours: hours,
      minutes: minutes,
      seconds: seconds,
      formatted: `${hours}:${minutes.toString().padStart(2, '0')}:${seconds.toString().padStart(2, '0')}`
    };
  };

  useEffect(() => {
    const updateTime = () => {
      setCurrentEgyptTime(getEgyptTimeNow().formatted);
    };
    
    updateTime();
    const interval = setInterval(updateTime, 1000);
    
    return () => clearInterval(interval);
  }, []);

  const formatEgyptDateTime = (dateString) => {
    try {
      if (!dateString) return '--/--/---- --:--:--';
      
      const date = new Date(dateString);
      const egyptDate = new Date(date.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
      
      const day = egyptDate.getDate().toString().padStart(2, '0');
      const month = (egyptDate.getMonth() + 1).toString().padStart(2, '0');
      const year = egyptDate.getFullYear();
      const hours = egyptDate.getHours();
      const minutes = egyptDate.getMinutes().toString().padStart(2, '0');
      
      return `${day}/${month}/${year} ${hours}:${minutes}`;
    } catch (error) {
      return '--/--/---- --:--:--';
