import React, { useState, useEffect, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';
import api from '../../services/api';

const GamesManagement = () => {
  const { user } = useAuth();
  const [allGames, setAllGames] = useState([]); // Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù…Ù† Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ±ÙˆØ¹
  const [currentGames, setCurrentGames] = useState([]); // Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…Ø¹Ø±ÙˆØ¶Ø© Ø­Ø§Ù„ÙŠØ§Ù‹
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingBranch, setLoadingBranch] = useState(false);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});
  const [gamesLoaded, setGamesLoaded] = useState(false); // ØªØªØ¨Ø¹ Ø­Ø§Ù„Ø© ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨

  useEffect(() => {
    loadGamesData();
  }, []);

  // âœ… Ø¯Ø§Ù„Ø© Ù…Ø­Ø³Ù†Ø© Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ù…Ø¹ ØªØ­Ø¯ÙŠØ« ØªÙ„Ù‚Ø§Ø¦ÙŠ
  const loadGamesData = async (forceRefresh = false) => {
    try {
      setLoading(true);
      setError('');

      console.log('ğŸ”„ Ø¨Ø¯Ø¡ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ÙØ±ÙˆØ¹...');

      // âœ… Ø¬Ù„Ø¨ Ø§Ù„ÙØ±ÙˆØ¹ Ø£ÙˆÙ„Ø§Ù‹
      const branchesResponse = await authService.getBranches();
      
      if (!branchesResponse.success) {
        throw new Error(branchesResponse.message || 'ÙØ´Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ÙØ±ÙˆØ¹');
      }
      
      const branchesData = branchesResponse.data || [];
      console.log('ğŸ¬ Ø¹Ø¯Ø¯ Ø§Ù„ÙØ±ÙˆØ¹ Ø§Ù„Ù…Ø­Ù…Ù„Ø©:', branchesData.length);
      setBranches(branchesData);
      
      // âœ… ØªÙ‡ÙŠØ¦Ø© Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ù„ÙƒÙ„ ÙØ±Ø¹
      const initialStats = {};
      branchesData.forEach(branch => {
        initialStats[branch.id] = {
          branch_id: branch.id,
          branch_name: branch.name,
          total_games: 0,
          gamesList: [] // ØªØ®Ø²ÙŠÙ† Ù‚Ø§Ø¦Ù…Ø© Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„ÙƒÙ„ ÙØ±Ø¹
        };
      });
      
      setBranchStats(initialStats);
      
      // âœ… Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨
      console.log('ğŸ® Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨...');
      let allGamesData = [];
      
      try {
        // Ù…Ø­Ø§ÙˆÙ„Ø© Ø¬Ù„Ø¨ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù…Ø±Ø© ÙˆØ§Ø­Ø¯Ø©
        const gamesResponse = await api.get('/games', {
          params: { include_branch_info: 'true' }
        });
        
        if (gamesResponse.success) {
          allGamesData = gamesResponse.data || [];
          console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${allGamesData.length} Ù„Ø¹Ø¨Ø© Ù…Ù† Ø¬Ù…ÙŠØ¹ Ø§Ù„ÙØ±ÙˆØ¹`);
          setGamesLoaded(true); // ØªÙ… ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø¨Ù†Ø¬Ø§Ø­
        } else {
          console.warn('âš ï¸ ÙØ´Ù„ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø¯ÙØ¹Ø© ÙˆØ§Ø­Ø¯Ø©:', gamesResponse.message);
          allGamesData = [];
        }
      } catch (gamesError) {
        console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¬Ù„Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', gamesError);
        allGamesData = [];
      }
      
      console.log('ğŸ® Ø¥Ø¬Ù…Ø§Ù„ÙŠ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…Ø­Ù…Ù„Ø©:', allGamesData.length);
      
      // âœ… Ø­Ø³Ø§Ø¨ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠØ©
      const finalStats = { ...initialStats };
      let totalCount = 0;
      
      // Ø­Ø³Ø§Ø¨ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„ÙƒÙ„ ÙØ±Ø¹
      allGamesData.forEach(game => {
        const branchId = game.branch_id;
        if (finalStats[branchId]) {
          finalStats[branchId].total_games++;
          finalStats[branchId].gamesList.push(game);
          totalCount++;
        } else {
          console.warn(`âš ï¸ Ù„Ø¹Ø¨Ø© Ù…Ø±ØªØ¨Ø·Ø© Ø¨ÙØ±Ø¹ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯: ${game.name} - ÙØ±Ø¹ ID: ${branchId}`);
          
          // Ø¥Ù†Ø´Ø§Ø¡ Ø¥Ø¯Ø®Ø§Ù„ Ù„Ù„ÙØ±Ø¹ ØºÙŠØ± Ø§Ù„Ù…ÙˆØ¬ÙˆØ¯
          if (!finalStats[branchId]) {
            finalStats[branchId] = {
              branch_id: branchId,
              branch_name: `ÙØ±Ø¹ ${branchId}`,
              total_games: 0,
              gamesList: []
            };
          }
          
          finalStats[branchId].total_games++;
          finalStats[branchId].gamesList.push(game);
          totalCount++;
        }
      });
      
      // âœ… ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø­Ø§Ù„Ø©
      setAllGames(allGamesData);
      setCurrentGames(allGamesData); // Ø¹Ø±Ø¶ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹ Ø¹Ù†Ø¯ Ø§Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£ÙˆÙ„
      setBranchStats(finalStats);
      
      console.log('ğŸ“Š Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ù†Ù‡Ø§Ø¦ÙŠØ©:', {
        totalBranches: branchesData.length,
        totalGames: totalCount,
        branchStats: Object.keys(finalStats).map(id => ({
          branchId: id,
          branchName: finalStats[id].branch_name,
          totalGames: finalStats[id].total_games
        }))
      });
      
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ Ø´Ø§Ù…Ù„ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setError('ØªØ¹Ø°Ø± ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨. ÙŠØ±Ø¬Ù‰ Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ù…Ø±Ø© Ø£Ø®Ø±Ù‰.');
      setAllGames([]);
      setCurrentGames([]);
      setBranches([]);
      setBranchStats({});
    } finally {
      setLoading(false);
    }
  };

  // âœ… Ø¯Ø§Ù„Ø© Ù…Ø­Ø³Ù†Ø© Ù„Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ ÙØ±Ø¹ Ù…Ø¹ÙŠÙ†
  const loadBranchGamesOnly = useCallback(async (branchId) => {
    try {
      setLoadingBranch(true);
      console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ ${branchId} ÙÙ‚Ø·...`);
      
      const response = await api.get('/games', {
        params: {
          branch_id: branchId,
          include_branch_info: 'true'
        }
      });
      
      if (response.success) {
        const branchGames = response.data || [];
        console.log(`âœ… ØªÙ… Ø¬Ù„Ø¨ ${branchGames.length} Ù„Ø¹Ø¨Ø© Ù„Ù„ÙØ±Ø¹ ${branchId}`);
        
        // âœ… ØªØ­Ø¯ÙŠØ« Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„ÙØ±Ø¹
        setBranchStats(prev => {
          const newStats = { ...prev };
          if (newStats[branchId]) {
            newStats[branchId].total_games = branchGames.length;
            newStats[branchId].gamesList = branchGames;
          }
          return newStats;
        });
        
        setCurrentGames(branchGames);
      } else {
        console.warn('âš ï¸ Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø­Ù„ÙŠØ© ÙƒØ¨Ø¯ÙŠÙ„');
        const branchGames = allGames.filter(game => game.branch_id == branchId);
        setCurrentGames(branchGames);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹:', error);
      // Ø§Ø³ØªØ®Ø¯Ø§Ù… Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù…Ø­Ù„ÙŠØ© ÙƒØ¨Ø¯ÙŠÙ„
      const branchGames = allGames.filter(game => game.branch_id == branchId);
      setCurrentGames(branchGames);
    } finally {
      setLoadingBranch(false);
    }
  }, [allGames]);

  // âœ… Ø¹Ù†Ø¯ Ø§Ø®ØªÙŠØ§Ø± ÙØ±Ø¹ Ù…Ø¹ÙŠÙ† - ÙŠØªÙ… Ø¹Ø±Ø¶ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹
  const handleSelectBranch = useCallback((branchId) => {
    setSelectedBranch(branchId);
    
    if (branchId === 'all') {
      // Ø¹Ø±Ø¶ Ø¬Ù…ÙŠØ¹ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹
