import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  
  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [currentShiftId, setCurrentShiftId] = useState(null);

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // âœ… Ø¥ØµÙ„Ø§Ø­ Ù…Ø´ÙƒÙ„Ø© Ø§Ù„ØªÙƒØ±Ø§Ø± - Ø­ÙØ¸ Ø¢Ø®Ø± Ù…Ø±Ø© ØªÙ… ÙÙŠÙ‡Ø§ Ø§Ù„ØªØ­Ù‚Ù‚
  const lastRentalTypeCheckRef = useRef({});
  
  const refreshAllData = async (force = false) => {
    if (loading.games && loading.rentals && loading.shift && !force) {
      console.log('â³ Ø§Ù„ØªØ­Ø¯ÙŠØ« Ù‚ÙŠØ¯ Ø§Ù„ØªÙ†ÙÙŠØ° Ø¨Ø§Ù„ÙØ¹Ù„...');
      return;
    }
    
    console.log('ğŸ”„ ØªØ­Ø¯ÙŠØ« Ø´Ø§Ù…Ù„ Ù„Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
    
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      await loadGames();
      await loadCurrentShift();
      
      if (currentShift && currentShift.id) {
        await Promise.all([
          loadActiveRentalsForCurrentShift(),
          isManager ? loadCompletedRentals() : Promise.resolve()
        ]);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª:', error);
    } finally {
      setTimeout(() => {
        setLoading({ games: false, rentals: false, shift: false, stats: false });
      }, 300);
    }
  };

  const loadGames = async () => {
    try {
      console.log('ğŸ® ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ÙØ±Ø¹', user?.branch_id);
      
      if (!user?.branch_id) {
        console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯');
        setGames([]);
        return;
      }
      
      const response = await api.loadGamesSmart(user.branch_id);
        
      if (response.success) {
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data.length} Ù„Ø¹Ø¨Ø©`);
        setGames(response.data);
        setAllGames(response.data);
        setStats(prev => ({
          ...prev,
          availableGames: response.data.length,
          totalGames: response.data.length
        }));
      } else {
        console.warn('âš ï¸ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', response.message);
        setGames([]);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setGames([]);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  };

  const loadCurrentShift = async (forceRefresh = false) => {
    try {
      const response = await api.getCurrentShift();
      
      if (response.success && response.data) {
        const shift = response.data;
        
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          localStorage.setItem('current_shift', JSON.stringify(shift));
          return shift;
        } else {
          console.warn('âš ï¸ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ù†Ø´Ø· Ù„Ø§ ÙŠÙ†ØªÙ…ÙŠ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ');
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } else {
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  };

  const loadActiveRentalsForCurrentShift = async () => {
    if (!user?.branch_id || !currentShift?.id) {
      console.log('âš ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ ÙØ±Ø¹ Ù…Ø­Ø¯Ø¯ Ø£Ùˆ Ø´ÙŠÙØª Ù†Ø´Ø·');
      setActiveRentals([]);
      return;
    }
    
    try {
      setLoading(prev => ({ ...prev, rentals: true }));
      
      console.log(`ğŸ”„ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø© Ù„Ù„Ø´ÙŠÙØª #${currentShift.id}`);
      
      const response = await api.get('/rentals/active', {
        params: { branch_id: user.branch_id }
      });
      
      if (response.success && response.data) {
        const shiftRentals = response.data.filter(rental => 
          rental.shift_id === currentShift.id
        );
        
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${shiftRentals.length} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø· Ù„Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ`);
        setActiveRentals(shiftRentals);
      } else {
        console.warn('âš ï¸ ÙØ´Ù„ Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', response.message);
        setActiveRentals([]);
