// frontend/src/pages/admin/GamesManagement.jsx
import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const GamesManagement = () => {
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('ELECTRIC');
  const [newGame, setNewGame] = useState({
    name: '',
    category: 'ELECTRIC',
    description: '',
    image_url: '',
    minimum_age: 12,
    max_weight: 100,
    safety_instructions: ''
  });

  // قائمة الألعاب الأساسية
  const defaultGames = [
    { name: 'دريفت كار', category: 'CARS', image: 'drift-car.jpg' },
    { name: 'هافر بورد', category: 'ELECTRIC', image: 'hoverboard.jpg' },
    { name: 'موتسكل كهربائي', category: 'ELECTRIC', image: 'electric-bike.jpg' },
    { name: 'عربيه كهربائيه', category: 'CARS', image: 'electric-car.jpg' },
    { name: 'سكوتر كهربائي', category: 'ELECTRIC', image: 'electric-scooter.jpg' },
    { name: 'هارلي', category: 'MOTORCYCLE', image: 'harley.jpg' },
    { name: 'سيجواي', category: 'ELECTRIC', image: 'segway.jpg' },
    { name: 'كريزي كار', category: 'CARS', image: 'crazy-car.jpg' }
  ];

  const categories = [
    { value: 'ELECTRIC', label: 'كهربائية' },
    { value: 'CARS', label: 'سيارات' },
    { value: 'MOTORCYCLE', label: 'دراجات نارية' },
    { value: 'WATER', label: 'مائية' },
    { value: 'OTHER', label: 'أخرى' }
  ];

  const loadGames = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getGames();
      
      if (response.success) {
        setGames(response.data);
      } else {
        setError(response.error || 'فشل في تحميل الألعاب');
      }
    } catch (error) {
      setError('تعذر الاتصال بالخادم');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadGames();
  }, []);

  const handleAddGame = async () => {
    if (!newGame.name) {
      alert('اسم اللعبة مطلوب');
      return;
    }

    try {
      const response = await authService.createGame(newGame);
      
      if (response.success) {
        alert('تم إنشاء اللعبة بنجاح');
        setShowAddModal(false);
        setNewGame({
          name: '',
          category: 'ELECTRIC',
          description: '',
          image_url: '',
          minimum_age: 12,
          max_weight: 100,
          safety_instructions: ''
        });
        loadGames();
      } else {
        alert(response.error || 'فشل في إنشاء اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleAvailability = async (id, currentStatus) => {
    try {
      const response = await authService.updateGame(id, {
        is_available: !currentStatus
      });
      
      if (response.success) {
        loadGames();
      } else {
        alert(response.error || 'فشل في تحديث الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleDeleteGame = async (id) => {
    if (!window.confirm('هل تريد حذف هذه اللعبة؟')) return;

    try {
      const response = await authService.deleteGame(id);
      
      if (response.success) {
        alert('تم حذف اللعبة بنجاح');
        loadGames();
      } else {
        alert(response.error || 'فشل في حذف اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleSelectDefaultGame = (gameName) => {
    const game = defaultGames.find(g => g.name === gameName);
    if (game) {
      setNewGame({
        ...newGame,
        name: game.name,
        category: game.category,
        image_url: game.image
      });
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل الألعاب...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1>إدارة الألعاب</h1>
        <button 
          className="btn-add"
          onClick={() => setShowAddModal(true)}
        >
          + إضافة لعبة جديدة
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      {/* الفلترة حسب التصنيف */}
      <div className="filters">
        <div className="category-filter">
          <button 
            className={`category-btn ${selectedCategory === 'ALL' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('ALL')}
          >
            الكل
          </button>
          {categories.map(category => (
            <button
              key={category.value}
              className={`category-btn ${selectedCategory === category.value ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category.value)}
            >
              {category.label}
            </button>
          ))}
        </div>
      </div>

      <div className="games-grid">
        {games
          .filter(game => selectedCategory === 'ALL' || game.category === selectedCategory)
          .map(game => (
            <div key={game.id} className="game-card">
              <div className="game-image">
                <img 
                  src={`/images/${game.image_url || 'default-game.jpg'}`}
                  alt={game.name}
                  onError={(e) => {
                    e.target.onerror = null;
                    e.target.src = "https://via.placeholder.com/150x150?text=" + game.name;
                  }}
                />
              </div>
              
              <div className="game-content">
                <div className="game-header">
                  <h3>{game.name}</h3>
                  <span className="game-category">
                    {categories.find(c => c.value === game.category)?.label || game.category}
                  </span>
                </div>
                
                <div className="game-info">
                  <p className="game-description">{game.description || 'لا يوجد وصف'}</p>
                  
                  <div className="game-specs">
                    <div className="spec-item">
                      <span className="spec-label">الحد الأدنى للعمر:</span>
                      <span className="spec-value">{game.minimum_age} سنة</span>
                    </div>
                    <div className="spec-item">
                      <span className="spec-label">الوزن الأقصى:</span>
                      <span className="spec-value">{game.max_weight} كجم</span>
                    </div>
                  </div>
                  
                  <div className="game-status">
                    <span className={`availability-badge ${game.is_available ? 'available' : 'unavailable'}`}>
                      {game.is_available ? 'متاحة' : 'غير متاحة'}
                    </span>
                    <span className={`status-badge ${game.is_active ? 'active' : 'inactive'}`}>
                      {game.is_active ? 'نشطة' : 'غير نشطة'}
                    </span>
                  </div>
                </div>
                
                <div className="game-actions">
                  <button className="btn-edit">تعديل</button>
                  <button 
                    className={`btn-status ${game.is_available ? 'make-unavailable' : 'make-available'}`}
                    onClick={() => handleToggleAvailability(game.id, game.is_available)}
                  >
                    {game.is_available ? 'إيقاف' : 'تفعيل'}
                  </button>
                  <button 
                    className="btn-delete"
                    onClick={() => handleDeleteGame(game.id)}
                  >
                    حذف
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* Modal إضافة لعبة */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal wide-modal">
            <div className="modal-header">
              <h2>إضافة لعبة جديدة</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              
              {/* اختيار لعبة أساسية */}
              <div className="section">
                <h3>اختر من الألعاب الأساسية</h3>
                <div className="default-games-grid">
                  {defaultGames.map(game => (
                    <button
                      key={game.name}
                      className="default-game-btn"
                      onClick={() => handleSelectDefaultGame(game.name)}
                    >
                      <img 
                        src={`/images/${game.image}`}
                        alt={game.name}
                        onError={(e) => e.target.style.display = 'none'}
                      />
                      <span>{game.name}</span>
                    </button>
                  ))}
                </div>
              </div>
              
              {/* نموذج إضافة لعبة */}
              <div className="section">
                <h3>أو أدخل بيانات اللعبة يدوياً</h3>
                
                <div className="form-group">
                  <label>اسم اللعبة *</label>
                  <input
                    type="text"
                    value={newGame.name}
                    onChange={(e) => setNewGame({...newGame, name: e.target.value})}
                    placeholder="أدخل اسم اللعبة"
                  />
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>التصنيف</label>
                    <select
                      value={newGame.category}
                      onChange={(e) => setNewGame({...newGame, category: e.target.value})}
                    >
                      {categories.map(category => (
                        <option key={category.value} value={category.value}>
                          {category.label}
                        </option>
                      ))}
                    </select>
                  </div>
                  
                  <div className="form-group">
                    <label>اسم ملف الصورة</label>
                    <input
                      type="text"
                      value={newGame.image_url}
                      onChange={(e) => setNewGame({...newGame, image_url: e.target.value})}
                      placeholder="اسم الملف مثل: game.jpg"
                    />
                  </div>
                </div>
                
                <div className="form-row">
                  <div className="form-group">
                    <label>الحد الأدنى للعمر</label>
                    <input
                      type="number"
                      value={newGame.minimum_age}
                      onChange={(e) => setNewGame({...newGame, minimum_age: parseInt(e.target.value) || 12})}
                      min="1"
                      max="100"
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>الوزن الأقصى (كجم)</label>
                    <input
                      type="number"
                      value={newGame.max_weight}
                      onChange={(e) => setNewGame({...newGame, max_weight: parseInt(e.target.value) || 100})}
                      min="1"
                      max="500"
                    />
                  </div>
                </div>
                
                <div className="form-group">
                  <label>الوصف</label>
                  <textarea
                    value={newGame.description}
                    onChange={(e) => setNewGame({...newGame, description: e.target.value})}
                    placeholder="أدخل وصفاً للعبة"
                    rows="3"
                  />
                </div>
                
                <div className="form-group">
                  <label>تعليمات السلامة</label>
                  <textarea
                    value={newGame.safety_instructions}
                    onChange={(e) => setNewGame({...newGame, safety_instructions: e.target.value})}
                    placeholder="أدخل تعليمات السلامة"
                    rows="3"
                  />
                </div>
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowAddModal(false)}
              >
                إلغاء
              </button>
              <button 
                className="btn-save"
                onClick={handleAddGame}
              >
                حفظ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GamesManagement;
