import React, { useState, useEffect, useRef, useCallback, useMemo } from 'react';
import { 
  Search, Filter, Download, RefreshCw, Calendar, 
  DollarSign, TrendingUp, BarChart3, FileText,
  ChevronDown, ChevronUp, CheckCircle, XCircle,
  Eye, Printer, Save, Building, Clock, Users,
  ChevronLeft, ChevronRight, AlertCircle, Check,
  X, Plus, Minus, Calculator, Lock, Unlock,
  Edit, Trash2, User, Gamepad2, CreditCard,
  Calendar as CalendarIcon, Clock as ClockIcon,
  Phone, Mail, MapPin, Package, Tag, ExternalLink,
  MoreVertical, DownloadCloud, Upload, Settings,
  Shield, AlertTriangle, Bell, Star, Zap
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== نظام الإشعارات ====================
const NotificationSystem = ({ type, message, onClose }) => {
  const [visible, setVisible] = useState(true);
  
  useEffect(() => {
    const timer = setTimeout(() => {
      setVisible(false);
      if (onClose) onClose();
    }, 5000);
    return () => clearTimeout(timer);
  }, [onClose]);

  if (!visible) return null;

  const colors = {
    success: 'bg-green-500 border-green-600',
    error: 'bg-red-500 border-red-600',
    warning: 'bg-yellow-500 border-yellow-600',
    info: 'bg-blue-500 border-blue-600'
  };

  const icons = {
    success: '✅',
    error: '❌',
    warning: '⚠️',
    info: 'ℹ️'
  };

  return (
    <div className={`notification ${colors[type]}`}>
      <span className="notification-icon">{icons[type]}</span>
      <span className="notification-message">{message}</span>
      <button onClick={() => { setVisible(false); if (onClose) onClose(); }} className="notification-close">✕</button>
    </div>
  );
};

// ==================== دوال مساعدة ====================
const formatCurrency = (amount) => {
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => /^01[0-9]{9}$/.test(phone);

const calculateDuration = (start, end) => {
  if (!start) return 0;
  const startTime = new Date(start);
  const endTime = end ? new Date(end) : new Date();
  return Math.floor((endTime - startTime) / (1000 * 60));
};

const generateRentalNumber = () => {
  const timestamp = Date.now().toString().slice(-8);
  const random = Math.floor(Math.random() * 1000).toString().padStart(3, '0');
  return `RENT-${timestamp}-${random}`;
};

// ==================== مكون قائمة الألعاب ====================
const GamesGrid = ({ games, branchId, onSelectGame }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');

  const getGameImage = (game) => {
    const imageMap = {
      'دريفت كار': '/images/Driftcar.jpg',
      'هافر بورد': '/images/Hoverboard.jpg',
      'سكوتر': '/images/Scooter.jpg',
      'عربة كهربائية': '/images/Car.jpg',
      'موتوسيكل كهربائي': '/images/Motor.jpg',
      'هارلي': '/images/harley.jpg',
      'سيجواي': '/images/Segway.jpg',
      'كرازي كار': '/images/Ninebot.jpg',
      'سكيت كهربائي': '/images/Skate.jpg',
      'ترامبولين': '/images/Trampoline.jpg',
      'محاكي': '/images/Simulator.jpg'
    };
    return imageMap[game.name] || '/images/default-game.jpg';
  };

  const filteredGames = useMemo(() => {
    return games.filter(game => {
      const matchesBranch = game.branch_id === parseInt(branchId);
      const matchesSearch = game.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          (game.category || '').toLowerCase().includes(searchTerm.toLowerCase());
      const matchesCategory = selectedCategory === 'all' || game.category === selectedCategory;
      
      return matchesBranch && matchesSearch && matchesCategory && game.is_active === 1;
    });
  }, [games, branchId, searchTerm, selectedCategory]);

  const categories = useMemo(() => {
    const cats = new Set();
    games.forEach(game => {
      if (game.category && game.branch_id === parseInt(branchId)) {
        cats.add(game.category);
      }
    });
    return Array.from(cats);
  }, [games, branchId]);

  return (
    <div className="games-grid-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">🎮</span>
          الألعاب المتاحة
          <span className="section-count">({filteredGames.length})</span>
        </h3>
        
        <div className="section-controls">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="ابحث عن لعبة..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="clear-search">
                <X size={16} />
              </button>
            )}
          </div>
          
          <div className="filter-controls">
            <select 
              value={selectedCategory}
              onChange={(e) => setSelectedCategory(e.target.value)}
              className="filter-select"
            >
              <option value="all">جميع الفئات</option>
              {categories.map(cat => (
                <option key={cat} value={cat}>{cat}</option>
              ))}
            </select>
          </div>
        </div>
      </div>
      
      {filteredGames.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">🎮</div>
          <h3>لا توجد ألعاب متاحة</h3>
          <p>قم بتعديل عوامل البحث أو تحقق من توفر الألعاب</p>
        </div>
      ) : (
        <div className="games-grid">
          {filteredGames.map(game => (
            <div 
              key={game.id} 
              className="game-card"
              onClick={() => onSelectGame(game)}
            >
              <div className="game-image-container">
                <img 
                  src={getGameImage(game)} 
                  alt={game.name}
                  className="game-image"
                  onError={(e) => e.target.src = '/images/default-game.jpg'}
                />
                <div className="game-status-badge available">
                  <CheckCircle size={14} />
                  <span>متاح</span>
                </div>
              </div>
              
              <div className="game-info">
                <h4 className="game-name">{game.name}</h4>
                <div className="game-details">
                  <span className="game-category">{game.category || 'عام'}</span>
                  <span className="game-price">
                    {formatCurrency(game.price_per_15min)} / 15 دقيقة
                  </span>
                </div>
                
                <div className="game-actions">
                  <button className="select-game-btn">
                    <span className="btn-icon">➕</span>
                    <span className="btn-text">إضافة للتأجير</span>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// ==================== مكون تأجير متعدد ====================
const MultiRentalModal = ({ show, onClose, selectedGames, onConfirmRental, currentShift }) => {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });
  
  const [rentalItems, setRentalItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('كاش');
  const [paymentStatus, setPaymentStatus] = useState('مدفوع');
  const [notes, setNotes] = useState('');
  
  useEffect(() => {
    if (show && selectedGames.length > 0) {
      const items = selectedGames.map(game => ({
        id: Date.now() + Math.random(),
        gameId: game.id,
        gameName: game.name,
        gameImage: game.image_url,
        rentalType: 'fixed',
        duration: 15,
        pricePer15min: game.price_per_15min || 100,
        quantity: 1,
        notes: ''
      }));
      setRentalItems(items);
    }
  }, [show, selectedGames]);

  const updateRentalItem = (id, updates) => {
    setRentalItems(items => 
      items.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  };

  const removeRentalItem = (id) => {
    setRentalItems(items => items.filter(item => item.id !== id));
  };

  const calculateItemPrice = (item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return item.pricePer15min * units * item.quantity;
  };

  const calculateTotal = () => {
    return rentalItems.reduce((total, item) => {
      return total + calculateItemPrice(item);
    }, 0);
  };

  const handleSubmit = () => {
    if (!customerInfo.name || !customerInfo.phone) {
      alert('يرجى إدخال اسم العميل ورقم الهاتف');
      return;
    }
    
    if (!validatePhone(customerInfo.phone)) {
      alert('رقم الهاتف غير صحيح. يجب أن يكون 11 رقماً ويبدأ بـ 01');
      return;
    }
    
    if (rentalItems.length === 0) {
      alert('يرجى إضافة ألعاب للتأجير');
      return;
    }

    const rentalData = {
      customerInfo,
      rentalItems,
      paymentMethod,
      paymentStatus,
      notes,
      totalAmount: calculateTotal(),
      shiftId: currentShift?.id,
      timestamp: new Date().toISOString()
    };

    onConfirmRental(rentalData);
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal multi-rental-modal">
        <div className="modal-header">
          <div className="modal-title">
            <h2>
              <span className="modal-icon">🎯</span>
              تأجير متعدد
              {currentShift && (
                <span className="shift-badge">الشيفت #{currentShift.id}</span>
              )}
            </h2>
          </div>
          <button onClick={onClose} className="modal-close">✕</button>
        </div>
        
        <div className="modal-body">
          {/* معلومات العميل */}
          <div className="customer-section">
            <h3 className="section-title">
              <User size={20} />
              معلومات العميل
            </h3>
            <div className="form-grid">
              <div className="form-group">
                <label>الاسم الكامل *</label>
                <div className="input-with-icon">
                  <User size={18} />
                  <input
                    type="text"
                    value={customerInfo.name}
                    onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                    placeholder="أدخل اسم العميل"
                    className="form-input"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>رقم الهاتف *</label>
                <div className="input-with-icon">
                  <Phone size={18} />
                  <input
                    type="tel"
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                    placeholder="01xxxxxxxxx"
                    maxLength={11}
                    className="form-input"
                  />
                </div>
                {customerInfo.phone && !validatePhone(customerInfo.phone) && (
                  <span className="error-text">رقم هاتف غير صحيح</span>
                )}
              </div>
              
              <div className="form-group">
                <label>البريد الإلكتروني</label>
                <div className="input-with-icon">
                  <Mail size={18} />
                  <input
                    type="email"
                    value={customerInfo.email}
                    onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                    placeholder="email@example.com"
                    className="form-input"
                  />
                </div>
              </div>
              
              <div className="form-group full-width">
                <label>العنوان</label>
                <div className="input-with-icon">
                  <MapPin size={18} />
                  <input
                    type="text"
                    value={customerInfo.address}
                    onChange={(e) => setCustomerInfo({...customerInfo, address: e.target.value})}
                    placeholder="العنوان الكامل"
                    className="form-input"
                  />
                </div>
              </div>
            </div>
          </div>
          
          {/* قائمة التأجيرات */}
          <div className="rentals-section">
            <h3 className="section-title">
              <FileText size={20} />
              قائمة التأجيرات ({rentalItems.length})
            </h3>
            
            {rentalItems.map(item => (
              <div key={item.id} className="rental-item-card">
                <div className="rental-item-header">
                  <div className="item-info">
                    <div className="item-image-container">
                      <img src={item.gameImage} alt={item.gameName} className="item-image" />
                    </div>
                    <div className="item-details">
                      <h4>{item.gameName}</h4>
                      <span className="item-price">
                        {formatCurrency(item.pricePer15min)} / 15 دقيقة
                      </span>
                    </div>
                  </div>
                  
                  <button 
                    onClick={() => removeRentalItem(item.id)}
                    className="remove-item-btn"
                    title="إزالة من القائمة"
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
                
                <div className="rental-item-options">
                  <div className="options-grid">
                    {/* نوع التأجير */}
                    <div className="option-group">
                      <label>نوع التأجير:</label>
                      <div className="toggle-buttons">
                        <button
                          className={`toggle-btn ${item.rentalType === 'fixed' ? 'active' : ''}`}
                          onClick={() => updateRentalItem(item.id, { rentalType: 'fixed', duration: 15 })}
                        >
                          <ClockIcon size={16} />
                          وقت ثابت
                        </button>
                        <button
                          className={`toggle-btn ${item.rentalType === 'open' ? 'active' : ''}`}
                          onClick={() => updateRentalItem(item.id, { rentalType: 'open', duration: 0 })}
                        >
                          <Unlock size={16} />
                          وقت مفتوح
                        </button>
                      </div>
                    </div>
                    
                    {/* المدة (للتأجير الثابت) */}
                    {item.rentalType === 'fixed' && (
                      <div className="option-group">
                        <label>المدة:</label>
                        <div className="duration-options">
                          {[15, 30, 45, 60, 90, 120].map(minutes => (
                            <button
                              key={minutes}
                              className={`duration-btn ${item.duration === minutes ? 'active' : ''}`}
                              onClick={() => updateRentalItem(item.id, { duration: minutes })}
                            >
                              {minutes} دقيقة
                            </button>
                          ))}
                        </div>
                      </div>
                    )}
                    
                    {/* الكمية */}
                    <div className="option-group">
                      <label>الكمية:</label>
                      <div className="quantity-control">
                        <button 
                          onClick={() => updateRentalItem(item.id, { 
                            quantity: Math.max(1, item.quantity - 1) 
                          })}
                          className="quantity-btn"
                        >
                          <Minus size={16} />
                        </button>
                        <span className="quantity-value">{item.quantity}</span>
                        <button 
                          onClick={() => updateRentalItem(item.id, { 
                            quantity: item.quantity + 1 
                          })}
                          className="quantity-btn"
                        >
                          <Plus size={16} />
                        </button>
                      </div>
                    </div>
                  </div>
                  
                  {/* ملاحظات العنصر */}
                  <div className="item-notes">
                    <label>ملاحظات خاصة:</label>
                    <input
                      type="text"
                      value={item.notes}
                      onChange={(e) => updateRentalItem(item.id, { notes: e.target.value })}
                      placeholder="ملاحظات خاصة بهذه اللعبة..."
                      className="notes-input"
                    />
                  </div>
                  
                  {/* الملخص */}
                  <div className="item-summary">
                    <span className="summary-label">المبلغ:</span>
                    <span className="summary-value">
                      {item.rentalType === 'open' 
                        ? '⏳ عند الإنهاء' 
                        : formatCurrency(calculateItemPrice(item))}
                    </span>
                  </div>
                </div>
              </div>
            ))}
            
            {rentalItems.length === 0 && (
              <div className="empty-items">
                <Package size={48} />
                <p>لا توجد ألعاب في قائمة التأجير</p>
                <small>قم بإضافة ألعاب من القائمة الرئيسية</small>
              </div>
            )}
          </div>
          
          {/* المعلومات المالية */}
          <div className="payment-section">
            <h3 className="section-title">
              <CreditCard size={20} />
              المعلومات المالية
            </h3>
            
            <div className="form-grid">
              <div className="form-group">
                <label>طريقة الدفع:</label>
                <select 
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="form-input"
                >
                  <option value="كاش">💵 نقدي</option>
                  <option value="بطاقة">💳 بطاقة ائتمان</option>
                  <option value="محفظة">📱 محفظة إلكترونية</option>
                  <option value="تحويل">🏦 تحويل بنكي</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>حالة الدفع:</label>
                <select 
                  value={paymentStatus}
                  onChange={(e) => setPaymentStatus(e.target.value)}
                  className="form-input"
                >
                  <option value="مدفوع">✅ مدفوع</option>
                  <option value="جزئي">💰 مدفوع جزئياً</option>
                  <option value="عند الإنهاء">⏳ عند الإنهاء</option>
                  <option value="مجاني">🎁 مجاني</option>
                </select>
              </div>
              
              <div className="form-group full-width">
                <label>ملاحظات إضافية:</label>
                <textarea
                  value={notes}
                  onChange={(e) => setNotes(e.target.value)}
                  placeholder="أي ملاحظات إضافية عن التأجير..."
                  rows="3"
                  className="form-textarea"
                />
              </div>
            </div>
          </div>
          
          {/* الملخص النهائي */}
          <div className="summary-section">
            <h3 className="section-title">
              <Calculator size={20} />
              الملخص النهائي
            </h3>
            
            <div className="summary-grid">
              <div className="summary-item">
                <span>عدد الألعاب:</span>
                <span className="summary-value">{rentalItems.length}</span>
              </div>
              
              <div className="summary-item">
                <span>إجمالي القطع:</span>
                <span className="summary-value">
                  {rentalItems.reduce((sum, item) => sum + item.quantity, 0)}
                </span>
              </div>
              
              <div className="summary-item">
                <span>المدفوع الآن:</span>
                <span className="summary-value total-amount">
                  {formatCurrency(calculateTotal())}
                </span>
              </div>
              
              {rentalItems.some(item => item.rentalType === 'open') && (
                <div className="summary-item warning">
                  <AlertTriangle size={16} />
                  <span>يوجد {rentalItems.filter(item => item.rentalType === 'open').length} لعبة بالدفع عند الإنهاء</span>
                </div>
              )}
            </div>
          </div>
        </div>
        
        <div className="modal-footer">
          <div className="footer-info">
            <div className="timestamp">
              <CalendarIcon size={14} />
              <span>{formatDateTime(new Date().toISOString())}</span>
            </div>
            {currentShift && (
              <div className="shift-info">
                <span className="shift-label">الشيفت:</span>
                <span className="shift-value">#{currentShift.id}</span>
              </div>
            )}
          </div>
          
          <div className="footer-actions">
            <button onClick={onClose} className="btn btn-secondary">
              <X size={18} />
              إلغاء
            </button>
            <button onClick={handleSubmit} className="btn btn-primary">
              <Check size={18} />
              بدء التأجير
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

// ==================== مكون التأجيرات النشطة ====================
const ActiveRentalsTable = ({ rentals, onAction, onRefresh }) => {
  const [selectedRentals, setSelectedRentals] = useState([]);
  const [showActions, setShowActions] = useState(false);

  const handleSelectAll = () => {
    if (selectedRentals.length === rentals.length) {
      setSelectedRentals([]);
    } else {
      setSelectedRentals(rentals.map(r => r.id));
    }
  };

  const handleSelectRental = (rentalId) => {
    setSelectedRentals(prev =>
      prev.includes(rentalId)
        ? prev.filter(id => id !== rentalId)
        : [...prev, rentalId]
    );
  };

  const handleBulkAction = (action) => {
    const selected = rentals.filter(r => selectedRentals.includes(r.id));
    selected.forEach(rental => {
      onAction(action, rental);
    });
    setSelectedRentals([]);
  };

  const getRemainingTime = (rental) => {
    if (rental.is_open_time || rental.rental_type === 'open') return '⏳ وقت مفتوح';
    
    const endTime = new Date(rental.start_time);
    endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
    const now = new Date();
    
    if (now > endTime) return '⏰ انتهى';
    
    const diffMs = endTime - now;
    const minutes = Math.floor(diffMs / 60000);
    const seconds = Math.floor((diffMs % 60000) / 1000);
    
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="active-rentals-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">🔥</span>
          التأجيرات النشطة
          <span className="section-count">({rentals.length})</span>
        </h3>
        
        <div className="section-controls">
          {selectedRentals.length > 0 && (
            <div className="bulk-actions">
              <span className="selected-count">
                تم اختيار {selectedRentals.length} تأجير
              </span>
              <button
                onClick={() => handleBulkAction('end')}
                className="btn btn-sm btn-warning"
                disabled={!selectedRentals.some(id => 
                  rentals.find(r => r.id === id)?.is_open_time || 
                  rentals.find(r => r.id === id)?.rental_type === 'open'
                )}
              >
                <Clock size={16} />
                إنهاء المحدد
              </button>
              <button
                onClick={() => handleBulkAction('cancel')}
                className="btn btn-sm btn-danger"
              >
                <XCircle size={16} />
                إلغاء المحدد
              </button>
              <button
                onClick={() => setSelectedRentals([])}
                className="btn btn-sm btn-secondary"
              >
                <X size={16} />
                إلغاء التحديد
              </button>
            </div>
          )}
          
          <button
            onClick={onRefresh}
            className="btn btn-sm btn-secondary"
          >
            <RefreshCw size={16} />
            تحديث
          </button>
        </div>
      </div>
      
      {rentals.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">⏱️</div>
          <h3>لا توجد تأجيرات نشطة</h3>
          <p>ابدأ تأجير جديد من قائمة الألعاب</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="rentals-table">
            <thead>
              <tr>
                <th width="40">
                  <input
                    type="checkbox"
                    checked={selectedRentals.length === rentals.length && rentals.length > 0}
                    onChange={handleSelectAll}
                    className="select-checkbox"
                  />
                </th>
                <th>رقم التأجير</th>
                <th>العميل</th>
                <th>اللعبة</th>
                <th>الوقت المنقضي</th>
                <th>المدة</th>
                <th>حالة الدفع</th>
                <th>الإجراءات</th>
              </tr>
            </thead>
            <tbody>
              {rentals.map(rental => (
                <tr key={rental.id} className={getRemainingTime(rental) === '⏰ انتهى' ? 'expired' : ''}>
                  <td>
                    <input
                      type="checkbox"
                      checked={selectedRentals.includes(rental.id)}
                      onChange={() => handleSelectRental(rental.id)}
                      className="select-checkbox"
                    />
                  </td>
                  <td className="rental-number">
                    <strong>#{rental.rental_number || rental.id}</strong>
                    {rental.is_open_time && (
                      <span className="badge badge-warning">مفتوح</span>
                    )}
                  </td>
                  <td>
                    <div className="customer-cell">
                      <div className="customer-name">
                        <User size={14} />
                        {rental.customer_name}
                      </div>
                      <div className="customer-phone">
                        <Phone size={12} />
                        {rental.customer_phone}
                      </div>
                    </div>
                  </td>
                  <td className="game-cell">
                    <Gamepad2 size={14} />
                    {rental.game_name}
                  </td>
                  <td>
                    <div className={`time-cell ${getRemainingTime(rental) === '⏰ انتهى' ? 'expired' : ''}`}>
                      <Clock size={14} />
                      {getRemainingTime(rental)}
                    </div>
                  </td>
                  <td>
                    {rental.is_open_time || rental.rental_type === 'open'
                      ? '⏳ مفتوح'
                      : `${rental.duration_minutes} دقيقة`}
                  </td>
                  <td>
                    <span className={`payment-status ${rental.payment_status === 'مدفوع' ? 'paid' : 'pending'}`}>
                      {rental.payment_status === 'مدفوع' 
                        ? <><CheckCircle size={14} /> مدفوع</>
                        : <><Clock size={14} /> {rental.payment_status || 'عند الإنهاء'}</>
                      }
                    </span>
                  </td>
                  <td>
                    <div className="action-buttons">
                      <button
                        onClick={() => onAction('details', rental)}
                        className="action-btn view-btn"
                        title="عرض التفاصيل"
                      >
                        <Eye size={16} />
                      </button>
                      
                      {(rental.is_open_time || rental.rental_type === 'open') && (
                        <button
                          onClick={() => onAction('end', rental)}
                          className="action-btn end-btn"
                          title="إنهاء الوقت"
                        >
                          <Clock size={16} />
                        </button>
                      )}
                      
                      {!(rental.is_open_time || rental.rental_type === 'open') && (
                       <button
  onClick={() => onAction('extend', rental)}
  className="action-btn extend-btn"
  title="تمديد الوقت"
>
  <Plus size={16} /> 
</button>
                      )}
                      
                      <button
                        onClick={() => onAction('cancel', rental)}
                        className="action-btn cancel-btn"
                        title="إلغاء التأجير"
                      >
                        <XCircle size={16} />
                      </button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

// ==================== مكون التأجيرات المكتملة ====================
const CompletedRentalsTable = ({ rentals, onExport, onPrint }) => {
  const [selectedPeriod, setSelectedPeriod] = useState('today');
  const [filteredRentals, setFilteredRentals] = useState([]);

  useEffect(() => {
    const now = new Date();
    let filtered = [...rentals];

    switch (selectedPeriod) {
      case 'today':
        const today = now.toISOString().split('T')[0];
        filtered = rentals.filter(r => r.end_time && r.end_time.split('T')[0] === today);
        break;
      case 'week':
        const weekAgo = new Date(now.setDate(now.getDate() - 7));
        filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= weekAgo);
        break;
      case 'month':
        const monthAgo = new Date(now.setMonth(now.getMonth() - 1));
        filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= monthAgo);
        break;
      default:
        break;
    }

    setFilteredRentals(filtered);
  }, [rentals, selectedPeriod]);

  const totalRevenue = useMemo(() => {
    return filteredRentals.reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
  }, [filteredRentals]);

  return (
    <div className="completed-rentals-section">
      <div className="section-header">
        <h3 className="section-title">
          <span className="section-icon">✅</span>
          التأجيرات المكتملة
          <span className="section-count">({filteredRentals.length})</span>
        </h3>
        
        <div className="section-controls">
          <div className="period-selector">
            <select
              value={selectedPeriod}
              onChange={(e) => setSelectedPeriod(e.target.value)}
              className="period-select"
            >
              <option value="today">اليوم</option>
              <option value="week">آخر 7 أيام</option>
              <option value="month">آخر 30 يوم</option>
              <option value="all">الكل</option>
            </select>
          </div>
          
          <button
            onClick={() => onExport(filteredRentals, selectedPeriod)}
            className="btn btn-sm btn-success"
          >
            <DownloadCloud size={16} />
            تصدير
          </button>
          
          <button
            onClick={() => onPrint(filteredRentals, selectedPeriod)}
            className="btn btn-sm btn-primary"
          >
            <Printer size={16} />
            طباعة
          </button>
        </div>
      </div>
      
      {filteredRentals.length === 0 ? (
        <div className="empty-state">
          <div className="empty-icon">📭</div>
          <h3>لا توجد تأجيرات مكتملة</h3>
          <p>لم تكتمل أي تأجيرات في الفترة المحددة</p>
        </div>
      ) : (
        <>
          <div className="revenue-summary">
            <div className="summary-card">
              <div className="summary-icon">
                <DollarSign size={24} />
              </div>
              <div className="summary-content">
                <div className="summary-label">إجمالي الإيرادات</div>
                <div className="summary-value">{formatCurrency(totalRevenue)}</div>
                <div className="summary-period">
                  {selectedPeriod === 'today' && 'لليوم'}
                  {selectedPeriod === 'week' && 'لآخر 7 أيام'}
                  {selectedPeriod === 'month' && 'لآخر 30 يوم'}
                  {selectedPeriod === 'all' && 'الإجمالي الكلي'}
                </div>
              </div>
            </div>
          </div>
          
          <div className="table-container">
            <table className="rentals-table">
              <thead>
                <tr>
                  <th>رقم التأجير</th>
                  <th>العميل</th>
                  <th>اللعبة</th>
                  <th>وقت البدء</th>
                  <th>وقت الانتهاء</th>
                  <th>المدة</th>
                  <th>المبلغ</th>
                  <th>حالة الدفع</th>
                  <th>طريقة الدفع</th>
                </tr>
              </thead>
              <tbody>
                {filteredRentals.map(rental => (
                  <tr key={rental.id}>
                    <td>#{rental.rental_number || rental.id}</td>
                    <td>
                      <div className="customer-info">
                        <div className="customer-name">{rental.customer_name}</div>
                        <div className="customer-phone">{rental.customer_phone}</div>
                      </div>
                    </td>
                    <td>{rental.game_name}</td>
                    <td>{formatTime(rental.start_time)}</td>
                    <td>{formatTime(rental.end_time)}</td>
                    <td>{rental.duration_minutes} دقيقة</td>
                    <td className="amount-cell">{formatCurrency(rental.total_amount)}</td>
                    <td>
                      <span className={`payment-status ${rental.payment_status === 'مدفوع' ? 'paid' : 'partial'}`}>
                        {rental.payment_status === 'مدفوع' 
                          ? <><CheckCircle size={12} /> مدفوع</>
                          : <><AlertCircle size={12} /> {rental.payment_status}</>
                        }
                      </span>
                    </td>
                    <td>
                      <span className="payment-method">
                        {rental.payment_method === 'كاش' ? '💵' : '💳'}
                        {rental.payment_method}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

