// ==================== المكون الرئيسي ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  const isEmployee = user?.role === 'employee';

  // الحالات الرئيسية
  const [loading, setLoading] = useState(true);
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  
  // الحالات للواجهة
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [notifications, setNotifications] = useState([]);
  
  // الإحصائيات
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    openRentals: 0,
    fixedRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    monthlyRevenue: 0
  });

  // ==================== دوال جلب البيانات ====================
  const loadGames = async () => {
    try {
      const response = await api.get('/games', {
        params: { branch_id: user.branch_id, is_active: 1 }
      });
      
      if (response.success) {
        setGames(response.data || []);
        const availableGames = response.data?.filter(g => g.status === 'متاح').length || 0;
        setStats(prev => ({
          ...prev,
          availableGames: availableGames,
          totalGames: response.data?.length || 0
        }));
      }
    } catch (error) {
      console.error('Error loading games:', error);
      addNotification('error', 'تعذر تحميل الألعاب');
    }
  };

  const loadActiveRentals = async () => {
    try {
      const response = await api.getActiveRentals(user.branch_id);
      
      if (response.success) {
        setActiveRentals(response.data || []);
        const openRentals = response.data?.filter(r => r.is_open_time || r.rental_type === 'open').length || 0;
        setStats(prev => ({
          ...prev,
          activeRentals: response.data?.length || 0,
          openRentals: openRentals,
          fixedRentals: (response.data?.length || 0) - openRentals
        }));
      }
    } catch (error) {
      console.error('Error loading active rentals:', error);
      addNotification('error', 'تعذر تحميل التأجيرات النشطة');
    }
  };

  const loadCompletedRentals = async () => {
    if (!isManager) return;
    
    try {
      const today = new Date().toISOString().split('T')[0];
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'مكتمل',
          start_date: today,
          limit: 50
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data || []);
        
        // حساب إيرادات اليوم
        const todayRentals = response.data?.filter(r => 
          r.end_time && r.end_time.split('T')[0] === today
        ) || [];
        
        const todayRevenue = todayRentals.reduce((sum, rental) => 
          sum + (parseFloat(rental.total_amount) || 0), 0
        );
        
        setStats(prev => ({
          ...prev,
          todayRevenue: todayRevenue,
          todayRentals: todayRentals.length
        }));
      }
    } catch (error) {
      console.error('Error loading completed rentals:', error);
      addNotification('error', 'تعذر تحميل التأجيرات المكتملة');
    }
  };

  const loadCurrentShift = async () => {
    try {
      console.log('🔄 محاولة جلب الشيفت الحالي...');
      
      let response = await api.getActiveShiftSafe();
      
      if (!response.success) {
        response = await api.get('/shifts/active');
      }
      
      if (response.success && response.data) {
        console.log(`✅ تم جلب الشيفت #${response.data.id}`);
        setCurrentShift(response.data);
      } else {
        console.log('ℹ️ لا يوجد شيفت نشط حالياً');
        setCurrentShift(null);
      }
      
    } catch (error) {
      console.error('❌ فشل جلب الشيفت:', error);
      addNotification('error', 'تعذر الاتصال بالخادم');
      setCurrentShift(null);
    }
  };

  const startShift = async () => {
    try {
      addNotification('info', 'جاري بدء شيفت جديد...');
      
      const response = await api.startShiftSafe();
      
      if (response.success) {
        setCurrentShift(response.data);
        addNotification('success', `تم بدء شيفت جديد #${response.data.id}`);
        
        setTimeout(() => {
          loadAllData();
        }, 1000);
        
      } else {
        addNotification('error', response.message || 'فشل بدء الشيفت');
      }
      
    } catch (error) {
      console.error('🔥 خطأ في بدء الشيفت:', error);
      addNotification('error', 'حدث خطأ غير متوقع');
    }
  };

  const endShift = async () => {
    if (!currentShift) return;
    
    if (!window.confirm('هل تريد إنهاء الشيفت الحالي؟')) return;
    
    try {
      const response = await api.endShift(currentShift.id, {
        end_time: new Date().toISOString(),
        status: 'منتهي'
      });
      
      if (response.success) {
        setCurrentShift(null);
        setActiveRentals([]);
        addNotification('success', 'تم إنهاء الشيفت بنجاح');
        await loadAllData();
      }
    } catch (error) {
      console.error('Error ending shift:', error);
      addNotification('error', 'فشل إنهاء الشيفت');
    }
  };

  // جلب جميع البيانات
  const loadAllData = async () => {
    setLoading(true);
    try {
      await Promise.all([
        loadGames(),
        loadActiveRentals(),
        loadCurrentShift()
      ]);
      
      if (currentShift) {
        await loadCompletedRentals();
      }
    } catch (error) {
      console.error('Error loading all data:', error);
      addNotification('error', 'حدث خطأ في تحميل البيانات');
    } finally {
      setLoading(false);
    }
  };

  // ==================== دوال إدارة التأجيرات ====================
  const handleSelectGame = (game) => {
    if (!currentShift) {
      addNotification('warning', 'يجب بدء شيفت أولاً');
      return;
    }
    
    const isAlreadySelected = selectedGames.some(g => g.id === game.id);
    
    if (!isAlreadySelected) {
      setSelectedGames([...selectedGames, game]);
      addNotification('success', `تمت إضافة ${game.name} للتأجير`);
    }
    
    setShowRentalModal(true);
  };

  const handleCreateRental = async (rentalData) => {
    try {
      const { customerInfo, rentalItems, paymentMethod, paymentStatus, notes, totalAmount } = rentalData;
      
      const results = [];
      
      for (const item of rentalItems) {
        const rentalPayload = {
          rental_number: generateRentalNumber(),
          customer_name: customerInfo.name,
          customer_phone: customerInfo.phone,
          customer_email: customerInfo.email || '',
          customer_address: customerInfo.address || '',
          game_id: item.gameId,
          branch_id: user.branch_id,
          shift_id: currentShift.id,
          employee_id: user.id,
          employee_name: user.name,
          rental_type: item.rentalType,
          duration_minutes: item.rentalType === 'fixed' ? item.duration : 0,
          total_amount: item.rentalType === 'fixed' ? 
            (item.pricePer15min * Math.ceil(item.duration / 15) * item.quantity) : 0,
          payment_method: paymentMethod,
          payment_status: paymentStatus,
          start_time: new Date().toISOString(),
          status: 'نشط',
          notes: `${notes} ${item.notes}`.trim(),
          quantity: item.quantity
        };
        
        const response = await api.createRental(rentalPayload);
        results.push(response);
      }
      
      if (results.every(r => r.success)) {
        addNotification('success', `تم إنشاء ${rentalItems.length} تأجير بنجاح`);
        setShowRentalModal(false);
        setSelectedGames([]);
        await loadAllData();
      } else {
        addNotification('error', 'حدث خطأ في بعض التأجيرات');
      }
      
    } catch (error) {
      console.error('Error creating rental:', error);
      addNotification('error', 'حدث خطأ في إنشاء التأجير');
    }
  };

const handleRentalAction = async (action, rental) => {
  switch(action) {
    case 'details': {
      // عرض التفاصيل
      alert(`تفاصيل التأجير #${rental.rental_number}\nالعميل: ${rental.customer_name}\nاللعبة: ${rental.game_name}\nالوقت: ${formatDateTime(rental.start_time)}`);
      break;
    }
      
    case 'end': {
      try {
        const response = await api.completeRentalSimple(rental.id, {
          payment_method: rental.payment_method || 'كاش'
        });
        
        if (response.success) {
          addNotification('success', 'تم إنهاء التأجير بنجاح');
          await loadAllData();
        }
      } catch (error) {
        console.error('Error ending rental:', error);
        addNotification('error', 'فشل إنهاء التأجير');
      }
      break;
    }
        
    case 'extend': {
      const extraTime = prompt('أدخل المدة الإضافية بالدقائق:', '15');
      if (extraTime && !isNaN(extraTime)) {
        addNotification('info', `سيتم تمديد الوقت بمقدار ${extraTime} دقيقة`);
        // هنا يمكن إضافة منطق التمديد
      }
      break;
    }
        
    case 'cancel': {
      if (window.confirm(`هل تريد إلغاء تأجير ${rental.rental_number}؟`)) {
        try {
          const response = await api.cancelRental(rental.id, rental.total_amount || 0, 'إلغاء من قبل الموظف');
          
          if (response.success) {
            addNotification('success', 'تم إلغاء التأجير بنجاح');
            await loadAllData();
          }
        } catch (error) {
          console.error('Error cancelling rental:', error);
          addNotification('error', 'فشل إلغاء التأجير');
        }
      }
      break;
    }
  }
};

  const handleExportCompleted = (rentals, period) => {
    const dataStr = JSON.stringify(rentals, null, 2);
    const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
    
    const exportFileDefaultName = `تأجيرات_${period}_${new Date().toISOString().split('T')[0]}.json`;
    
    const linkElement = document.createElement('a');
    linkElement.setAttribute('href', dataUri);
    linkElement.setAttribute('download', exportFileDefaultName);
    linkElement.click();
    
    addNotification('success', `تم تصدير ${rentals.length} تأجير`);
  };

  const handlePrintCompleted = (rentals, period) => {
    const printWindow = window.open('', '_blank');
    const content = `
      <html>
        <head>
          <title>تقرير التأجيرات - ${period}</title>
          <style>
            body { font-family: 'Arial', sans-serif; direction: rtl; padding: 20px; }
            h1 { text-align: center; color: #333; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: center; }
            th { background-color: #f2f2f2; }
            .total { font-weight: bold; color: #2196F3; }
            @media print {
              body { padding: 0; }
              .no-print { display: none; }
            }
          </style>
        </head>
        <body>
          <h1>تقرير التأجيرات المكتملة</h1>
          <p><strong>الفترة:</strong> ${period}</p>
          <p><strong>عدد التأجيرات:</strong> ${rentals.length}</p>
          <p><strong>التاريخ:</strong> ${new Date().toLocaleDateString('ar-EG')}</p>
          
          <table>
            <thead>
              <tr>
                <th>رقم التأجير</th>
                <th>العميل</th>
                <th>اللعبة</th>
                <th>وقت البدء</th>
                <th>وقت الانتهاء</th>
                <th>المبلغ</th>
              </tr>
            </thead>
            <tbody>
              ${rentals.map(rental => `
                <tr>
                  <td>${rental.rental_number || rental.id}</td>
                  <td>${rental.customer_name}</td>
                  <td>${rental.game_name}</td>
                  <td>${formatTime(rental.start_time)}</td>
                  <td>${formatTime(rental.end_time)}</td>
                  <td>${formatCurrency(rental.total_amount)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          
          <div style="margin-top: 20px; padding: 10px; background: #f9f9f9;">
            <p class="total">الإجمالي: ${formatCurrency(rentals.reduce((sum, r) => sum + (parseFloat(r.total_amount) || 0), 0))}</p>
          </div>
          
          <button class="no-print" onclick="window.print()" style="margin-top: 20px; padding: 10px 20px; background: #2196F3; color: white; border: none; cursor: pointer;">
            طباعة التقرير
          </button>
        </body>
      </html>
    `;
    
    printWindow.document.write(content);
    printWindow.document.close();
    
    addNotification('info', 'جاري فتح نافذة الطباعة...');
  };

  // ==================== نظام الإشعارات ====================
  const addNotification = (type, message) => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, type, message }]);
    
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 5000);
  };

  const removeNotification = (id) => {
    setNotifications(prev => prev.filter(n => n.id !== id));
  };

  // ==================== useEffect ====================
  useEffect(() => {
    loadAllData();
    
    const interval = setInterval(() => {
      if (currentShift) {
        loadActiveRentals();
      }
    }, 30000);
    
    return () => clearInterval(interval);
  }, []);

  // ==================== التصيير ====================
  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل النظام...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* الإشعارات */}
      <div className="notifications-container">
        {notifications.map(notification => (
          <NotificationSystem 
            key={notification.id} 
            type={notification.type} 
            message={notification.message}
            onClose={() => removeNotification(notification.id)}
          />
        ))}
      </div>
      
      {/* الرأس */}
      <header className="page-header">
        <div className="header-content">
          <div className="header-title">
            <h1 className="page-title">
              <span className="title-icon">🎮</span>
              نظام إدارة التأجيرات
            </h1>
            <div className="page-subtitle">
              إدارة تأجير الألعاب والشيفتات
            </div>
          </div>
          <div className="user-info">
            <div className="user-badge">
              <User size={18} />
              <div className="user-details">
                <span className="user-name">{user?.name}</span>
                <span className="user-role">
                  {isManager ? '👑 مدير' : '👷 موظف'}
                </span>
              </div>
            </div>
            <div className="branch-info">
              <Building size={16} />
              <span className="branch-name">{user?.branch_name}</span>
            </div>
          </div>
        </div>
        
        <div className="header-actions">
          {selectedGames.length > 0 && (
            <button 
              onClick={() => setShowRentalModal(true)}
              className="btn btn-primary"
            >
              <Zap size={18} />
              بدء تأجير ({selectedGames.length})
            </button>
          )}
          
          <button 
            onClick={loadAllData}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            تحديث البيانات
          </button>
          
          {currentShift && (
            <div className="shift-indicator">
              <Clock size={16} />
              <span>شيفت #{currentShift.id}</span>
              <span className="shift-time">
                {formatTime(currentShift.start_time)}
              </span>
            </div>
          )}
        </div>
      </header>
      
      {/* لوحة التحكم */}
      <DashboardStats 
        stats={stats}
        currentShift={currentShift}
        onStartShift={startShift}
        onEndShift={endShift}
      />
      
      {/* الرسالة عند عدم وجود شيفت */}
      {!currentShift && (
        <div className="no-shift-alert">
          <div className="alert-content">
            <div className="alert-icon">
              <AlertCircle size={24} />
            </div>
            <div className="alert-text">
              <h3>لا يوجد شيفت نشط</h3>
              <p>يجب بدء شيفت جديد للتمكن من إدارة التأجيرات</p>
            </div>
            <button onClick={startShift} className="btn btn-success">
              <Zap size={18} />
              بدء شيفت جديد
            </button>
          </div>
        </div>
      )}
      
      {/* المحتوى الرئيسي */}
      {currentShift && (
        <div className="main-content">
          {/* الألعاب المتاحة */}
          <GamesGrid 
            games={games}
            branchId={user.branch_id}
            onSelectGame={handleSelectGame}
          />
          
          {/* التأجيرات النشطة */}
          <ActiveRentalsTable 
            rentals={activeRentals}
            onAction={handleRentalAction}
            onRefresh={loadActiveRentals}
          />
          
          {/* التأجيرات المكتملة (للمدير فقط) */}
          {isManager && (
            <CompletedRentalsTable 
              rentals={completedRentals}
              onExport={handleExportCompleted}
              onPrint={handlePrintCompleted}
            />
          )}
        </div>
      )}
      
      {/* نافذة التأجير المتعدد */}
      <MultiRentalModal 
        show={showRentalModal}
        onClose={() => {
          setShowRentalModal(false);
          if (selectedGames.length === 0) {
            setSelectedGames([]);
          }
        }}
        selectedGames={selectedGames}
        currentShift={currentShift}
        onConfirmRental={handleCreateRental}
      />
    </div>
  );
};

export default Rentals;
