import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, BarChart
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== Ø¯ÙˆØ§Ù„ Ù…Ø³Ø§Ø¹Ø¯Ø© ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 Ø¬.Ù…';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

// ==================== Ø§Ù„Ù…ÙƒÙˆÙ† Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  const [loading, setLoading] = useState({
    games: true,
    activeRentals: true,
    shift: true,
    stats: true,
    completedRentals: false
  });
  
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showStats, setShowStats] = useState(false);
  
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    shiftRevenue: 0,
    shiftRentals: 0
  });

  const [searchTerm, setSearchTerm] = useState('');
  const [filterStatus, setFilterStatus] = useState('all');
  const [refreshInterval, setRefreshInterval] = useState(null);

  // ==================== Ø¯ÙˆØ§Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ====================
  const loadGames = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, games: true }));
      
      const response = await api.getGames({
        branch_id: user?.branch_id,
        status: 'all'
      });
      
      if (response.success) {
        const gamesData = response.data || [];
        setGames(gamesData);
        
        const availableGames = gamesData.filter(g => g.status === 'Ù…ØªØ§Ø­').length;
        
        setStats(prev => ({
          ...prev,
          availableGames,
          totalGames: gamesData.length
        }));
        
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${gamesData.length} Ù„Ø¹Ø¨Ø© (${availableGames} Ù…ØªØ§Ø­Ø©)`);
      } else {
        console.error('âŒ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', response.message);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  const loadActiveRentals = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, activeRentals: true }));
      
      const response = await api.getActiveRentals(user?.branch_id);
      
      if (response.success) {
        const rentalsData = response.data || [];
        setActiveRentals(rentalsData);
        
        setStats(prev => ({
          ...prev,
          activeRentals: rentalsData.length
        }));
        
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${rentalsData.length} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø·`);
      } else {
        console.error('âŒ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', response.message);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©:', error);
    } finally {
      setLoading(prev => ({ ...prev, activeRentals: false }));
    }
  }, [user?.branch_id]);

  const loadCurrentShift = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, shift: true }));
      
      const response = await api.getActiveShift();
      
      if (response.success) {
        if (response.data) {
          setCurrentShift(response.data);
          console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ: #${response.data.id}`);
          
          // Ø¬Ù„Ø¨ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª
          try {
            const statsResponse = await api.get(`/shifts/${response.data.id}/stats`);
            if (statsResponse.success && statsResponse.data?.stats) {
              setStats(prev => ({
                ...prev,
                shiftRevenue: statsResponse.data.stats.total_revenue || 0,
                shiftRentals: statsResponse.data.stats.total_rentals || 0
              }));
            }
          } catch (statsError) {
            console.warn('âš ï¸ Ù„Ø§ ÙŠÙ…ÙƒÙ† ØªØ­Ù…ÙŠÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª:', statsError.message);
          }
        } else {
          setCurrentShift(null);
          console.log('â„¹ï¸ Ù„Ø§ ÙŠÙˆØ¬Ø¯ Ø´ÙŠÙØª Ù†Ø´Ø·');
        }
      } else {
        console.error('âŒ ÙØ´Ù„ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', response.message);
      }
    } catch (error) {
      console.error('ğŸ”¥ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª:', error);
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, []);

  const loadCompletedRentals = useCallback(async () => {
    if (!isManager) return;
    
    try {
      setLoading(prev => ({ ...prev, completedRentals: true }));
      
      const today = new Date().toISOString().split('T')[0];
      const response = await api.getRentals({
        branch_id: user?.branch_id,
        status: 'Ù…ÙƒØªÙ…Ù„',
        start_date: today,
        limit: 50
      });
      
      if (response.success) {
        const completedData = response.data || [];
        setCompletedRentals(completedData);
        
        const todayRentals = completedData.filter(r => 
