import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  // ==================== CONTEXT & STATES ====================
  const { user } = useAuth();
  
  // States Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª...');
  const [currentTime, setCurrentTime] = useState('');

  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [games, setGames] = useState([]);
  const [gameDurations, setGameDurations] = useState({});
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ù†Ø§ÙØ°Ø§Øª Ù…Ø®ØªÙ„ÙØ©
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [showEndModal, setShowEndModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…Ø§Ø°Ø¬
  const [selectedGames, setSelectedGames] = useState([]);
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø¥Ø¶Ø§ÙÙŠØ©
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØª
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0
  });
  
  // Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ØªÙ†Ø¨ÙŠÙ‡Ø§Øª Ø§Ù„ÙˆÙ‚Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠ
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // ==================== CONSTANTS ====================
  const fixedDurations = [
    { value: 15, label: '15 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 1 },
    { value: 30, label: '30 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 2 },
    { value: 45, label: '45 Ø¯Ù‚ÙŠÙ‚Ø©', priceMultiplier: 3 },
    { value: 60, label: 'Ø³Ø§Ø¹Ø© ÙˆØ§Ø­Ø¯Ø©', priceMultiplier: 4 },
    { value: 120, label: 'Ø³Ø§Ø¹ØªÙŠÙ†', priceMultiplier: 8 },
    { value: 180, label: '3 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 12 },
    { value: 240, label: '4 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 16 },
    { value: 300, label: '5 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 20 },
    { value: 360, label: '6 Ø³Ø§Ø¹Ø§Øª', priceMultiplier: 24 }
  ];
  
  // ==================== HELPER FUNCTIONS ====================
  const startCountdownTimer = (rentalId, startTimeStr, durationMinutes) => {
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
    }
    
    const timer = setInterval(() => {
      setActiveRentals(prev => {
        return prev.map(rental => {
          if (rental.id === rentalId) {
            const startTime = new Date(startTimeStr);
            const endTime = new Date(startTime.getTime() + durationMinutes * 60000);
            const now = new Date();
            const timeLeftMs = endTime - now;
            
            let timeLeft = 'Ø§Ù†ØªÙ‡Ù‰';
            let isExpired = false;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
              const hours = Math.floor(minutesLeft / 60);
              const minutes = minutesLeft % 60;
              timeLeft = hours > 0 ? `${hours}Ø³ ${minutes}Ø¯` : `${minutes} Ø¯Ù‚ÙŠÙ‚Ø©`;
            } else {
              timeLeft = 'Ø§Ù†ØªÙ‡Ù‰';
              isExpired = true;
              clearInterval(timer);
            }
            
            return {
              ...rental,
              time_left: timeLeft,
              is_expired: isExpired,
              time_left_ms: timeLeftMs
            };
          }
          return rental;
        });
      });
    }, 60000);
    
    countdownTimersRef.current[rentalId] = timer;
  };
  
  const handleAutoCompleteRental = async (rental) => {
    try {
      console.log('ğŸ”” Ø¥ÙƒÙ…Ø§Ù„ ØªØ£Ø¬ÙŠØ± Ù…Ù†ØªÙ‡ÙŠ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹:', rental.rental_number);
      
      const response = await api.post('/rentals/auto-complete', {
        rental_id: rental.id
      });
      
      if (response.success) {
        await Promise.all([
          loadGames(),
          loadActiveRentals(),
          loadCompletedRentals()
        ]);
        
        showNotification('info', `ØªÙ… Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªØ£Ø¬ÙŠØ± #${rental.rental_number} ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹`);
      }
    } catch (error) {
      console.error('âš ï¸ Ø®Ø·Ø£ ÙÙŠ Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªØ£Ø¬ÙŠØ± ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹:', error);
    }
  };
  
  const checkAndAutoCompleteExpiredRentals = async () => {
    try {
      const now = new Date();
      const expiredRentals = activeRentals.filter(rental => {
        if (rental.duration_minutes <= 0 || rental.status !== 'Ù†Ø´Ø·') return false;
        
        const startTime = new Date(rental.start_time);
        const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
        return endTime <= now;
      });
      
      if (expiredRentals.length > 0) {
        console.log(`â° ${expiredRentals.length} ØªØ£Ø¬ÙŠØ± Ù…Ù†ØªÙ‡ÙŠ ÙŠØ­ØªØ§Ø¬ Ù„Ù„Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªÙ„Ù‚Ø§Ø¦ÙŠ`);
        
        const response = await api.post('/rentals/complete-expired');
        
        if (response.success) {
          const completedCount = response.data?.completed_count || 0;
          if (completedCount > 0) {
            console.log(`âœ… ØªÙ… Ø¥ÙƒÙ…Ø§Ù„ ${completedCount} ØªØ£Ø¬ÙŠØ± ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹`);
            showNotification('info', `ØªÙ… Ø¥ÙƒÙ…Ø§Ù„ ${completedCount} ØªØ£Ø¬ÙŠØ± Ù…Ù†ØªÙ‡ÙŠ ØªÙ„Ù‚Ø§Ø¦ÙŠØ§Ù‹`);
            
            await Promise.all([
              loadGames(),
              loadActiveRentals(),
              loadCompletedRentals()
            ]);
          }
        }
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø§Ù„Ø¥ÙƒÙ…Ø§Ù„ Ø§Ù„ØªÙ„Ù‚Ø§Ø¦ÙŠ:', error);
    }
  };


useEffect(() => {
  const updateCurrentTime = () => {
    setCurrentTime(getCurrentEgyptTime());
  };
  
  updateCurrentTime();
  
  // ØªØ­Ø¯ÙŠØ« ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
  const interval = setInterval(updateCurrentTime, 60000);
  
  return () => clearInterval(interval);
}, []);
  
  // ==================== USE EFFECTS ====================
  
  // Ø§Ù„ØªØ£Ø«ÙŠØ± Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ Ù„ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const dataInterval = setInterval(() => {
        loadData();
      }, 30000);
      
      // Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…Ù†ØªÙ‡ÙŠØ© ÙƒÙ„ Ø¯Ù‚ÙŠÙ‚Ø©
      const checkExpiredInterval = setInterval(() => {
        checkAndAutoCompleteExpiredRentals();
      }, 60000);
      
      return () => {
        clearInterval(dataInterval);
        clearInterval(checkExpiredInterval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
