const getEgyptTimeHM = (dateString = null) => {
  try {
    const date = dateString ? new Date(dateString) : new Date();
    
    // Ù…ØµØ± UTC+2 Ø¯Ø§Ø¦Ù…Ù‹Ø§
    const utcHours = date.getUTCHours();
    const utcMinutes = date.getUTCMinutes();
    
    // Ø¥Ø¶Ø§ÙØ© Ø³Ø§Ø¹ØªÙŠÙ† Ù„ØªÙˆÙ‚ÙŠØª Ù…ØµØ±
    let egyptHours = utcHours + 2;
    if (egyptHours >= 24) egyptHours -= 24;
    if (egyptHours < 0) egyptHours += 24;
    
    return {
      hours: egyptHours.toString().padStart(2, '0'),
      minutes: utcMinutes.toString().padStart(2, '0'),
      formatted: `${egyptHours.toString().padStart(2, '0')}:${utcMinutes.toString().padStart(2, '0')}`
    };
  } catch (error) {
    console.error('âŒ Ø®Ø·Ø£ ÙÙŠ getEgyptTimeHM:', error);
    const now = new Date();
    return {
      hours: now.getHours().toString().padStart(2, '0'),
      minutes: now.getMinutes().toString().padStart(2, '0'),
      formatted: `${now.getHours().toString().padStart(2, '0')}:${now.getMinutes().toString().padStart(2, '0')}`
    };
  }
};

