import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import authService from '../services/authService';

const BranchesManagement = () => {
  const [activeShift, setActiveShift] = useState(null);
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const loadData = async () => {
      // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ© ÿ£ŸàŸÑÿßŸã
      if (!authService.isAuthenticated()) {
        console.log('üîí ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ÿå ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ŸÑŸÑÿ™ÿ≥ÿ¨ŸäŸÑ...');
        navigate('/login');
        return;
      }

      try {
        setLoading(true);
        setError('');

        console.log('üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© ÿßŸÑŸÜÿ∏ÿßŸÖ...');
        const systemCheck = await authService.checkSystem();
        console.log('ŸÜÿ™ÿßÿ¶ÿ¨ ÿßŸÑÿ™ÿ≠ŸÇŸÇ:', systemCheck);

        if (!systemCheck.success) {
          const failedChecks = systemCheck.checks.filter(c => !c.success);
          console.warn('‚ö†Ô∏è ŸÖÿ¥ÿßŸÉŸÑ ŸÅŸä ÿßŸÑŸÜÿ∏ÿßŸÖ:', failedChecks);
        }

        // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ
        console.log('üè¨ ÿ¨ŸÑÿ® ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÅÿ±Ÿàÿπ...');
        const branchesResponse = await authService.getBranches();
        
        if (branchesResponse.success) {
          setBranches(branchesResponse.data || []);
          console.log(`‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ${branchesResponse.data?.length || 0} ŸÅÿ±ÿπ`);
        } else {
          console.error('‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ:', branchesResponse.message);
          setBranches([]);
        }

        // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑
        console.log('‚è±Ô∏è ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑...');
        const shiftResponse = await authService.getActiveShift();
        
        if (shiftResponse.success) {
          setActiveShift(shiftResponse.data);
          console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑');
        } else {
          console.warn('‚ö†Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑:', shiftResponse.message);
          setActiveShift(null);
        }

      } catch (error) {
        console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
        
        if (error.status === 403 || error.status === 401) {
          setError('ÿßŸÜÿ™Ÿáÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ©. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ');
          authService.logout();
          setTimeout(() => navigate('/login'), 2000);
        } else {
          setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™: ' + (error.message || 'ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ'));
        }
        
        // ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ¨ÿ±Ÿäÿ®Ÿäÿ© ŸÑŸÑÿπÿ±ÿ∂
        setBranches([
          { id: 1, name: 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä', location: 'ÿßŸÑŸÇÿßŸáÿ±ÿ©' },
          { id: 2, name: 'ŸÅÿ±ÿπ ÿßŸÑŸÖÿπÿßÿØŸä', location: 'ÿßŸÑŸÖÿπÿßÿØŸä' }
        ]);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, [navigate]);

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container">
        <div className="alert alert-danger">
          <h4>‚ùå ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£</h4>
          <p>{error}</p>
          <button 
            className="btn btn-primary" 
            onClick={() => window.location.reload()}
          >
            ÿ•ÿπÿßÿØÿ© ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ©
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="branches-management">
      <h2>ÿ•ÿØÿßÿ±ÿ© ÿßŸÑŸÅÿ±Ÿàÿπ</h2>
      
      <div className="stats-cards">
        <div className="stat-card">
          <h3>ÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ</h3>
          <p className="stat-number">{branches.length}</p>
        </div>
        
        <div className="stat-card">
          <h3>ÿ≠ÿßŸÑÿ© ÿßŸÑÿ¥ŸäŸÅÿ™</h3>
          <p className={`stat-number ${activeShift ? 'active' : 'inactive'}`}>
            {activeShift ? 'ŸÜÿ¥ÿ∑' : 'ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑'}
          </p>
        </div>
      </div>

      <div className="branches-list">
        <h3>ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÅÿ±Ÿàÿπ</h3>
        {branches.length === 0 ? (
          <p className="text-muted">ŸÑÿß ÿ™Ÿàÿ¨ÿØ ŸÅÿ±Ÿàÿπ</p>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>#</th>
                <th>ÿßÿ≥ŸÖ ÿßŸÑŸÅÿ±ÿπ</th>
                <th>ÿßŸÑŸÖŸàŸÇÿπ</th>
                <th>ÿßŸÑÿ≠ÿßŸÑÿ©</th>
              </tr>
            </thead>
            <tbody>
              {branches.map(branch => (
                <tr key={branch.id}>
                  <td>{branch.id}</td>
                  <td>{branch.name}</td>
                  <td>{branch.location}</td>
                  <td>
                    <span className={`status-badge ${branch.is_active ? 'active' : 'inactive'}`}>
                      {branch.is_active ? 'ŸÜÿ¥ÿ∑' : 'ÿ∫Ÿäÿ± ŸÜÿ¥ÿ∑'}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </div>
  );
};

export default BranchesManagement;
