import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './GamesManagement.css';

const GamesManagement = () => {
  const { user } = useAuth();
  const [allGames, setAllGames] = useState([]); // ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ
  const [currentGames, setCurrentGames] = useState([]); // ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿπÿ±Ÿàÿ∂ÿ© ÿ≠ÿßŸÑŸäÿßŸã
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [loadingBranch, setLoadingBranch] = useState(false);
  const [error, setError] = useState('');
  const [selectedBranch, setSelectedBranch] = useState('all');
  const [branchStats, setBranchStats] = useState({});

useEffect(() => {
  loadGamesData(); 
}, []);

const loadGamesData = async () => {
  try {
    setLoading(true);
    setError('');

    // ‚úÖ **ÿßŸÑÿÆŸäÿßÿ± 1: ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ getGames ÿßŸÑÿπÿßÿØŸä ÿ®ÿØŸàŸÜ ŸÅŸÑÿ™ÿ±**
    const gamesResponse = await authService.getGames();
    
    if (gamesResponse.success) {
      const gamesData = gamesResponse.data || [];
      console.log('üéÆ ÿπÿØÿØ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ≠ŸÖŸÑÿ©:', gamesData.length);
      setAllGames(gamesData);
      setCurrentGames(gamesData);
      
      // ‚úÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ
      const branchesResponse = await authService.getBranches();
      if (branchesResponse.success) {
        const branchesData = branchesResponse.data || [];
        console.log('üè¨ ÿπÿØÿØ ÿßŸÑŸÅÿ±Ÿàÿπ ÿßŸÑŸÖÿ≠ŸÖŸÑÿ©:', branchesData.length);
        setBranches(branchesData);
        
        // ‚úÖ ÿ≠ÿ≥ÿßÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ ŸÖÿ≠ŸÑŸäÿßŸã
        calculateBranchStats(gamesData, branchesData);
      } else {
        setError('ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÅÿ±Ÿàÿπ');
      }
    } else {
      setError(gamesResponse.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®');
    }

  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
    setError('ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®. Ÿäÿ±ÿ¨Ÿâ ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ.');
    setAllGames([]);
    setCurrentGames([]);
    setBranches([]);
    setBranchStats({});
  } finally {
    setLoading(false);
  }
};

  // ÿπŸÜÿØ ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑŸÅÿ±ÿπ ÿßŸÑŸÖÿ≠ÿØÿØ
  useEffect(() => {
    if (selectedBranch === 'all') {
      // ÿπÿ±ÿ∂ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ®
      setCurrentGames(allGames);
    } else {
      // ÿπÿ±ÿ∂ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ÿßŸÑŸÖÿ≠ÿØÿØ ŸÅŸÇÿ∑
      const branchGames = allGames.filter(game => game.branch_id == selectedBranch);
      setCurrentGames(branchGames);
      console.log(`üìä ÿπÿ±ÿ∂ ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ${selectedBranch}: ${branchGames.length} ŸÑÿπÿ®ÿ©`);
    }
  }, [selectedBranch, allGames]);

  const calculateBranchStats = (gamesList, branchesList) => {
    const stats = {};
    
    console.log('üìä ÿ®ÿØÿ° ÿ≠ÿ≥ÿßÿ® ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ...');
    
    // ÿ£ŸàŸÑÿßŸã: ÿ™ŸáŸäÿ¶ÿ© ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿ¨ŸÖŸäÿπ ÿßŸÑŸÅÿ±Ÿàÿπ
    branchesList.forEach(branch => {
      stats[branch.id] = {
        id: branch.id,
        name: branch.name,
        totalGames: 0,
        gameCounts: {}, // ÿπÿØÿØ ŸÉŸÑ ŸÇÿ∑ÿπÿ© ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ®
        gamesByStatus: {
          ŸÖÿ™ÿßÿ≠: 0,
          ŸÖÿ§ÿ¨ÿ±ÿ©: 0,
          ÿµŸäÿßŸÜÿ©: 0,
          'ÿ∫Ÿäÿ± ŸÖÿ™ÿßÿ≠': 0
        },
        branchData: branch // ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ ÿßŸÑŸÉÿßŸÖŸÑÿ©
      };
    });
    
    // ÿ´ŸÖ: ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸÑŸÉŸÑ ŸÅÿ±ÿπ
    gamesList.forEach(game => {
      const branchId = game.branch_id;
      const gameName = game.name;
      const gameStatus = game.status || 'ŸÖÿ™ÿßÿ≠';
      
      // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸÅÿ±ÿπ ŸÖŸàÿ¨ŸàÿØÿßŸã ŸÅŸä ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
      if (stats[branchId]) {
        // ÿ≤ŸäÿßÿØÿ© ÿßŸÑÿπÿØÿØ ÿßŸÑÿ•ÿ¨ŸÖÿßŸÑŸä ŸÑŸÑÿ£ŸÑÿπÿßÿ® ŸÅŸä Ÿáÿ∞ÿß ÿßŸÑŸÅÿ±ÿπ
        stats[branchId].totalGames++;
        
        // ÿ≤ŸäÿßÿØÿ© ÿπÿØÿØ ŸÉŸÑ ŸÇÿ∑ÿπÿ© ŸÖŸÜ ÿßŸÑÿ£ŸÑÿπÿßÿ®
