        stats[branchId].gameCounts[gameName] = (stats[branchId].gameCounts[gameName] || 0) + 1;
        stats[branchId].gamesByStatus[gameStatus] = (stats[branchId].gamesByStatus[gameStatus] || 0) + 1;
      }
