    console.log('‚úÖ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÅÿ±Ÿàÿπ ÿßŸÑŸÖÿ≠ÿ≥Ÿàÿ®ÿ©:', stats);
    setBranchStats(stats);
  };

const loadSpecificBranchGames = async (branchId) => {
