// frontend/src/pages/Login.jsx - ÿßŸÑÿ™ÿπÿØŸäŸÑ
import React, { useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [rememberMe, setRememberMe] = useState(false);
  const [serverStatus, setServerStatus] = useState('checking');
  const { login, user, loading: authLoading } = useAuth(); // ‚Üê ÿßŸÑÿ™ÿπÿØŸäŸÑ ŸáŸÜÿß
  const navigate = useNavigate();

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© ÿßŸÑÿÆÿßÿØŸÖ ÿπŸÜÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿµŸÅÿ≠ÿ©
  useEffect(() => {
    checkServerStatus();
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑÿå ÿ•ÿπÿßÿØÿ© ÿ™Ÿàÿ¨ŸäŸáŸá
    if (user) {
      redirectBasedOnRole(user);
    }
    
    // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ∞ŸÉÿ±ŸÜŸä
    const savedEmail = localStorage.getItem('rememberedEmail');
    if (savedEmail) {
      setEmail(savedEmail);
      setRememberMe(true);
    }
  }, [user]); // ‚Üê ÿ•ÿ∂ÿßŸÅÿ© user ŸÉŸÄ dependency

  const checkServerStatus = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/health');
      if (response.ok) {
        setServerStatus('online');
      } else {
        setServerStatus('offline');
      }
    } catch (error) {
      setServerStatus('offline');
      console.error('‚ùå ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ:', error);
    }
  };

  const redirectBasedOnRole = (user) => {
    if (!user) return;
    
    const routes = {
      'admin': '/admin/dashboard',
      'branch_manager': '/manager/dashboard',
      'employee': '/employee/dashboard'
    };
    
    const route = routes[user.role] || '/dashboard';
    console.log(`üîÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ•ŸÑŸâ: ${route} ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ${user.name}`);
    navigate(route);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑŸÖÿØÿÆŸÑÿßÿ™
    if (!email || !password) {
      setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ŸàŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ŸÖÿ∑ŸÑŸàÿ®ÿßŸÜ');
      setLoading(false);
      return;
    }

    if (!email.includes('@')) {
      setError('ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠');
      setLoading(false);
      return;
    }

    if (password.length < 6) {
      setError('ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± Ÿäÿ¨ÿ® ÿ£ŸÜ ÿ™ŸÉŸàŸÜ 6 ÿ£ÿ≠ÿ±ŸÅ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ');
      setLoading(false);
      return;
    }

    console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ:', { email });

    try {
      const result = await login(email, password);
      
      console.log('üìã ŸÜÿ™Ÿäÿ¨ÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ:', result);

      if (result.success) {
        // ÿ≠ŸÅÿ∏ ÿ™ÿ∞ŸÉÿ±ŸÜŸä
        if (rememberMe) {
          localStorage.setItem('rememberedEmail', email);
        } else {
          localStorage.removeItem('rememberedEmail');
        }
        
        // ÿπÿ±ÿ∂ ÿ±ÿ≥ÿßŸÑÿ© ŸÜÿ¨ÿßÿ≠
        setTimeout(() => {
          alert(`‚úÖ ŸÖÿ±ÿ≠ÿ®ÿßŸã ${result.user?.name || 'ÿπÿ≤Ÿäÿ≤Ÿä ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ'}! ÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑŸÉ ÿ®ŸÜÿ¨ÿßÿ≠.`);
        }, 500);
        
