// frontend/src/components/MainLayout.jsx
import React from 'react';
import { Outlet, Link, useLocation, useNavigate } from 'react-router-dom';
import './MainLayout.css';

const MainLayout = () => {
  const location = useLocation();
  const navigate = useNavigate();
  
  // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  const getUser = () => {
    const userStr = localStorage.getItem('user');
    return userStr ? JSON.parse(userStr) : null;
  };
  
  const user = getUser();
  
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };
  
  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'ŸÖŸàÿ∏ŸÅ';
      default: return 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ';
    }
  };
  
  // ÿπŸÜÿßÿµÿ± ÿßŸÑŸÇÿßÿ¶ŸÖÿ© ÿ®ŸÜÿßÿ°Ÿã ÿπŸÑŸâ ÿßŸÑÿØŸàÿ±
  const getMenuItems = () => {
    if (!user) return [];
    
    if (user.role === 'employee') {
      return [
        { path: '/dashboard', label: 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©', icon: 'üè†' },
        { path: '/rental', label: 'ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ', icon: 'üöÄ' },
        { path: '/employee/rentals', label: 'ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™', icon: '‚è±Ô∏è' },
        { path: '/employee/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
      ];
    } else {
      return [
        { path: '/dashboard', label: 'ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿäÿ©', icon: 'üè†' },
        { path: '/admin/users', label: 'ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖŸäŸÜ', icon: 'üë•' },
        { path: '/admin/branches', label: 'ÿßŸÑŸÅÿ±Ÿàÿπ', icon: 'üè¨' },
        { path: '/admin/games', label: 'ÿßŸÑÿ£ŸÑÿπÿßÿ®', icon: 'üéÆ' },
        { path: '/analytics', label: 'ÿßŸÑÿ™ÿ≠ŸÑŸäŸÑÿßÿ™', icon: 'üìä' },
        { path: '/reports', label: 'ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±', icon: 'üìã' },
        { path: '/admin/activities', label: 'ÿ≥ÿ¨ŸÑ ÿßŸÑÿ£ŸÜÿ¥ÿ∑ÿ©', icon: 'üìù' },
        { path: '/admin/settings', label: 'ÿßŸÑÿ•ÿπÿØÿßÿØÿßÿ™', icon: '‚öôÔ∏è' },
      ];
    }
  };

  return (
    <div className="main-layout">
      {/* Sidebar */}
      <aside className="sidebar">
        <div className="sidebar-header">
          <img 
            src="/images/logol3bty.jpg" 
            alt="L3BTY Logo" 
            className="logo"
          />
          <h2>ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± L3BTY</h2>
          <div className="user-info">
            <span className="user-name">{user?.name || 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ'}</span>
            <span className="user-role">{getRoleText(user?.role)}</span>
            <span className="user-branch">{user?.branch_name || 'ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ±ÿ¶Ÿäÿ≥Ÿä'}</span>
          </div>
        </div>
        
        <nav className="sidebar-nav">
          <ul>
            {getMenuItems().map((item, index) => (
              <li key={index}>
                <Link 
                  to={item.path} 
                  className={`nav-link ${location.pathname === item.path ? 'active' : ''}`}
                >
                  <span className="nav-icon">{item.icon}</span>
                  <span className="nav-label">{item.label}</span>
                </Link>
              </li>
            ))}
          </ul>
        </nav>
        
        <div className="sidebar-footer">
          <button onClick={handleLogout} className="logout-btn">
            <span>üö™</span>
            <span>ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨</span>
          </button>
        </div>
      </aside>
      
      {/* Main Content */}
      <main className="main-content">
        <header className="main-header">
          <h1>ŸÜÿ∏ÿßŸÖ ÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÉŸáÿ±ÿ®ÿßÿ¶Ÿäÿ©</h1>
          <div className="header-info">
            <span>{new Date().toLocaleDateString('ar-SA')}</span>
          </div>
        </header>
        
        <div className="content-container">
          <Outlet />
        </div>
      </main>
    </div>
  );
};

export default MainLayout;
