// src/pages/Login.jsx
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const result = await login(email, password);
      
      if (result.success) {
        // جلب بيانات المستخدم من localStorage
        const userData = JSON.parse(localStorage.getItem('user'));
        
        // ✅ **إصلاح: استخدام userData بدلاً من user غير المعرف**
        if (userData?.role === 'admin') {
          navigate('/admin/dashboard');
        } else if (userData?.role === 'branch_manager' || userData?.role === 'employee') {
          navigate('/employee/rentals');
        } else {
          navigate('/employee/dashboard');
        }
      } else {
        setError(result.message || 'بيانات الدخول غير صحيحة');
      }
    } catch (error) {
      setError('حدث خطأ في الاتصال بالخادم');
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <img src="/images/logol3bty.jpg" alt="L3BTY Logo" className="logo" />
          <h1>نظام تأجير الألعاب</h1>
          <p>L3BTY Rental System</p>
        </div>

        <form onSubmit={handleSubmit} className="login-form">
          <div className="form-group">
            <label htmlFor="email">البريد الإلكتروني</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="example@l3bty.com"
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">كلمة المرور</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="********"
              required
              disabled={loading}
            />
          </div>

          {error && (
            <div className="error-message">
              ⚠️ {error}
            </div>
          )}

          <button 
            type="submit" 
            className="login-btn"
            disabled={loading}
          >
            {loading ? 'جاري تسجيل الدخول...' : 'تسجيل الدخول'}
          </button>
        </form>

        <div className="login-footer">
          <p>© 2024 L3BTY Rental System. جميع الحقوق محفوظة.</p>
        </div>
      </div>
    </div>
  );
};

export default Login;
