  // ⚡ CSS للشاشة الجديدة
  _s4(StartShiftScreen, "B/OnnZIozbcuGW1SY1JEgqD+cc0=");
  const startShiftStyles = `
/* شاشة بدء الشيفت */
.start-shift-screen {
  min-height: 100vh;
  background: linear-gradient(135deg, #1e3a8a 0%, #3b82f6 50%, #60a5fa 100%);
  color: white;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 20px;
  font-family: 'Segoe UI', 'Cairo', sans-serif;
  direction: rtl;
}

.start-shift-container {
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
}

/* الرأس */
.start-shift-header {
  text-align: center;
  margin-bottom: 40px;
  animation: fadeInDown 0.8s ease;
}

.welcome-animation {
  display: flex;
  justify-content: center;
  gap: 20px;
  margin-bottom: 20px;
  animation: bounce 2s infinite;
}

.welcome-icon {
  font-size: 3rem;
  filter: drop-shadow(0 4px 6px rgba(0, 0, 0, 0.3));
}

.welcome-title {
  font-size: 3rem;
  font-weight: 800;
  margin-bottom: 10px;
  background: linear-gradient(135deg, #ffffff, #fbbf24);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.user-highlight {
  color: #fbbf24;
  text-shadow: 0 2px 4px rgba(0, 0, 0, 0.3);
}

.welcome-subtitle {
  font-size: 1.2rem;
  opacity: 0.9;
  margin-bottom: 25px;
  color: #dbeafe;
}

.welcome-badges {
  display: flex;
  justify-content: center;
  gap: 15px;
  flex-wrap: wrap;
}

.welcome-badge {
  background: rgba(255, 255, 255, 0.15);
  backdrop-filter: blur(10px);
  padding: 8px 16px;
  border-radius: 50px;
  display: flex;
  align-items: center;
  gap: 8px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  transition: all 0.3s ease;
}

.welcome-badge:hover {
  background: rgba(255, 255, 255, 0.25);
  transform: translateY(-2px);
}

.badge-icon {
  font-size: 1.1rem;
}

.badge-text {
  font-size: 0.9rem;
  font-weight: 500;
}

/* البطاقة الرئيسية */
.start-shift-main-card {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border-radius: 24px;
  padding: 50px;
  margin-bottom: 40px;
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 
    0 20px 40px rgba(0, 0, 0, 0.2),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
  animation: fadeInUp 0.8s ease 0.2s both;
}

.main-card-content {
  text-align: center;
  max-width: 700px;
  margin: 0 auto;
}

.main-card-icon-container {
  position: relative;
  width: 120px;
  height: 120px;
  margin: 0 auto 30px;
}

.main-card-icon {
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #fbbf24, #f59e0b);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 3.5rem;
  z-index: 2;
  position: relative;
  box-shadow: 0 10px 30px rgba(245, 158, 11, 0.4);
}

.icon-ring {
  position: absolute;
  top: -10px;
  left: -10px;
  right: -10px;
  bottom: -10px;
  border: 3px solid rgba(251, 191, 36, 0.3);
  border-radius: 50%;
  animation: pulse-ring 2s infinite;
}

.ring-2 {
  top: -20px;
  left: -20px;
  right: -20px;
  bottom: -20px;
  border-color: rgba(251, 191, 36, 0.2);
  animation-delay: 0.5s;
}

.main-card-title {
  font-size: 2.2rem;
  font-weight: 700;
  margin-bottom: 15px;
  position: relative;
  display: inline-block;
}

.title-underline {
  position: absolute;
  bottom: -8px;
  left: 25%;
  width: 50%;
  height: 4px;
  background: linear-gradient(90deg, transparent, #fbbf24, transparent);
  border-radius: 2px;
}

.main-card-description {
  font-size: 1.1rem;
  line-height: 1.6;
  color: #dbeafe;
  margin-bottom: 40px;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.main-card-stats {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;
  margin-bottom: 40px;
  max-width: 500px;
  margin-left: auto;
  margin-right: auto;
}

.stat-item {
  background: rgba(255, 255, 255, 0.08);
  border-radius: 16px;
  padding: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.stat-item:hover {
  background: rgba(255, 255, 255, 0.12);
  transform: translateY(-3px);
  border-color: rgba(255, 255, 255, 0.2);
}

.stat-icon {
  font-size: 2rem;
  margin-bottom: 10px;
}

.stat-info {
  text-align: center;
}

.stat-value {
  font-size: 1.8rem;
  font-weight: 700;
  margin-bottom: 4px;
  color: #fbbf24;
}

.stat-label {
  font-size: 0.9rem;
  opacity: 0.8;
}

/* زر البدء */
.start-shift-primary-btn {
  background: linear-gradient(135deg, #10b981, #059669);
  color: white;
  border: none;
  padding: 18px 50px;
  border-radius: 16px;
  font-size: 1.3rem;
  font-weight: 700;
  cursor: pointer;
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 15px;
  margin: 0 auto 40px;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  min-width: 250px;
  box-shadow: 
    0 10px 30px rgba(16, 185, 129, 0.4),
    inset 0 1px 0 rgba(255, 255, 255, 0.3);
}

.start-shift-primary-btn:hover:not(:disabled) {
  transform: translateY(-3px) scale(1.02);
  box-shadow: 
    0 15px 40px rgba(16, 185, 129, 0.6),
    inset 0 1px 0 rgba(255, 255, 255, 0.4);
}

.start-shift-primary-btn:active:not(:disabled) {
  transform: translateY(-1px);
}

.start-shift-primary-btn:disabled {
  opacity: 0.6;
  cursor: not-allowed;
}

.btn-icon {
  font-size: 1.5rem;
}

.btn-text {
  flex: 1;
}

.btn-arrow {
  font-size: 1.5rem;
  transition: transform 0.3s ease;
}

.start-shift-primary-btn:hover .btn-arrow {
  transform: translateX(5px);
}

.main-card-features {
  display: flex;
  flex-direction: column;
  gap: 12px;
  max-width: 400px;
  margin: 0 auto;
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px 20px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 12px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.feature-item:hover {
  background: rgba(255, 255, 255, 0.08);
  transform: translateX(-5px);
}

.feature-icon {
  font-size: 1.2rem;
  color: #fbbf24;
}

.feature-text {
  font-size: 0.95rem;
  opacity: 0.9;
}

/* إحصائيات سريعة */
.quick-stats-section {
  margin-bottom: 40px;
  animation: fadeInUp 0.8s ease 0.4s both;
}

.section-title {
  font-size: 1.5rem;
  font-weight: 600;
  margin-bottom: 25px;
  display: flex;
  align-items: center;
  gap: 10px;
}

.section-icon {
  font-size: 1.8rem;
}

.quick-stats-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
}

.quick-stat-card {
  background: rgba(255, 255, 255, 0.07);
  border-radius: 16px;
  padding: 20px;
  display: flex;
  align-items: center;
  gap: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.quick-stat-card:hover {
  background: rgba(255, 255, 255, 0.1);
  transform: translateY(-3px);
  border-color: rgba(255, 255, 255, 0.2);
}

.quick-stat-icon {
  width: 60px;
  height: 60px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.8rem;
}

.branch-icon { background: rgba(59, 130, 246, 0.2); color: #60a5fa; }
.user-icon { background: rgba(16, 185, 129, 0.2); color: #34d399; }
.games-icon { background: rgba(245, 158, 11, 0.2); color: #fbbf24; }
.time-icon { background: rgba(139, 92, 246, 0.2); color: #a78bfa; }

.quick-stat-info {
  flex: 1;
}

.quick-stat-value {
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 4px;
}

.quick-stat-label {
  font-size: 0.9rem;
  opacity: 0.8;
}

/* الشيفتات الأخيرة */
.recent-shifts-section {
  margin-bottom: 40px;
  animation: fadeInUp 0.8s ease 0.6s both;
}

.section-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 25px;
}

.refresh-shifts-btn {
  background: rgba(255, 255, 255, 0.1);
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: white;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  font-size: 1.2rem;
  transition: all 0.3s ease;
}

.refresh-shifts-btn:hover {
  background: rgba(255, 255, 255, 0.2);
  transform: rotate(180deg);
}

.recent-shifts-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 20px;
}

.recent-shift-card {
  background: rgba(255, 255, 255, 0.07);
  border-radius: 16px;
  padding: 20px;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.recent-shift-card:hover {
  background: rgba(255, 255, 255, 0.1);
  transform: translateY(-3px);
}

.recent-shift-card.latest-shift {
  border: 2px solid #fbbf24;
  position: relative;
}

.recent-shift-card.latest-shift::before {
  content: 'الأحدث';
  position: absolute;
  top: -12px;
  right: 20px;
  background: #fbbf24;
  color: #1e3a8a;
  padding: 4px 12px;
  border-radius: 12px;
  font-size: 0.8rem;
  font-weight: 600;
}

.shift-card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 15px;
  padding-bottom: 15px;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.shift-number {
  font-size: 1.2rem;
  font-weight: 700;
  color: #fbbf24;
}

.shift-status {
  font-size: 0.85rem;
  padding: 4px 12px;
  border-radius: 20px;
  font-weight: 600;
}

.shift-status.ended {
  background: rgba(16, 185, 129, 0.2);
  color: #34d399;
}

.shift-status.active {
  background: rgba(239, 68, 68, 0.2);
  color: #f87171;
}

.shift-card-body {
  margin-bottom: 15px;
}

.shift-info-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
  font-size: 0.95rem;
}

.info-label {
  opacity: 0.7;
}

.info-value {
  font-weight: 500;
  text-align: left;
}

.revenue-row {
  margin-top: 10px;
  padding-top: 10px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
}

.revenue-value {
  color: #10b981;
  font-weight: 700;
}

.shift-card-footer {
  padding-top: 15px;
  border-top: 1px solid rgba(255, 255, 255, 0.1);
  text-align: center;
}

.shift-date {
  font-size: 0.85rem;
  opacity: 0.7;
}

.loading-shifts {
  text-align: center;
  padding: 40px;
}

.loading-spinner {
  width: 50px;
  height: 50px;
  border: 3px solid rgba(255, 255, 255, 0.3);
  border-radius: 50%;
  border-top-color: #fbbf24;
  animation: spin 1s linear infinite;
  margin: 0 auto 20px;
}

.no-recent-shifts {
  text-align: center;
  padding: 40px;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 16px;
  border: 2px dashed rgba(255, 255, 255, 0.2);
}

.empty-icon {
  font-size: 3rem;
  margin-bottom: 15px;
  opacity: 0.5;
}

.empty-subtext {
  font-size: 0.9rem;
  opacity: 0.7;
}

/* دليل سريع */
.quick-guide-section {
  margin-bottom: 40px;
  animation: fadeInUp 0.8s ease 0.8s both;
}

.guide-steps {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
}

.guide-step {
  background: rgba(255, 255, 255, 0.07);
  border-radius: 16px;
  padding: 25px;
  display: flex;
  gap: 20px;
  align-items: flex-start;
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
}

.guide-step:hover {
  background: rgba(255, 255, 255, 0.1);
  transform: translateY(-3px);
}

.step-number {
  width: 40px;
  height: 40px;
  background: linear-gradient(135deg, #3b82f6, #1d4ed8);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-weight: 700;
  font-size: 1.2rem;
  flex-shrink: 0;
}

.step-content h4 {
  font-size: 1.1rem;
  font-weight: 600;
  margin-bottom: 8px;
  color: #fbbf24;
}

.step-content p {
  font-size: 0.9rem;
  opacity: 0.8;
  line-height: 1.5;
}

/* معلومات الدعم */
.support-info {
  animation: fadeInUp 0.8s ease 1s both;
}

.support-card {
  background: rgba(255, 255, 255, 0.07);
  border-radius: 16px;
  padding: 25px;
  display: flex;
  gap: 25px;
  align-items: center;
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.support-icon {
  font-size: 2.5rem;
  width: 70px;
  height: 70px;
  background: rgba(239, 68, 68, 0.2);
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #f87171;
}

.support-content h4 {
  font-size: 1.3rem;
  font-weight: 600;
  margin-bottom: 8px;
}

.support-content p {
  font-size: 0.95rem;
  opacity: 0.8;
  margin-bottom: 12px;
}

.support-contacts {
  display: flex;
  align-items: center;
  gap: 15px;
  flex-wrap: wrap;
}

.support-contact {
  color: #60a5fa;
  text-decoration: none;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: all 0.3s ease;
}

.support-contact:hover {
  color: #93c5fd;
  transform: translateY(-2px);
}

.support-separator {
  opacity: 0.5;
}

.support-email {
  color: #d1d5db;
  display: flex;
  align-items: center;
  gap: 8px;
}

/* الأنيميشن */
@keyframes fadeInDown {
  from {
    opacity: 0;
    transform: translateY(-30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes bounce {
  0%, 100% {
    transform: translateY(0);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes pulse-ring {
  0% {
    transform: scale(0.95);
    opacity: 0.7;
  }
  100% {
    transform: scale(1.15);
    opacity: 0;
  }
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

/* تصميم متجاوب */
@media (max-width: 768px) {
  .start-shift-screen {
    padding: 15px;
  }

  .welcome-title {
    font-size: 2.2rem;
  }

  .start-shift-main-card {
    padding: 30px 20px;
  }

  .main-card-title {
    font-size: 1.8rem;
  }

  .main-card-stats {
    grid-template-columns: 1fr;
    gap: 15px;
  }

  .quick-stats-grid {
    grid-template-columns: 1fr;
  }

  .recent-shifts-grid {
    grid-template-columns: 1fr;
  }

  .guide-steps {
    grid-template-columns: 1fr;
  }

  .support-card {
    flex-direction: column;
    text-align: center;
    gap: 20px;
  }

  .support-contacts {
    justify-content: center;
  }
}
