const apiCallCooldown = 2000; // 2 ثانية كحد أدنى بين الطلبات
// ⚡ دالة ذكية لمنع تكرار الطلبات
const debouncedApiCall = async (callType, apiFunction, params = []) => {
  const now = Date.now();
  const queueItem = apiCallQueue[callType];
  
  // إذا كانت الدالة قيد التشغيل، إلغاء
  if (queueItem.isRunning) {
    console.log(`⏱️ ${callType} قيد التشغيل بالفعل، تم إلغاء الطلب`);
    return null;
  }
  
  // إذا مر وقت أقل من cooldown منذ آخر طلب
  if (now - queueItem.lastCall < apiCallCooldown) {
    console.log(`⏱️ ${callType}: تم إلغاء الطلب (تكرار سريع)`);
    return null;
  }
  
  // إذا كان هناك timeout، إلغاءه
  if (queueItem.timeout) {
    clearTimeout(queueItem.timeout);
  }
  
  // تحديث الحالة
  setApiCallQueue(prev => ({
    ...prev,
    [callType]: {
      ...prev[callType],
      isRunning: true,
      lastCall: now
    }
  }));
  
  console.log(`✅ ${callType}: بدء الطلب...`);
  
  try {
    const result = await apiFunction(...params);
    return result;
  } catch (error) {
    console.error(`❌ ${callType}:`, error);
    throw error;
  } finally {
    // إعادة تعيين الحالة بعد التنفيذ
    setTimeout(() => {
      setApiCallQueue(prev => ({
        ...prev,
        [callType]: {
          ...prev[callType],
          isRunning: false,
          timeout: null
        }
      }));
      console.log(`✅ ${callType}: اكتمل`);
    }, 100);
  }
