    console.log('🎮 loadGames: مرفوض (قيد التشغيل بالفعل)');
    return;
  }
  
  const now = Date.now();
  if (now - apiCallQueue.loadGames.lastCall < 3000) { // 3 ثواني
    console.log('🎮 loadGames: مرفوض (تكرار سريع)');
    return;
  }
  
  // تحديث حالة التشغيل
  setApiCallQueue(prev => ({
    ...prev,
    loadGames: { ...prev.loadGames, isRunning: true, lastCall: now }
  }));
  
  try {
    console.log('🎮 تحميل الألعاب - بإدارة الذكية');
    
    if (!user?.branch_id) {
      console.log('❌ لا يوجد branch_id للمستخدم');
      setGames([]);
      return;
    }
    
    // ⚡ طلب واحد فقط
    try {
      const response = await api.get('/games', {
        params: {
          branch_id: user.branch_id,
          include_images: true,
          is_active: 1,
          _t: Date.now() // لمنع caching
        },
        timeout: 10000
      });
      
      if (response && response.success) {
        let gamesData = [];
        
        if (Array.isArray(response.data)) {
          gamesData = response.data;
        } else if (response.data?.data && Array.isArray(response.data.data)) {
          gamesData = response.data.data;
        }
        
        // معالجة البيانات
        const processedGames = gamesData
          .filter(game => game.is_active)
          .map(game => ({
            id: game.id || game.game_id || Date.now() + Math.random(),
            name: game.name || 'لعبة غير معروفة',
            status: game.status || 'متاح',
            is_active: game.is_active === undefined ? 1 : game.is_active,
            price_per_15min: game.price_per_15min || 100,
            category: game.category || 'عام',
            image_url: game.image_url,
            external_image_url: game.external_image_url,
            ...game
          }));
        
        console.log(`✅ تم تحميل ${processedGames.length} لعبة`);
        setGames(processedGames);
        
        // تحديث الإحصائيات
        const availableCount = processedGames.filter(g => g.status === 'متاح').length;
        setStatistics(prev => ({
          ...prev,
          availableGames: availableCount,
          totalGames: processedGames.length
        }));
        
      }
