    }
    
  } catch (error) {
    console.error('❌ خطأ في تحميل الألعاب:', error);
  } finally {
    // إعادة تعيين حالة التشغيل
    setApiCallQueue(prev => ({
      ...prev,
      loadGames: { ...prev.loadGames, isRunning: false }
    }));
  }
};
