// ⚡ استبدل دالة updateStatistics بهذه النسخة القوية
const updateStatistics = async () => {
  try {
    console.log('🔥 تحديث الإحصائيات الفوري الشامل...');
    
    // ⚡ حساب محلي فوري بدون انتظار API
    const now = new Date();
    const todayStart = new Date(now.getFullYear(), now.getMonth(), now.getDate());
    
    // 1. حساب الألعاب المتاحة
    const availableGamesCount = games.filter(game => 
      game && game.is_active && game.status === 'متاح'
    ).length;
    
    // 2. حساب التأجيرات النشطة (مباشرة من activeRentals)
    const activeRentalsCount = activeRentals.length;
    
    // 3. حساب إيرادات اليوم من المكتملة
    const todayCompleted = completedRentals.filter(rental => {
      if (!rental.start_time) return false;
      const rentalDate = new Date(rental.start_time);
      return rentalDate >= todayStart && rental.status === 'مكتمل';
    });
    
    const todayRevenue = todayCompleted.reduce((sum, rental) => 
      sum + (Number(rental.total_amount) || 0), 0
    );
    
    // 4. حساب إيرادات الشيفت من التأجيرات النشطة المدفوعة
    const shiftPaidRentals = activeRentals.filter(rental => 
      rental.payment_status === 'مدفوع' && rental.shift_id === currentShift?.id
    );
    
    const shiftRevenue = shiftPaidRentals.reduce((sum, rental) => 
      sum + (Number(rental.total_amount) || 0), todayRevenue
    );
    
    // 5. تحديث فوري
    const newStats = {
      availableGames: availableGamesCount,
      totalGames: games.filter(g => g && g.is_active).length,
      todayRevenue: todayRevenue,
      todayRentals: todayCompleted.length,
      activeRentals: activeRentalsCount,
      shiftRevenue: shiftRevenue,
      monthlyRevenue: 0,
      uniqueCustomers: 0
    };
    
    console.log('📊 الإحصائيات المحدثة فورياً:', newStats);
    console.log(`🎯 التحقق: activeRentals.length = ${activeRentalsCount}`);
    
    setStatistics(newStats);
    
    // ⚡ تحديث من API في الخلفية (غير متزامن)
    setTimeout(async () => {
      try {
        const response = await api.get('/dashboard/stats');
        if (response.success && response.data) {
          console.log('📡 تم تحديث الإحصائيات من API في الخلفية');
        }
      } catch (error) {
        // تجاهل الخطأ، البيانات المحلية كافية
      }
    }, 0);
    
    return newStats;
    
  } catch (error) {
    console.error('❌ خطأ في تحديث الإحصائيات:', error);
    
    // ⚡ تحديث بسيط في حالة الخطأ
    const fallbackStats = {
      availableGames: games.filter(g => g && g.status === 'متاح').length,
      totalGames: games.length,
      todayRevenue: 0,
      todayRentals: 0,
      activeRentals: activeRentals.length,
      shiftRevenue: 0,
      monthlyRevenue: 0,
      uniqueCustomers: 0
    };
    
    setStatistics(fallbackStats);
    return fallbackStats;
  }
};
const startCountdownTimer = (rentalId, startTime, durationMinutes) => {
  // ⚡ تحقق خاص للتأجيرات الثابتة
  if (durationMinutes > 0 && durationMinutes !== 0) {
    console.log(`⏰ تأجير ثابت ${rentalId}: ${durationMinutes} دقيقة`);
    
    // ⚡ عند انتهاء الوقت، أكمل التأجير تلقائياً
    const timer = setInterval(() => {
      const elapsed = Math.floor((new Date() - new Date(startTime)) / 60000);
      
      if (elapsed >= durationMinutes) {
        console.log(`🛑 تأجير ثابت ${rentalId} انتهى`);
        clearInterval(timer);
        
        // ⚡ أكمل التأجير تلقائياً
        const rental = activeRentals.find(r => r.id === rentalId);
        if (rental && rental.payment_status === 'مدفوع') {
          handleAutoCompleteRental(rental);
        }
      }
    }, 30000); // تحقق كل 30 ثانية
  }
};
// ⚡ **دالة مساعدة لتحديث الوقت المتبقي**
const updateRentalTimeLeft = (rentalId, endTime) => {
  const now = new Date();
  const timeLeftMs = endTime - now;
  
  if (timeLeftMs <= 0) {
    console.log(`🛑 تأجير ${rentalId} انتهى`);
    
    // إلغاء العداد
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
      delete countdownTimersRef.current[rentalId];
    }
    
    // تحديث حالة التأجير
    setActiveRentals(prev => prev.map(rental => {
      if (rental.id === rentalId) {
        return {
          ...rental,
          time_left: 'انتهى',
          remaining_seconds: 0,
          is_expired: true
        };
      }
      return rental;
    }));
    
    // ⚡ **تشغيل الإكمال التلقائي بعد ثانيتين**
    setTimeout(() => {
      const expiredRental = activeRentals.find(r => r.id === rentalId);
      if (expiredRental && !expiredRental.is_open_time) {
        console.log(`⚡ تشغيل الإكمال التلقائي للتأجير ${rentalId}`);
        handleAutoCompleteRental(expiredRental);
      }
    }, 2000);
    
    return;
  }
  
  const totalSeconds = Math.floor(timeLeftMs / 1000);
  const minutes = Math.floor(totalSeconds / 60);
  const seconds = totalSeconds % 60;
  
  const timeLeftFormatted = `${minutes}:${seconds.toString().padStart(2, '0')}`;
  
  // تحديث الوقت المتبقي في القائمة
  setActiveRentals(prev => prev.map(rental => {
    if (rental.id === rentalId) {
      return {
        ...rental,
        time_left: timeLeftFormatted,
        remaining_seconds: totalSeconds,
        is_expired: false
      };
    }
    return rental;
  }));
};
// ==================== دالة جديدة: التحقق من التأجيرات المنتهية وتحويلها ====================
const checkAndAutoCompleteRentals = async () => {
  try {
    const now = new Date();
    console.log('⏰ التحقق من التأجيرات المنتهية...');
    
    // 🔧 إصلاح: تضمين التأجيرات المنتهية الوقت المفتوح أيضاً
    const expiredRentals = activeRentals.filter(rental => {
      // التأجيرات ذات الوقت الثابت المنتهية
      if (!rental.is_open_time && rental.duration_minutes > 0) {
        const startTime = new Date(rental.start_time);
        const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
        const isExpired = now > endTime && rental.payment_status === 'مدفوع';
        
        if (isExpired && !rental.is_expired) {
          console.log(`🛑 تأجير ${rental.rental_number} منتهي (وقت ثابت)`);
          return true;
        }
      }
      
      // 🔧 إضافة: التأجيرات التي انتهى عدّادها مباشرة
      if (rental.time_left === 'انتهى' && rental.payment_status === 'مدفوع') {
        console.log(`🛑 تأجير ${rental.rental_number} انتهى (عُدّاد)`);
        return true;
      }
      
      return false;
    });
    
    if (expiredRentals.length > 0) {
      console.log(`🔄 العثور على ${expiredRentals.length} تأجير منتهي للتحويل`);
      
      for (const rental of expiredRentals) {
        await handleAutoCompleteRental(rental);
      }
      
      // 🔧 إضافة: تحديث البيانات مباشرة بعد التحويل
      await loadActiveRentals();
      await updateStatistics();
    }
  } catch (error) {
    console.error('❌ خطأ في التحقق من التأجيرات المنتهية:', error);
  }
};
// ⚡ دالة مساعدة للتحقق من حالة اللعبة
const checkGameRentalStatus = (gameId) => {
  // التحقق إذا كانت اللعبة مؤجرة في التأجيرات النشطة
  const isRented = activeRentals.some(rental => rental.game_id === gameId);
  return isRented ? 'مؤجرة' : 'متاح';
};
// ⚡ تحديث دالة filteredGames
const filteredGames = useMemo(() => {
  console.log('🔄 حساب الألعاب المصفاة للشيفت الحالي...');
  
  if (!games || !Array.isArray(games)) return [];
  
  console.log(`📊 الشيفت الحالي: ${currentShift ? `#${currentShift.id}` : 'لا يوجد'}`);
  
  // ⚡ **تحديث حالة الألعاب بناءً على تأجيرات الشيفت الحالي فقط**
  const gamesWithUpdatedStatus = games.map(game => {
    // التحقق إذا كانت اللعبة مؤجرة في الشيفت الحالي
    const isRentedInCurrentShift = activeRentals.some(rental => {
      // مطابقة بطرق متعددة
      const matchById = rental.game_id === game.id;
      const matchByName = rental.game_name === game.name;
      
      return matchById || matchByName;
    });
    
    // ⚡ **الحالة الصحيحة: متاح إذا لم تكن مؤجرة في الشيفت الحالي**
    const correctStatus = isRentedInCurrentShift ? 'مؤجرة' : 'متاح';
    
    return {
      ...game,
      status: correctStatus,
      rented_in_current_shift: isRentedInCurrentShift
    };
  });
  
  let filtered = gamesWithUpdatedStatus.filter(game => {
    if (!game) return false;
    
    // التحقق من أن اللعبة نشطة
    const isActive = game.is_active === 1 || game.is_active === true;
    if (!isActive) return false;
    
    // ⚡ **التحقق من أن اللعبة متاحة (غير مؤجرة في الشيفت الحالي)**
    const isAvailable = game.status === 'متاح';
    if (!isAvailable) return false;
    
    // البحث حسب الاسم
    if (searchTerm && searchTerm.trim() !== '') {
      const gameName = (game.name || '').toLowerCase();
      const searchTermLower = searchTerm.toLowerCase();
      if (!gameName.includes(searchTermLower)) {
        return false;
      }
    }
    
    // البحث حسب الفئة
    if (selectedCategory && selectedCategory.trim() !== '') {
      const gameCategory = game.category || '';
      if (gameCategory !== selectedCategory) {
        return false;
      }
    }
    
    return true;
  });
  
  console.log(`✅ الألعاب المصفاة: ${filtered.length} من ${games.length}`);
  console.log(`📊 الألعاب مؤجرة في الشيفت الحالي: ${gamesWithUpdatedStatus.filter(g => g.status === 'مؤجرة').length}`);
  
  return filtered;
}, [games, activeRentals, searchTerm, selectedCategory, currentShift]);
const loadActiveRentals = async () => {
  // ⚡ التحقق من التكرار أولاً
  if (apiCallQueue.loadActiveRentals.isRunning) {
    console.log('🔥 loadActiveRentals: مرفوض (قيد التشغيل بالفعل)');
    return [];
  }
  
  const now = Date.now();
  if (now - apiCallQueue.loadActiveRentals.lastCall < 3000) {
    console.log('🔥 loadActiveRentals: مرفوض (تكرار سريع)');
    return [];
  }
  
  // تحديث حالة التشغيل
  setApiCallQueue(prev => ({
    ...prev,
    loadActiveRentals: { ...prev.loadActiveRentals, isRunning: true, lastCall: now }
  }));
  
  try {
    console.log('🔥 تحميل التأجيرات النشطة - بإدارة الذكية');
    
    if (!user?.branch_id) {
      console.log('❌ لا يوجد branch_id للمستخدم');
      setActiveRentals([]);
      return [];
    }
    
    if (!currentShift) {
      console.log('⚠️ لا يوجد شيفت نشط');
      setActiveRentals([]);
      return [];
    }
    
    console.log(`🏪 الفرع: ${user.branch_id}, ⏰ الشيفت الحالي: ${currentShift.id}`);
    
    let rentalsData = [];
    
    try {
      console.log('🔄 جلب التأجيرات النشطة للفرع...');
      const response = await api.get('/rentals/active/separated', {
        params: {
          branch_id: user.branch_id,
          include_game: true,
          _t: Date.now() // لمنع caching
        },
        timeout: 8000
      });
      
      if (response && response.success) {
        let rawData = response.data;
        
        if (rawData && rawData.data && Array.isArray(rawData.data)) {
          rawData = rawData.data;
        }
        
        if (Array.isArray(rawData)) {
          rentalsData = rawData;
          console.log(`📊 المستلمة من السيرفر: ${rentalsData.length} تأجير (جميع الشيفتات)`);
        }
      }
    } catch (error) {
      console.log('❌ فشل جلب التأجيرات:', error.message);
      return [];
    }
    
    // ⚡ الفلترة حسب الشيفت الحالي
    const currentShiftRentals = rentalsData.filter(rental => {
      if (!rental) return false;
      
      const status = rental.status?.toLowerCase() || '';
      const isActive = status.includes('نشط') || status.includes('active');
      if (!isActive) return false;
      
      const rentalShiftId = rental.shift_id || rental.shift?.id;
      const isCurrentShift = rentalShiftId === currentShift.id;
      
      if (!isCurrentShift) {
        console.log(`⚠️ تأجير ${rental.rental_number} لشيفت ${rentalShiftId} - تم استبعاده`);
        return false;
      }
      
      return true;
    });
    
    console.log(`✅ تأجيرات الشيفت الحالي (#${currentShift.id}): ${currentShiftRentals.length}`);
    
    // ⚡ معالجة البيانات
    const nowTime = new Date();
    const activeRentalsList = currentShiftRentals.map(rental => {
      try {
        const startTime = new Date(rental.start_time || rental.created_at);
        const duration = Number(rental.duration_minutes) || 0;
        const isOpenTime = duration === 0;
        
        let time_left = 'مفتوح';
        let is_expired = false;
        let remaining_seconds = 0;
        
        if (!isOpenTime && duration > 0) {
          const endTime = new Date(startTime.getTime() + duration * 60000);
          const timeLeftMs = endTime - nowTime;
          
          if (timeLeftMs > 0) {
            remaining_seconds = Math.floor(timeLeftMs / 1000);
            const minutes = Math.floor(remaining_seconds / 60);
            const seconds = remaining_seconds % 60;
            time_left = `${minutes}:${seconds.toString().padStart(2, '0')}`;
          } else {
            time_left = 'انتهى';
            is_expired = true;
          }
        }
        
        return {
          id: rental.id,
          rental_number: rental.rental_number || `RENT-${rental.id}`,
          customer_name: rental.customer_name || 'غير معروف',
          customer_phone: rental.customer_phone || 'غير معروف',
          game_id: rental.game_id,
          game_name: rental.game_name || rental.game?.name || 'لعبة',
          start_time: rental.start_time,
          duration_minutes: duration,
          payment_status: isOpenTime ? 'عند الإنهاء' : (rental.payment_status || 'مدفوع'),
          payment_method: rental.payment_method || 'كاش',
          total_amount: Number(rental.total_amount) || 0,
          status: 'نشط',
          time_left,
          is_expired,
          is_open_time: isOpenTime,
          remaining_seconds,
          branch_id: rental.branch_id || user.branch_id,
          shift_id: currentShift.id,
          price_per_15min: Number(rental.price_per_15min) || 100,
          notes: rental.notes || '',
          image_url: rental.image_url || rental.game?.image_url,
          external_image_url: rental.external_image_url || rental.game?.external_image_url
        };
      } catch (error) {
        console.error(`❌ خطأ في معالجة تأجير:`, error);
        return null;
      }
    }).filter(rental => rental !== null);
    
    console.log(`🎯 تم تحميل ${activeRentalsList.length} تأجير للشيفت #${currentShift.id}`);
    
    // تحديث فوري
    setActiveRentals(activeRentalsList);
    
    return activeRentalsList;
    
  } catch (error) {
    console.error('❌ خطأ في تحميل التأجيرات النشطة:', error);
    return [];
  } finally {
    // إعادة تعيين حالة التشغيل
    setApiCallQueue(prev => ({
      ...prev,
      loadActiveRentals: { ...prev.loadActiveRentals, isRunning: false }
    }));
  }
};

const validateDataConsistency = useCallback(() => {
  const now = Date.now();
  
  // التحقق من cooldown
  if (now - lastValidationRef.current < validationCooldown) {
    return {
      valid: true,
      issues: [],
      warnings: [],
      skipped: true
    };
  }
  
  // إلغاء timeout السابق
  if (validationTimeoutRef.current) {
    clearTimeout(validationTimeoutRef.current);
  }
  
  // تحديث وقت آخر تحقق
  lastValidationRef.current = now;
  
  console.log('🔍 بدء التحقق من صحة البيانات...');
  
  const issues = [];
  const warnings = [];
  
  try {
    // التحقق 1: تطابق activeRentals
    if (statistics.activeRentals !== activeRentals.length) {
      issues.push({
        type: 'critical',
        message: `تناقض في التأجيرات النشطة: الإحصائيات=${statistics.activeRentals}, البيانات=${activeRentals.length}`,
        fix: () => {
          setStatistics(prev => ({ ...prev, activeRentals: activeRentals.length }));
          console.log(`🔄 تم تصحيح activeRentals إلى ${activeRentals.length}`);
        }
      });
    }
    
    // التحقق 2: الألعاب المؤجرة
    const rentedGames = games.filter(game => 
      game.status === 'متاح' && 
      activeRentals.some(rental => rental.game_id === game.id)
    );
    
    if (rentedGames.length > 0) {
      warnings.push({
        type: 'warning',
        message: `${rentedGames.length} لعبة مؤجرة ولكن حالتها "متاح"`,
        fix: () => updateGamesStatusFromRentals()
      });
    }
    
    // التحقق 3: إيرادات الشيفت
    const calculatedShiftRevenue = activeRentals
      .filter(r => r.payment_status === 'مدفوع')
      .reduce((sum, r) => sum + (Number(r.total_amount) || 0), 0);
    
    if (Math.abs(statistics.shiftRevenue - calculatedShiftRevenue) > 10) {
      warnings.push(`تناقض في إيرادات الشيفت: ${statistics.shiftRevenue} ≠ ${calculatedShiftRevenue}`);
    }
    
    if (issues.length > 0 || warnings.length > 0) {
      console.group('⚠️ مشاكل في البيانات');
      issues.forEach((issue, index) => {
        console.log(`${index + 1}. [${issue.type.toUpperCase()}] ${issue.message}`);
      });
      warnings.forEach((warning, index) => {
        console.log(`⚠️ ${warning.message || warning}`);
      });
      console.groupEnd();
      
      // التصحيح التلقائي للمشاكل الحرجة
      issues.filter(issue => issue.type === 'critical').forEach(issue => {
        console.log(`🔄 تصحيح تلقائي: ${issue.message}`);
        issue.fix();
      });
      
      return {
        valid: issues.filter(i => i.type === 'critical').length === 0,
        issues,
        warnings,
        skipped: false
      };
    }
    
    console.log('✅ البيانات صحيحة ومتزامنة');
    return { 
      valid: true, 
      issues: [], 
      warnings: [],
      skipped: false 
    };
    
  } catch (error) {
    console.error('❌ خطأ في التحقق من البيانات:', error);
    return {
      valid: false,
      issues: [{ type: 'error', message: `خطأ في التحقق: ${error.message}` }],
      warnings: [],
      skipped: false
    };
  }
}, [statistics, activeRentals, games]);
// ⚡ استدعاء دوري للتحقق
useEffect(() => {
  const validationInterval = setInterval(() => {
    if (currentShift && !loading) {
      validateDataConsistency();
    }
  }, 30000); // كل 30 ثانية
  
  return () => clearInterval(validationInterval);
}, [currentShift, loading]);
// ⚡ دالة تحديث إحصائيات فورية بدون تأخير
const updateStatisticsImmediate = () => {
  try {
    console.log('⚡ تحديث إحصائيات فوري ودقيق');
    
    // ⚡ **تحقق من البيانات أولاً**
    console.log('🔍 بيانات النظام الحالية:');
    console.log(`   • games: ${games.length}`);
    console.log(`   • activeRentals: ${activeRentals.length}`);
    console.log(`   • completedRentals: ${completedRentals.length}`);
    
    // حساب الألعاب المتاحة (فقط الألعاب غير مؤجرة)
    const availableGamesList = games.filter(game => {
      if (!game || !game.is_active) return false;
      
      // التحقق إذا كانت اللعبة مؤجرة
      const isRented = activeRentals.some(rental => 
        rental.game_id === game.id || 
        rental.game_name === game.name ||
        (rental.game && rental.game.id === game.id)
      );
      
      return !isRented && game.status === 'متاح';
    });
    
    // حساب التأجيرات المكتملة اليوم
    const todayStart = new Date();
    todayStart.setHours(0, 0, 0, 0);
    
    const todayCompleted = completedRentals.filter(rental => {
      if (!rental.start_time) return false;
      const rentalDate = new Date(rental.start_time);
      return rentalDate >= todayStart && rental.status === 'مكتمل';
    });
    
    // حساب الإيرادات
    const todayRevenue = todayCompleted.reduce((sum, rental) => 
      sum + (Number(rental.total_amount) || 0), 0
    );
    
    // حساب إيرادات الشيفت (من التأجيرات النشطة المدفوعة + المكتملة)
    const shiftRevenue = activeRentals
      .filter(rental => rental.payment_status === 'مدفوع')
      .reduce((sum, rental) => sum + (Number(rental.total_amount) || 0), todayRevenue);
    
    // ⚡ **الإحصائيات النهائية**
    const newStats = {
      availableGames: availableGamesList.length,
      totalGames: games.filter(g => g && g.is_active).length,
      todayRevenue: todayRevenue,
      todayRentals: todayCompleted.length,
      activeRentals: activeRentals.length, // ⚡ مهم: تأخذ من activeRentals مباشرة
      shiftRevenue: shiftRevenue,
      monthlyRevenue: 0,
      uniqueCustomers: 0
    };
    
    console.log('📊 الإحصائيات الفورية الجديدة:', newStats);
    console.log(`✅ التحقق النهائي: activeRentals = ${activeRentals.length} (دقيقة)`);
    
    // ⚡ **مقارنة مع الإحصائيات القديمة**
    if (statistics.activeRentals !== activeRentals.length) {
      console.log(`🔄 تصحيح: ${statistics.activeRentals} → ${activeRentals.length}`);
    }
    
    setStatistics(newStats);
    return newStats;
    
  } catch (error) {
    console.error('❌ خطأ في التحديث الفوري:', error);
    
    // تحديث أساسي
    const fallbackStats = {
      availableGames: games.filter(g => g && g.status === 'متاح').length,
      totalGames: games.length,
      todayRevenue: 0,
      todayRentals: 0,
      activeRentals: activeRentals.length,
      shiftRevenue: 0,
      monthlyRevenue: 0,
      uniqueCustomers: 0
    };
    
    setStatistics(fallbackStats);
    return fallbackStats;
  }
};
// ⚡ دالة معالجة البيانات المشتركة
const processRentalsData = (rentalsData) => {
  const now = new Date();
  
  return rentalsData
    .filter(rental => rental && rental.status === 'نشط')
    .map(rental => {
      const startTime = new Date(rental.start_time || rental.created_at);
      const duration = Number(rental.duration_minutes) || 0;
      const isOpenTime = duration === 0;
      
      // حساب الوقت المتبقي
      let time_left = 'مفتوح';
      let is_expired = false;
      let remaining_seconds = 0;
      
      if (!isOpenTime && duration > 0) {
        const endTime = new Date(startTime.getTime() + duration * 60000);
        const timeLeftMs = endTime - now;
        
        if (timeLeftMs > 0) {
          remaining_seconds = Math.floor(timeLeftMs / 1000);
          const minutes = Math.floor(remaining_seconds / 60);
          const seconds = remaining_seconds % 60;
          time_left = `${minutes}:${seconds.toString().padStart(2, '0')}`;
        } else {
          time_left = 'انتهى';
          is_expired = true;
          remaining_seconds = 0;
        }
      }
      
      return {
        id: rental.id || `temp-${Date.now()}-${Math.random()}`,
        rental_number: rental.rental_number || `RENT-${rental.id || 'UNK'}`,
        customer_name: rental.customer_name || 'غير معروف',
        customer_phone: rental.customer_phone || 'غير معروف',
        game_id: rental.game_id,
        game_name: rental.game_name || rental.game?.name || 'لعبة',
        start_time: rental.start_time,
        duration_minutes: duration,
        payment_status: isOpenTime ? 'عند الإنهاء' : (rental.payment_status || 'مدفوع'),
        payment_method: rental.payment_method || 'كاش',
        total_amount: Number(rental.total_amount) || 0,
        status: 'نشط',
        time_left,
        is_expired,
        is_open_time: isOpenTime,
        remaining_seconds,
        branch_id: rental.branch_id || user.branch_id,
        shift_id: rental.shift_id,
        price_per_15min: Number(rental.price_per_15min) || 100,
        notes: rental.notes || '',
        image_url: rental.image_url || rental.game?.image_url,
        external_image_url: rental.external_image_url || rental.game?.external_image_url
      };
    });
};
// أضف هذه الدالة في useEffect الرئيسي
