// ⚡ **دالة مساعدة لمعالجة بيانات التأجيرات**
const validateShift = () => {
  if (!currentShift) {
    showNotification('error', 'يجب بدء شيفت أولاً قبل بدء أي تأجيرات');
    return false;
  }

  // التحقق من أن الشيفت نشط ولم ينتهي
  if (currentShift.status === 'منتهي') {
    showNotification('error', 'الشيفت الحالي منتهي. يرجى بدء شيفت جديد');
    return false;
  }

  // التحقق من أن وقت الشيفت لا يتجاوز 24 ساعة (حماية من التجاوز)
  if (currentShift.start_time) {
    const startTime = new Date(currentShift.start_time);
    const now = new Date();
    const hoursDiff = Math.floor((now - startTime) / (1000 * 60 * 60));
    
    if (hoursDiff > 24) {
      showNotification('warning', 'الشيفت تجاوز 24 ساعة. يوصى بإنهاء الشيفت وبدء شيفت جديد');
    }
  }

  // التحقق من أن الموظف الحالي هو نفسه من بدأ الشيفت
  if (currentShift.employee_id && currentShift.employee_id !== user?.id) {
    const confirmChange = window.confirm(
      '⚠️ تحذير: الشيفت بدأ بواسطة موظف آخر.\n' +
      `الموظف الحالي: ${user?.name}\n` +
      `موظف الشيفت: ${currentShift.employee_name}\n\n` +
      'هل تريد المتابعة باستخدام هذا الشيفت؟'
    );
    
    if (!confirmChange) {
      return false;
    }
  }

  return true;
};
const loadCompletedRentals = async () => {
  try {
    if (!user?.branch_id) return;
    
    console.log('📊 جلب التأجيرات المكتملة...');
    
    // 🔧 محاولة نقاط نهاية متعددة
    let response;
    try {
      response = await api.get('/rentals/completed', {
        params: { 
          branch_id: user.branch_id,
          limit: 100,
          include_game: true,
          sort_order: 'DESC'
        }
      });
    } catch (error) {
      console.log('⚠️ نقطة /rentals/completed فشلت، جرب نقطة بديلة');
      
      // 🔧 جلب من التأجيرات العادية بحالة مكتمل
      response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'مكتمل',
          limit: 100,
          include_game: true
        }
      });
    }
    
    if (response.success) {
      let completedData = [];
      
      if (Array.isArray(response.data)) {
        completedData = response.data;
      } else if (response.data && Array.isArray(response.data.data)) {
        completedData = response.data.data;
      }
      
      console.log(`📊 عدد التأجيرات المحملة: ${completedData.length}`);
      
      // 🔧 معالجة جميع الحالات
      const processedRentals = completedData
        .filter(rental => {
          const status = rental.status?.toLowerCase() || '';
          return ['مكتمل', 'completed', 'ملغي', 'cancelled', 'مسترد', 'refunded'].some(s => 
            status.includes(s)
          );
        })
        .map(rental => {
          const isRefunded = rental.status?.includes('مسترد') || rental.status?.includes('refunded');
          const isCancelled = rental.status?.includes('ملغي') || rental.status?.includes('cancelled');
          const isCompleted = rental.status?.includes('مكتمل') || rental.status?.includes('completed');
          
          return {
            ...rental,
            game_name: rental.game_name || rental.game?.name || 'لعبة',
            image_url: rental.image_url || rental.game?.image_url,
            external_image_url: rental.external_image_url || rental.game?.external_image_url,
            formatted_start_time: formatEgyptDateTime(rental.start_time),
            formatted_end_time: formatEgyptDateTime(rental.actual_end_time || rental.end_time || rental.completed_at),
            actual_duration_minutes: rental.actual_duration_minutes || rental.duration_minutes,
            // 🔧 إصلاح: عرض المبلغ الصحيح
            total_amount: isRefunded ? 0 : (rental.total_amount || 0),
            refund_amount: rental.refund_amount || (isRefunded ? rental.total_amount : 0),
            status: isRefunded ? 'مسترد' : isCancelled ? 'ملغي' : 'مكتمل',
            status_original: rental.status
